INSERT INTO `measureDescriptiontext` VALUES (1,1,1,'Security Policy','');
INSERT INTO `measureDescriptiontext` VALUES (2,2,1,'Information security policy','');
INSERT INTO `measureDescriptiontext` VALUES (3,3,1,'Information security policy document','An information security policy document shall be approved by management, and published and communicated to all employees and relevant external parties.');
INSERT INTO `measureDescriptiontext` VALUES (4,4,1,'Review of the information security policy','The information security policy shall be reviewed at planned intervals or if significant changes occur to ensure its continuing suitability, adequacy, and effectiveness.');
INSERT INTO `measureDescriptiontext` VALUES (5,5,1,'Organization of information security','');
INSERT INTO `measureDescriptiontext` VALUES (6,6,1,'Internal Organization','');
INSERT INTO `measureDescriptiontext` VALUES (7,7,1,'Management commitment to information security','Management shall actively support security within the organization through clear direction, demonstrated commitment, explicit assignment, and acknowledgment of information security responsibilities.');
INSERT INTO `measureDescriptiontext` VALUES (8,8,1,'Information security coordination','Information security activities shall be co-ordinated by representatives from different parts of the organization with relevant roles and job functions.');
INSERT INTO `measureDescriptiontext` VALUES (9,9,1,'Allocation of information security responsibilities','All information security responsibilities shall be clearly defined.');
INSERT INTO `measureDescriptiontext` VALUES (10,10,1,'Authorization process for information processing facilities','A management authorization process for new information processing facilities shall be defined and implemented.');
INSERT INTO `measureDescriptiontext` VALUES (11,11,1,'Confidentiality agreements','Requirements for confidentiality or non-disclosure agreements reflecting the organizationâ€™s needs for the protection of information shall be identified and regularly reviewed.');
INSERT INTO `measureDescriptiontext` VALUES (12,12,1,'Contact with authorities','Appropriate contacts with relevant authorities shall be maintained.');
INSERT INTO `measureDescriptiontext` VALUES (13,13,1,'Contact with special interest groups','Appropriate contacts with special interest groups or other specialist security forums and professional associations shall be maintained.');
INSERT INTO `measureDescriptiontext` VALUES (14,14,1,'Independent review of information security','The organizationâ€™s approach to managing information security and its implementation (i.e. control objectives, controls, policies, processes, and procedures for information security) shall be reviewed independently at planned intervals, or when significant changes to the security implementation occur.');
INSERT INTO `measureDescriptiontext` VALUES (15,15,1,'External parties','');
INSERT INTO `measureDescriptiontext` VALUES (16,16,1,'Identification of risks related to external parties','The risks to the organizationâ€™s information and information processing facilities from business processes involving external parties shall be identified and appropriate controls implemented before granting access.');
INSERT INTO `measureDescriptiontext` VALUES (17,17,1,'Addressing security when dealing with customers','All identified security requirements shall be addressed before giving customers access to the organizationâ€™s information or assets.');
INSERT INTO `measureDescriptiontext` VALUES (18,18,1,'Addressing security in third party agreements','Agreements with third parties involving accessing, processing, communicating or managing the organizationâ€™s information or information processing facilities, or adding products or services to information processing facilities shall cover all relevant security requirements.');
INSERT INTO `measureDescriptiontext` VALUES (19,19,1,'Asset management','');
INSERT INTO `measureDescriptiontext` VALUES (20,20,1,'Responsibility for assets','');
INSERT INTO `measureDescriptiontext` VALUES (21,21,1,'Inventory of assets','All assets shall be clearly identified and an inventory of all important assets drawn up and maintained.');
INSERT INTO `measureDescriptiontext` VALUES (22,22,1,'Ownership of assets','All information and assets associated with information processing facilities shall be â€˜ownedâ€™ 3) by a designated part of the organization.');
INSERT INTO `measureDescriptiontext` VALUES (23,23,1,'Acceptable use of assets','Rules for the acceptable use of information and assets associated with information processing facilities shall be identified, documented, and implemented.');
INSERT INTO `measureDescriptiontext` VALUES (24,24,1,'Information Classification','');
INSERT INTO `measureDescriptiontext` VALUES (25,25,1,'Classification guidelines','Information shall be classified in terms of its value, legal requirements, sensitivity and criticality to the organization.');
INSERT INTO `measureDescriptiontext` VALUES (26,26,1,'Information labelling and handling','An appropriate set of procedures for information labeling and handling shall be developed and implemented in accordance with the classification scheme adopted by the organization.');
INSERT INTO `measureDescriptiontext` VALUES (27,27,1,'Human resources security','');
INSERT INTO `measureDescriptiontext` VALUES (28,28,1,'Prior to employment','');
INSERT INTO `measureDescriptiontext` VALUES (29,29,1,'Roles and responsibilities','Security roles and responsibilities of employees, contractors and third party users shall be defined and documented in accordance with the organizationâ€™s information security policy.');
INSERT INTO `measureDescriptiontext` VALUES (30,30,1,'Screening','Background verification checks on all candidates for employment, contractors, and third party users shall be carried out in accordance with relevant laws, regulations and ethics, and proportional to the business requirements, the classification of the information to be accessed, and the perceived risks.');
INSERT INTO `measureDescriptiontext` VALUES (31,31,1,'Terms and conditions of employment','As part of their contractual obligation, employees, contractors and third party users shall agree and sign the terms and conditions of their employment contract, which shall state their and the organizationâ€™s responsibilities for information security.');
INSERT INTO `measureDescriptiontext` VALUES (32,32,1,'During employment','');
INSERT INTO `measureDescriptiontext` VALUES (33,33,1,'Management responsibilities','Management shall require employees, contractors and third party users to apply security in accordance with established policies and procedures of the organization.');
INSERT INTO `measureDescriptiontext` VALUES (34,34,1,'Information security awareness, education and training','All employees of the organization and, where relevant, contractors and third party users shall receive appropriate awareness training and regular updates in organizational policies and procedures, as relevant for their job function.');
INSERT INTO `measureDescriptiontext` VALUES (35,35,1,'Disciplinary process','There shall be a formal disciplinary process for employees who have committed a security breach.');
INSERT INTO `measureDescriptiontext` VALUES (36,36,1,'Termination or change of employment','');
INSERT INTO `measureDescriptiontext` VALUES (37,37,1,'Termination responsibilities','Responsibilities for performing employment termination or change of employment shall be clearly defined and assigned.');
INSERT INTO `measureDescriptiontext` VALUES (38,38,1,'Return of assets','All employees, contractors and third party users shall return all of the organizationâ€™s assets in their possession upon termination of their employment, contract or agreement.');
INSERT INTO `measureDescriptiontext` VALUES (39,39,1,'Removal of access rights','The access rights of all employees, contractors and third party users to information and information processing facilities shall be removed upon termination of their employment, contract or agreement, or adjusted upon change.');
INSERT INTO `measureDescriptiontext` VALUES (40,40,1,'Physical and environmental security','');
INSERT INTO `measureDescriptiontext` VALUES (41,41,1,'Secure areas','');
INSERT INTO `measureDescriptiontext` VALUES (42,42,1,'Physical security perimeter','Security perimeters (barriers such as walls, card controlled entry gates or manned reception desks) shall be used to protect areas that contain information and information processing facilities.');
INSERT INTO `measureDescriptiontext` VALUES (43,43,1,'Physical entry controls','Secure areas shall be protected by appropriate entry controls to ensure that only authorized personnel are allowed access.');
INSERT INTO `measureDescriptiontext` VALUES (44,44,1,'Securing offices, rooms and facilities','Physical security for offices, rooms, and facilities shall be designed and applied.');
INSERT INTO `measureDescriptiontext` VALUES (45,45,1,'Protecting against external and environmental threats','Physical protection against damage from fire, flood, earthquake, explosion, civil unrest, and other forms of natural or man-made disaster shall be designed and applied.');
INSERT INTO `measureDescriptiontext` VALUES (46,46,1,'Working in secure areas','Physical protection and guidelines for working in secure areas shall be designed and applied.');
INSERT INTO `measureDescriptiontext` VALUES (47,47,1,'Public access, delivery and loading areas','Access points such as delivery and loading areas and other points where unauthorized persons may enter the premises shall be controlled and, if possible, isolated from information processing facilities to avoid unauthorized access.');
INSERT INTO `measureDescriptiontext` VALUES (48,48,1,'Equipment security','');
INSERT INTO `measureDescriptiontext` VALUES (49,49,1,'Equipment siting and protection','Equipment shall be sited or protected to reduce the risks from environmental threats and hazards, and opportunities for unauthorized access.');
INSERT INTO `measureDescriptiontext` VALUES (50,50,1,'Supporting utilities','Equipment shall be protected from power failures and other disruptions caused by failures in supporting utilities.');
INSERT INTO `measureDescriptiontext` VALUES (51,51,1,'Cabling security','Power and telecommunications cabling carrying data or supporting information services shall be protected from interception or damage.');
INSERT INTO `measureDescriptiontext` VALUES (52,52,1,'Equipment maintenance','Equipment shall be correctly maintained to ensure its continued availability and integrity.');
INSERT INTO `measureDescriptiontext` VALUES (53,53,1,'Security of equipment offpremises','Security shall be applied to off-site equipment taking into account the different risks of working outside the organizationâ€™s premises.');
INSERT INTO `measureDescriptiontext` VALUES (54,54,1,'Secure disposal or re-use of equipment','All items of equipment containing storage media shall be checked to ensure that any sensitive data and licensed software has been removed or securely overwritten prior to disposal.');
INSERT INTO `measureDescriptiontext` VALUES (55,55,1,'Removal of property','Equipment, information or software shall not be taken off-site without prior authorization.');
INSERT INTO `measureDescriptiontext` VALUES (56,56,1,'Communications and operations management','');
INSERT INTO `measureDescriptiontext` VALUES (57,57,1,'Operational procedures and responsibilities','');
INSERT INTO `measureDescriptiontext` VALUES (58,58,1,'Documented operating procedures','Operating procedures shall be documented, maintained, and made available to all users who need them.');
INSERT INTO `measureDescriptiontext` VALUES (59,59,1,'Change management','Changes to information processing facilities and systems shall be controlled.');
INSERT INTO `measureDescriptiontext` VALUES (60,60,1,'Segregation of duties','Duties and areas of responsibility shall be segregated to reduce opportunities for unauthorized or unintentional modification or misuse of the organizationâ€™s assets.');
INSERT INTO `measureDescriptiontext` VALUES (61,61,1,'Separation of development, test and operational facilities','Development, test and operational facilities shall be separated to reduce the risks of unauthorised access or changes to the operational system.');
INSERT INTO `measureDescriptiontext` VALUES (62,62,1,'Third party service delivery management','');
INSERT INTO `measureDescriptiontext` VALUES (63,63,1,'Service delivery','It shall be ensured that the security controls, service definitions and delivery levels included in the third party service delivery agreement are implemented, operated, and maintained by the third party.');
INSERT INTO `measureDescriptiontext` VALUES (64,64,1,'Monitoring and review of third party services','The services, reports and records provided by the third party shall be regularly monitored and reviewed, and audits shall be carried out regularly.');
INSERT INTO `measureDescriptiontext` VALUES (65,65,1,'Managing changes to third party services','Changes to the provision of services, including maintaining and improving existing information security policies, procedures and controls, shall be managed, taking account of the criticality of business systems and processes involved and re-assessment of risks.');
INSERT INTO `measureDescriptiontext` VALUES (66,66,1,'System planning and acceptance','');
INSERT INTO `measureDescriptiontext` VALUES (67,67,1,'Capacity management','The use of resources shall be monitored, tuned, and projections made of future capacity requirements to ensure the required system performance.');
INSERT INTO `measureDescriptiontext` VALUES (68,68,1,'System acceptance','Acceptance criteria for new information systems, upgrades, and new versions shall be established and suitable tests of the system(s) carried out during development and prior to acceptance.');
INSERT INTO `measureDescriptiontext` VALUES (69,69,1,'Protection against malicious and mobile code','');
INSERT INTO `measureDescriptiontext` VALUES (70,70,1,'Controls against malicious code','Detection, prevention, and recovery controls to protect against malicious code and appropriate user awareness procedures shall be implemented.');
INSERT INTO `measureDescriptiontext` VALUES (71,71,1,'Controls against mobile code','Where the use of mobile code is authorized, the configuration shall ensure that the authorized mobile code operates according to a clearly defined security policy, and unauthorized mobile code shall be prevented from executing.');
INSERT INTO `measureDescriptiontext` VALUES (72,72,1,'Back-up','');
INSERT INTO `measureDescriptiontext` VALUES (73,73,1,'Information back-up','Back-up copies of information and software shall be taken and tested regularly in accordance with the agreed backup policy.');
INSERT INTO `measureDescriptiontext` VALUES (74,74,1,'Network security management','');
INSERT INTO `measureDescriptiontext` VALUES (75,75,1,'Network controls','Networks shall be adequately managed and controlled, in order to be protected from threats, and to maintain security for the systems and applications using the network, including information in transit.');
INSERT INTO `measureDescriptiontext` VALUES (76,76,1,'Security of network services','Security features, service levels, and management requirements of all network services shall be identified and included in any network services agreement, whether these services are provided in-house or outsourced.');
INSERT INTO `measureDescriptiontext` VALUES (77,77,1,'Media handling','');
INSERT INTO `measureDescriptiontext` VALUES (78,78,1,'Management of removable media','There shall be procedures in place for the management of removable media');
INSERT INTO `measureDescriptiontext` VALUES (79,79,1,'Disposal of media','Media shall be disposed of securely and safely when no longer required, using formal procedures.');
INSERT INTO `measureDescriptiontext` VALUES (80,80,1,'Information handling procedures','Procedures for the handling and storage of information shall be established to protect this information from unauthorized disclosure or misuse.');
INSERT INTO `measureDescriptiontext` VALUES (81,81,1,'Security of system documentation','System documentation shall be protected against unauthorized access.');
INSERT INTO `measureDescriptiontext` VALUES (82,82,1,'Exchange of information','');
INSERT INTO `measureDescriptiontext` VALUES (83,83,1,'Information exchange policies and procedures','Formal exchange policies, procedures, and controls shall be in place to protect the exchange of information through the use of all types of communication facilities.');
INSERT INTO `measureDescriptiontext` VALUES (84,84,1,'Exchange agreements','Agreements shall be established for the exchange of information and software between the organization and external parties.');
INSERT INTO `measureDescriptiontext` VALUES (85,85,1,'Physical media in transit ','Media containing information shall be protected against unauthorized access, misuse or corruption during transportation beyond an organizationâ€™s physical boundaries.');
INSERT INTO `measureDescriptiontext` VALUES (86,86,1,'Electronic messaging','Information involved in electronic messaging shall be appropriately protected.');
INSERT INTO `measureDescriptiontext` VALUES (87,87,1,'Business information systems','Policies and procedures shall be developed and implemented to protect information associated with the interconnection of business information systems.');
INSERT INTO `measureDescriptiontext` VALUES (88,88,1,'Electronic commerce services','');
INSERT INTO `measureDescriptiontext` VALUES (89,89,1,'Electronic commerce','Information involved in electronic commerce passing over public networks shall be protected from fraudulent activity, contract dispute, and unauthorized disclosure and modification.');
INSERT INTO `measureDescriptiontext` VALUES (90,90,1,'On-line transactions','Information involved in on-line transactions shall be protected to prevent incomplete transmission, mis-routing, unauthorized message alteration, unauthorized disclosure, unauthorized message duplication or replay.');
INSERT INTO `measureDescriptiontext` VALUES (91,91,1,'Publicly available information','The integrity of information being made available on a publicly available system shall be protected to prevent unauthorized modification.');
INSERT INTO `measureDescriptiontext` VALUES (92,92,1,'Monitoring','');
INSERT INTO `measureDescriptiontext` VALUES (93,93,1,'Audit logging','Audit logs recording user activities, exceptions, and information security events shall be produced and kept for an agreed period to assist in future investigations and access control monitoring.');
INSERT INTO `measureDescriptiontext` VALUES (94,94,1,'Monitoring system use','Procedures for monitoring use of information processing facilities shall be established and the results of the monitoring activities reviewed regularly.');
INSERT INTO `measureDescriptiontext` VALUES (95,95,1,'Protection of log information','Logging facilities and log information shall be protected against tampering and unauthorized access.');
INSERT INTO `measureDescriptiontext` VALUES (96,96,1,'Administrator and operator logs','System administrator and system operator activities shall be logged.');
INSERT INTO `measureDescriptiontext` VALUES (97,97,1,'Fault logging','Faults shall be logged, analyzed, and appropriate action taken.');
INSERT INTO `measureDescriptiontext` VALUES (98,98,1,'Clock synchronization','The clocks of all relevant information processing systems within an organization or security domain shall be synchronized with an agreed accurate time source.');
INSERT INTO `measureDescriptiontext` VALUES (99,99,1,'Access control','');
INSERT INTO `measureDescriptiontext` VALUES (100,100,1,'Business requirement for access control','');
INSERT INTO `measureDescriptiontext` VALUES (101,101,1,'Access control policy','An access control policy shall be established, documented, and reviewed based on business and security requirements for access.');
INSERT INTO `measureDescriptiontext` VALUES (102,102,1,'User access management','');
INSERT INTO `measureDescriptiontext` VALUES (103,103,1,'User registration','There shall be a formal user registration and de-registration procedure in place for granting and revoking access to all information systems and services.');
INSERT INTO `measureDescriptiontext` VALUES (104,104,1,'Privilege management','The allocation and use of privileges shall be restricted and controlled.');
INSERT INTO `measureDescriptiontext` VALUES (105,105,1,'User password management','The allocation of passwords shall be controlled through a formal management process.');
INSERT INTO `measureDescriptiontext` VALUES (106,106,1,'Review of user access rights','Management shall review user''s access rights at regular intervals using a formal process.');
INSERT INTO `measureDescriptiontext` VALUES (107,107,1,'User responsibilities','');
INSERT INTO `measureDescriptiontext` VALUES (108,108,1,'Password use','Users shall be required to follow good security practices in the selection and use of passwords.');
INSERT INTO `measureDescriptiontext` VALUES (109,109,1,'Unattended user equipment','Users shall ensure that unattended equipment has appropriate protection.');
INSERT INTO `measureDescriptiontext` VALUES (110,110,1,'Clear desk and clear screen policy','A clear desk policy for papers and removable storage media and a clear screen policy for information processing facilities shall be adopted.');
INSERT INTO `measureDescriptiontext` VALUES (111,111,1,'Network access control','');
INSERT INTO `measureDescriptiontext` VALUES (112,112,1,'Policy on use of network services','Users shall only be provided with access to the services that they have been specifically authorized to use.');
INSERT INTO `measureDescriptiontext` VALUES (113,113,1,'User authentication for external connections','Appropriate authentication methods shall be used to control access by remote users.');
INSERT INTO `measureDescriptiontext` VALUES (114,114,1,'Equipment identification in networks','Automatic equipment identification shall be considered as a means to authenticate connections from specific locations and equipment.');
INSERT INTO `measureDescriptiontext` VALUES (115,115,1,'Remote diagnostic and configuration port protection','Physical and logical access to diagnostic and configuration ports shall be controlled.');
INSERT INTO `measureDescriptiontext` VALUES (116,116,1,'Segregation in networks','Groups of information services, users, and information systems shall be segregated on networks.');
INSERT INTO `measureDescriptiontext` VALUES (117,117,1,'Network connection control','For shared networks, especially those extending across the organizationâ€™s boundaries, the capability of users to connect to the network shall be restricted, in line with the access control policy and requirements of the business applications (see 11.1).');
INSERT INTO `measureDescriptiontext` VALUES (118,118,1,'Network routing control','Routing controls shall be implemented for networks to ensure that computer connections and information flows do not breach the access control policy of the business applications.');
INSERT INTO `measureDescriptiontext` VALUES (119,119,1,'Operating system access control','');
INSERT INTO `measureDescriptiontext` VALUES (120,120,1,'Secure log-on procedures','Access to operating systems shall be controlled by a secure log-on procedure.');
INSERT INTO `measureDescriptiontext` VALUES (121,121,1,'User identification and authentication','All users shall have a unique identifier (user ID) for their personal use only, and a suitable authentication technique shall be chosen to substantiate the claimed identity of a user.');
INSERT INTO `measureDescriptiontext` VALUES (122,122,1,'Password management system','Systems for managing passwords shall be interactive and shall ensure quality passwords.');
INSERT INTO `measureDescriptiontext` VALUES (123,123,1,'Use of system utilities','The use of utility programs that might be capable of overriding system and application controls shall be restricted and tightly controlled.');
INSERT INTO `measureDescriptiontext` VALUES (124,124,1,'Session time-out','Inactive sessions shall shut down after a defined period of inactivity.');
INSERT INTO `measureDescriptiontext` VALUES (125,125,1,'Limitation of connection time','Restrictions on connection times shall be used to provide additional security for high-risk applications.');
INSERT INTO `measureDescriptiontext` VALUES (126,126,1,'Application and information access control','');
INSERT INTO `measureDescriptiontext` VALUES (127,127,1,'Information access restriction','Access to information and application system functions by users and support personnel shall be restricted in accordance with the defined access control policy.');
INSERT INTO `measureDescriptiontext` VALUES (128,128,1,'Sensitive system isolation','Sensitive systems shall have a dedicated (isolated) computing environment.');
INSERT INTO `measureDescriptiontext` VALUES (129,129,1,'Mobile computing and teleworking','');
INSERT INTO `measureDescriptiontext` VALUES (130,130,1,'Mobile computing and communications','A formal policy shall be in place, and appropriate security measures shall be adopted to protect against the risks of using mobile computing and communication facilities.');
INSERT INTO `measureDescriptiontext` VALUES (131,131,1,'Teleworking','A policy, operational plans and procedures shall be developed and implemented for teleworking activities.');
INSERT INTO `measureDescriptiontext` VALUES (132,132,1,'Information systems acquisition, development and maintenance','');
INSERT INTO `measureDescriptiontext` VALUES (133,133,1,'Security requirements of information systems','');
INSERT INTO `measureDescriptiontext` VALUES (134,134,1,'Security requirements analysis and specification','Statements of business requirements for new information systems, or enhancements to existing information systems shall specify the requirements for security controls.');
INSERT INTO `measureDescriptiontext` VALUES (135,135,1,'Correct processing in applications','');
INSERT INTO `measureDescriptiontext` VALUES (136,136,1,'Input data validation','Data input to applications shall be validated to ensure that this data is correct and appropriate.');
INSERT INTO `measureDescriptiontext` VALUES (137,137,1,'Control of internal processing','Validation checks shall be incorporated into applications to detect any corruption of information through processing errors or deliberate acts.');
INSERT INTO `measureDescriptiontext` VALUES (138,138,1,'Message integrity','Requirements for ensuring authenticity and protecting message integrity in applications shall be identified, and appropriate controls identified and implemented.');
INSERT INTO `measureDescriptiontext` VALUES (139,139,1,'Output data validation','Data output from an application shall be validated to ensure that the processing of stored information is correct and appropriate to the circumstances.');
INSERT INTO `measureDescriptiontext` VALUES (140,140,1,'Cryptographic controls','');
INSERT INTO `measureDescriptiontext` VALUES (141,141,1,'Policy on the use of cryptographic controls','A policy on the use of cryptographic controls for protection of information shall be developed and implemented.');
INSERT INTO `measureDescriptiontext` VALUES (142,142,1,'Key management','Key management shall be in place to support the organizationâ€™s use of cryptographic techniques.');
INSERT INTO `measureDescriptiontext` VALUES (143,143,1,'Security of system files','');
INSERT INTO `measureDescriptiontext` VALUES (144,144,1,'Control of operational software','There shall be procedures in place to control the installation of software on operational systems.');
INSERT INTO `measureDescriptiontext` VALUES (145,145,1,'Protection of system test data','Test data shall be selected carefully, and protected and controlled.');
INSERT INTO `measureDescriptiontext` VALUES (146,146,1,'Access control to program source code','Access to program source code shall be restricted.');
INSERT INTO `measureDescriptiontext` VALUES (147,147,1,'Security in development and support processes','');
INSERT INTO `measureDescriptiontext` VALUES (148,148,1,'Change control procedures','The implementation of changes shall be controlled by the use of formal change control procedures.');
INSERT INTO `measureDescriptiontext` VALUES (149,149,1,'Technical review of applications after operating system changes','When operating systems are changed, business critical applications shall be reviewed and tested to ensure there is no adverse impact on organizational operations or security.');
INSERT INTO `measureDescriptiontext` VALUES (150,150,1,'Restrictions on changes to software packages','Modifications to software packages shall be discouraged, limited to necessary changes, and all changes shall be strictly controlled.');
INSERT INTO `measureDescriptiontext` VALUES (151,151,1,'Information leakage','Opportunities for information leakage shall be prevented.');
INSERT INTO `measureDescriptiontext` VALUES (152,152,1,'Outsourced software development','Outsourced software development shall be supervised and monitored by the organization.');
INSERT INTO `measureDescriptiontext` VALUES (153,153,1,'Technical Vulnerability Management','');
INSERT INTO `measureDescriptiontext` VALUES (154,154,1,'Control of technical vulnerabilities','Timely information about technical vulnerabilities of information systems being used shall be obtained, the organization''s exposure to such vulnerabilities evaluated, and appropriate measures taken to address the associated risk.');
INSERT INTO `measureDescriptiontext` VALUES (155,155,1,'Information security incident management','');
INSERT INTO `measureDescriptiontext` VALUES (156,156,1,'Reporting information security events and weaknesses','');
INSERT INTO `measureDescriptiontext` VALUES (157,157,1,'Reporting information security events','Information security events shall be reported through appropriate management channels as quickly as possible.');
INSERT INTO `measureDescriptiontext` VALUES (158,158,1,'Reporting security weaknesses','All employees, contractors and third party users of information systems and services shall be required to note and report any observed or suspected security weaknesses in systems or services.');
INSERT INTO `measureDescriptiontext` VALUES (159,159,1,'Management of information security incidents and improvements','');
INSERT INTO `measureDescriptiontext` VALUES (160,160,1,'Responsibilities and procedures','Management responsibilities and procedures shall be established to ensure a quick, effective, and orderly response to information security incidents.');
INSERT INTO `measureDescriptiontext` VALUES (161,161,1,'Learning from information security incidents','There shall be mechanisms in place to enable the types, volumes, and costs of information security incidents to be quantified and monitored.');
INSERT INTO `measureDescriptiontext` VALUES (162,162,1,'Collection of evidence','Where a follow-up action against a person or organization after an information security incident involves legal action (either civil or criminal), evidence shall be collected, retained, and presented to conform to the rules for evidence laid down in the relevant jurisdiction(s).');
INSERT INTO `measureDescriptiontext` VALUES (163,163,1,'Business continuity management','');
INSERT INTO `measureDescriptiontext` VALUES (164,164,1,'Information security aspects of business continuity management','');
INSERT INTO `measureDescriptiontext` VALUES (165,165,1,'Including information security in the business continuity management process','A managed process shall be developed and maintained for business continuity throughout the organization that addresses the information security requirements needed for the organizationâ€™s business continuity.');
INSERT INTO `measureDescriptiontext` VALUES (166,166,1,'Business continuity and risk assessment','Events that can cause interruptions to business processes shall be identified, along with the probability and impact of such interruptions and their consequences for information security.');
INSERT INTO `measureDescriptiontext` VALUES (167,167,1,'Developing and implementing continuity plans including information security','Plans shall be developed and implemented to maintain or restore operations and ensure availability of information at the required level and in the required time scales following interruption to, or failure of, critical business processes.');
INSERT INTO `measureDescriptiontext` VALUES (168,168,1,'Business continuity planning framework','A single framework of business continuity plans shall be maintained to ensure all plans are consistent, to consistently address information security requirements, and to identify priorities for testing and maintenance.');
INSERT INTO `measureDescriptiontext` VALUES (169,169,1,'Testing, maintaining and reassessing business continuity plans','Business continuity plans shall be tested and updated regularly to ensure that they are up to date and effective.');
INSERT INTO `measureDescriptiontext` VALUES (170,170,1,'Compliance','');
INSERT INTO `measureDescriptiontext` VALUES (171,171,1,'Compliance with legal requirements','');
INSERT INTO `measureDescriptiontext` VALUES (172,172,1,'Identification of applicable legislation','All relevant statutory, regulatory and contractual requirements and the organizationâ€™s approach to meet these requirements shall be explicitly defined, documented, and kept up to date for each information system and the organization.');
INSERT INTO `measureDescriptiontext` VALUES (173,173,1,'Intellectual property rights (IPR)','Appropriate procedures shall be implemented to ensure compliance with legislative, regulatory, and contractual requirements on the use of material in respect of which there may be intellectual property rights and on the use of proprietary software products.');
INSERT INTO `measureDescriptiontext` VALUES (174,174,1,'Protection of organizational records','Important records shall be protected from loss, destruction and falsification, in accordance with statutory, regulatory, contractual, and business requirements.');
INSERT INTO `measureDescriptiontext` VALUES (175,175,1,'Data protection and privacy of personal information','Data protection and privacy shall be ensured as required in relevant legislation, regulations, and, if applicable, contractual clauses.');
INSERT INTO `measureDescriptiontext` VALUES (176,176,1,'Prevention of misuse of information processing facilities','Users shall be deterred from using information processing facilities for unauthorized purposes.');
INSERT INTO `measureDescriptiontext` VALUES (177,177,1,'Regulation of cryptographic controls','Cryptographic controls shall be used in compliance with all relevant agreements, laws, and regulations.');
INSERT INTO `measureDescriptiontext` VALUES (178,178,1,'Compliance with security policies and standards, and technical compliance','');
INSERT INTO `measureDescriptiontext` VALUES (179,179,1,'Compliance with security policies and standards','Managers shall ensure that all security procedures within their area of responsibility are carried out correctly to achieve compliance with security policies and standards.');
INSERT INTO `measureDescriptiontext` VALUES (180,180,1,'Technical compliance checking','Information systems shall be regularly checked for compliance with security implementation standards.');
INSERT INTO `measureDescriptiontext` VALUES (181,181,1,'Information systems audit considerations','');
INSERT INTO `measureDescriptiontext` VALUES (182,182,1,'Information systems audit controls','Audit requirements and activities involving checks on operational systems shall be carefully planned and agreed to minimize the risk of disruptions to business processes.');
INSERT INTO `measureDescriptiontext` VALUES (183,183,1,'Protection of information systems audit tools','Access to information systems audit tools shall be protected to prevent any possible misuse or compromise.');
insert into measureDescriptiontext values (184,1,2,'Politique de sécurité','');
insert into measureDescriptiontext values (185,2,2,'Politique de sécurité de l’information','');
insert into measureDescriptiontext values (186,3,2,'Document de politique de sécurité de l’information','Il convient qu’un document de politique de sécurité de l’information soit approuvé par la direction, puis publié et diffusé auprès de l’ensemble des salariés et des tiers concernés.');
insert into measureDescriptiontext values (187,4,2,'Réexamen de la politique de sécurité de l’information','Pour garantir la pertinence, l’adéquation et l’efficacité de la politique de sécurité de l’information, il convient de réexaminer la politique à intervalles fixés préalablement ou en cas de changements majeurs.');
insert into measureDescriptiontext values (188,5,2,'Organisation de la sécurité de l’information','');
insert into measureDescriptiontext values (189,6,2,'Organisation interne','');
insert into measureDescriptiontext values (190,7,2,'Engagement de la direction vis-à-vis de la sécurité de l’information','Il convient que la direction soutienne activement la politique de sécurité au sein de l’organisme au moyen de directives claires, d’un engagement franc, d’attribution de fonctions explicites et d’une reconnaissance des responsabilités liées à la sécurité de l’information.');
insert into measureDescriptiontext values (191,8,2,'Coordination de la sécurité de l’information','Il convient que les activités relatives à la sécurité de l’information soient coordonnées par des intervenants ayant des fonctions et des rôles appropriés représentatifs des différentes parties de l’organisme.');
insert into measureDescriptiontext values (192,9,2,'Attribution des responsabilités en matière de sécurité de l’information','Il convient de définir clairement toutes les responsabilités en matière de sécurité de l’information.');
insert into measureDescriptiontext values (193,10,2,'Système d’autorisation concernant les moyens de traitement de l’information','Il convient de définir et de mettre en oeuvre un système de gestion des autorisations pour chaque nouveau moyen de traitement de l’information.');
insert into measureDescriptiontext values (194,11,2,'Engagements de confidentialité','Il convient d’identifier et de réexaminer régulièrement les exigences en matière d’engagements de confidentialité ou de non-divulgation, conformément aux besoins de l’organisme.');
insert into measureDescriptiontext values (195,12,2,'Relations avec les autorités','Il convient de mettre en place des relations appropriées avec les autorités compétentes.');
insert into measureDescriptiontext values (196,13,2,'Relations avec des groupes de spécialistes','Il convient d’entretenir des contacts appropriés avec des groupes de spécialistes, des forums spécialisés dans la sécurité et des associations professionnelles.');
insert into measureDescriptiontext values (197,14,2,'Revue indépendante de la sécurité de l’information','Il convient de procéder à des revues régulières et indépendantes de l’approche retenue par l’organisme pour gérer et mettre en oeuvre sa sécurité (à savoir le suivi des objectifs de sécurité, les politiques, les procédures et les processus relatifs à la sécurité de l’information');
insert into measureDescriptiontext values (198,15,2,'Tiers','');
insert into measureDescriptiontext values (199,16,2,'Identification des risques provenant des tiers','Il convient d’identifier les risques pesant sur l’information et les moyens de traitement de l’organisme qui découlent d’activités impliquant des tiers, et de mettre en oeuvre des mesures appropriées avant d’accorder des accès.');
insert into measureDescriptiontext values (200,17,2,'La sécurité et les clients','Tous les besoins de sécurité doivent être traités avant d’accorder aux clients l’accès à l’information ou aux actifs de l’organisme.');
insert into measureDescriptiontext values (201,18,2,'La sécurité dans les accords conclus avec des tiers','Il convient que les accords conclus avec des tiers qui portent sur l’accès, le traitement, la communication ou la gestion de l’information, ou des moyens de traitement de l’information de l’organisme, ou qui portent sur l’ajout de produits ou de services aux moyens de traitement de l’information, couvrent l’ensemble des exigences applicables en matière de sécurité.');
insert into measureDescriptiontext values (202,19,2,'Gestion des actifs','');
insert into measureDescriptiontext values (203,20,2,'Responsabilités relatives aux actifs','');
insert into measureDescriptiontext values (204,21,2,'Inventaire des actifs','Il convient de clairement identifier tous les actifs, de réaliser et de gérer un inventaire de tous les actifs importants.');
insert into measureDescriptiontext values (205,22,2,'Propriété des actifs','Il convient d’attribuer la propriété de chaque information et moyens de traitement de l’information à une partie définie de l’organisme.');
insert into measureDescriptiontext values (206,23,2,'Utilisation correcte des biens','Il convient d’identifier, de documenter et de mettre en oeuvre des règles permettant l’utilisation correcte de l’information et des biens associés aux moyens de traitement de l’information.');
insert into measureDescriptiontext values (207,24,2,'Classification des informations','');
insert into measureDescriptiontext values (208,25,2,'Lignes directrices pour la classification','Il convient de classer les informations en termes de valeur, d’exigences légales, de sensibilité et de criticité.');
insert into measureDescriptiontext values (209,26,2,'Marquage et manipulation de l’information','Il convient d’élaborer et de mettre en oeuvre un ensemble approprié de procédures pour le marquage et la manipulation de l’information, conformément au plan de classification adopté par l’organisme.');
insert into measureDescriptiontext values (210,27,2,'Sécurité liée aux ressources humaines','');
insert into measureDescriptiontext values (211,28,2,'Avant le recrutement','');
insert into measureDescriptiontext values (212,29,2,'Rôles et responsabilités','Il convient de définir et de documenter les rôles et responsabilités en matière de sécurité des salariés, contractants et utilisateurs tiers, conformément à la politique de sécurité de l’information de l’organisme.');
insert into measureDescriptiontext values (213,30,2,'Sélection','Qu’il s’agisse de postulants, de contractants ou d’utilisateurs tiers, il convient que les vérifications des informations concernant tous les candidats soient réalisées conformément aux lois, aux règlements et à l’étique et qu’elles soient proportionnelles aux exigences métier, à la classification des informations accessibles et aux risques identifiés.');
insert into measureDescriptiontext values (214,31,2,'Conditions d’embauche','Dans le cadre de leurs obligations contractuelles, il convient que les salariés, contractants et utilisateurs tiers se mettent d’accord sur les modalités du contrat d’embauche les liant et le signent. Il convient que ce contrat définisse les responsabilités de l’organisme et de l’autre partie quant à la sécurité de l’information.');
insert into measureDescriptiontext values (215,32,2,'Pendant la durée du contrat','');
insert into measureDescriptiontext values (216,33,2,'Obligation d''appliquer les règles','Il convient que la direction demande aux salariés, contractants et utilisateurs tiers d’appliquer les règles de sécurité conformément aux politiques et procédures établies de l’organisme.');
insert into measureDescriptiontext values (217,34,2,'Sensibilisation, qualification et formations en matière de sécurité de l’information','Il convient que l’ensemble des salariés d’un organisme et, le cas échant, les contractants et utilisateurs tiers suivent une formation adaptée sur la sensibilisation et reçoivent régulièrement les mises à jour des politiques et procédures de l’organisme, pertinentes pour leurs fonctions.');
insert into measureDescriptiontext values (218,35,2,'Processus disciplinaire','Il convient d’élaborer un processus disciplinaire formel pour les salariés ayant enfreint les règles de sécurité.');
insert into measureDescriptiontext values (219,36,2,'Fin ou modification de contrat','');
insert into measureDescriptiontext values (220,37,2,'Responsabilités en fin de contrat','Il convient que les responsabilités relatives aux fins ou aux modifications de contrats soient clairement définies et attribuées.');
insert into measureDescriptiontext values (221,38,2,'Restitution des biens','Il convient que tous les salariés, contractants et utilisateurs tiers restituent la totalité des biens de l’organisme qu’ils ont en leur possession à la fin de leur période d’emploi, contrat ou accord.');
insert into measureDescriptiontext values (222,39,2,'Retrait des droits d’accès','Il convient que les droits d’accès de l’ensemble des salariés, contractants et utilisateurs tiers à l’information et aux moyens de traitement de l’information soient supprimés à la fin de leur période d’emploi, ou modifiés en cas de modification du contrat ou de l’accord.');
insert into measureDescriptiontext values (223,40,2,'Sécurité physique et environnementale','');
insert into measureDescriptiontext values (224,41,2,'Zones sécurisées','');
insert into measureDescriptiontext values (225,42,2,'Périmètre de sécurité physique','Il convient de protéger les zones contenant des informations et des moyens de traitement de l’information par des périmètres de sécurité (obstacles tels que des murs, des portes avec un contrôle d’accès par cartes, ou des bureaux de réception avec personnel d’accueil).');
insert into measureDescriptiontext values (226,43,2,'Contrôles physiques des accès','Il convient de protéger les zones sécurisées par des contrôles à l’entrée adéquats pour s’assurer que seul le personnel habilité soit admis.');
insert into measureDescriptiontext values (227,44,2,'Sécurisation des bureaux, des salles et des équipements','Il convient de concevoir et d’appliquer des mesures de sécurité physique pour les bureaux, les salles et les équipements.');
insert into measureDescriptiontext values (228,45,2,'Protection contre les menaces extérieures et environnementales','Il convient de concevoir et d’appliquer des mesures de protection physique contre les dommages causés par les incendies, les inondations, les tremblements de terre, les explosions, les troubles civils et autres formes de catastrophes naturelles ou de sinistres provoqués par l’homme.');
insert into measureDescriptiontext values (229,46,2,'Travail dans les zones sécurisées','Il convient de concevoir et d’appliquer des mesures de protection physique et des directives pour le travail en zone sécurisée.');
insert into measureDescriptiontext values (230,47,2,'Zones d’accès public, de livraison et de chargement','Il convient de contrôler les points d’accès tels que les zones de livraison/chargement et les autres points par lesquels des personnes non habilitées peuvent pénétrer dans les locaux. Il convient également d’isoler les points d’accès, si possible, des moyens de traitement de l’information, de façon à éviter les accès non autorisés.');
insert into measureDescriptiontext values (231,48,2,'Sécurité du matériel','');
insert into measureDescriptiontext values (232,49,2,'Choix de l’emplacement et protection du matériel','Il convient de situer et de protéger le matériel de manière à réduire les risques de menaces et de dangers environnementaux et les possibilités d’accès non autorisé.');
insert into measureDescriptiontext values (233,50,2,'Services généraux','Il convient de protéger le matériel des coupures de courant et autres perturbations dues à une défaillance des services généraux.');
insert into measureDescriptiontext values (234,51,2,'Sécurité du câblage','Il convient de protéger les câbles électriques ou de télécommunications transportant des données contre toute interception ou dommage.');
insert into measureDescriptiontext values (235,52,2,'Maintenance du matériel','Il convient d’entretenir le matériel correctement pour garantir sa disponibilité permanente et son intégrité.');
insert into measureDescriptiontext values (236,53,2,'Sécurité du matériel hors des locaux','Il convient d’appliquer la sécurité au matériel utilisé hors des locaux de l’organisme en tenant compte des différents risques associés au travail hors site.');
insert into measureDescriptiontext values (237,54,2,'Mise au rebut ou recyclage sécurisé(e) du matériel','Il convient de vérifier tout le matériel contenant des supports de stockage soient vérifiées pour s’assurer que toute donnée sensible a bien été supprimée et que tout logiciel sous licence a bien été désinstallé ou écrasé de façon sécurisée, avant sa mise au rebut.');
insert into measureDescriptiontext values (238,55,2,'Sortie d’un bien','Il convient de ne pas sortir un matériel, des informations ou des logiciels des locaux de l’organisme sans autorisation préalable.');
insert into measureDescriptiontext values (239,56,2,'Gestion de l’exploitation et des télécommunications','');
insert into measureDescriptiontext values (240,57,2,'Procédures et responsabilités liées à l’exploitation','');
insert into measureDescriptiontext values (241,58,2,'Procédures d’exploitation documentées','Il convient que les procédures d’exploitation soient documentées, tenues à jour et disponibles pour tous les utilisateurs concernés.');
insert into measureDescriptiontext values (242,59,2,'Gestion des modifications','Il convient de contrôler les changements apportés aux systèmes et moyens de traitement de l’information.');
insert into measureDescriptiontext values (243,60,2,'Séparation des tâches','Il convient de séparer les tâches et les domaines de responsabilité pour réduire les occasions de modification ou de mauvais usage non autorisé(e) ou involontaire des biens de l’organisme.');
insert into measureDescriptiontext values (244,61,2,'Séparation des équipements de développement, de test et d’exploitation','Il convient de séparer les équipements de développement, de test et d’exploitation pour réduire les risques d’accès ou de changements non autorisés dans le système en exploitation.');
insert into measureDescriptiontext values (245,62,2,'Gestion de la prestation de service par un tiers','');
insert into measureDescriptiontext values (246,63,2,'Prestation de service','Il convient de s’assurer que les mesures de sécurité, les définitions du service et les niveaux de prestation prévus dans l’accord de prestation de service tiers sont mis en oeuvre, appliqués et tenus à jour par le tiers.');
insert into measureDescriptiontext values (247,64,2,'Surveillance et réexamen des services tiers','Il convient que les services, rapports et enregistrements fournis par les tiers soient régulièrement contrôlés et réexaminés, et que des audits soient régulièrement réalisés.');
insert into measureDescriptiontext values (248,65,2,'Gestion des modifications dans les services tiers','Il convient de gérer les changements effectués dans la prestation de service, comprenant le maintien et l’amélioration des politiques, procédures et mesures existant en matière de sécurité de l’information, en tenant compte de la criticité des systèmes et processus de gestion concernés et de la réévaluation du risque.');
insert into measureDescriptiontext values (249,66,2,'Planification et acceptation du système','');
insert into measureDescriptiontext values (250,67,2,'Dimensionnement','Il convient de surveiller et d’ajuster au plus près l’utilisation des ressources et il convient de faire des projections sur les dimensionnements futurs pour assurer les performances requises pour le système.');
insert into measureDescriptiontext values (251,68,2,'Acceptation du système','Il convient de fixer les critères d’acceptation pour les nouveaux systèmes d’information, les nouvelles versions et les mises à niveau, et de réaliser les tests adaptés du (des) système(s) au moment du développement et préalablement à leur acceptation.');
insert into measureDescriptiontext values (252,69,2,'Protection contre les codes malveillant et mobile','');
insert into measureDescriptiontext values (253,70,2,'Mesures contre les codes malveillants','Il convient de mettre en oeuvre des mesures de détection, de prévention et de récupération pour se protéger des codes malveillants ainsi que des procédures appropriées de sensibilisation des utilisateurs.');
insert into measureDescriptiontext values (254,71,2,'Mesures contre le code mobile','Lorsque l’utilisation de code mobile est autorisée, il convient que la configuration garantisse que le code mobile fonctionne selon une politique de sécurité clairement définie et il convient d’empêcher tout code mobile non autorisé de s’exécuter.');
insert into measureDescriptiontext values (255,72,2,'Sauvegarde','');
insert into measureDescriptiontext values (256,73,2,'Sauvegarde des informations','Il convient de réaliser des copies de sauvegarde des informations et logiciels et de les soumettre régulièrement à essai conformément à la politique de sauvegarde convenue.');
insert into measureDescriptiontext values (257,74,2,'Gestion de la sécurité des réseaux','');
insert into measureDescriptiontext values (258,75,2,'Mesures sur les réseaux','Il convient de gérer et de contrôler les réseaux de manière adéquate pour qu’ils soient protégés des menaces et de maintenir la sécurité des systèmes et des applications utilisant le réseau, notamment les informations en transit.');
insert into measureDescriptiontext values (259,76,2,'Sécurité des services réseau','Pour tous les services réseau, il convient d’identifier les fonctions réseau, les niveaux de service et les exigences de gestion, et de les intégrer dans tout accord sur les services réseau, qu’ils soient fournis en interne ou en externe.');
insert into measureDescriptiontext values (260,77,2,'Manipulation des supports','');
insert into measureDescriptiontext values (261,78,2,'Gestion des supports amovibles','Il convient de mettre en place des procédures pour la gestion des supports amovibles.');
insert into measureDescriptiontext values (262,79,2,'Mise au rebut des supports','Il convient de mettre au rebut de façon sûre les supports qui ne servent plus, en suivant des procédures formelles.');
insert into measureDescriptiontext values (263,80,2,'Procédures de manipulation des informations','Il convient d’établir des procédures de manipulation et de stockage des informations pour protéger ces informations d’une divulgation non autorisée ou d’un mauvais usage.');
insert into measureDescriptiontext values (264,81,2,'Sécurité de la documentation système','Il convient de protéger la documentation système contre les accès non autorisés.');
insert into measureDescriptiontext values (265,82,2,'Échange des informations','');
insert into measureDescriptiontext values (266,83,2,'Politiques et procédures d’échange des informations','Il convient de mettre en place des politiques, procédures et mesures d’échange formelles pour protéger les échanges d’informations transitant par tous types d’équipements de télécommunication.');
insert into measureDescriptiontext values (267,84,2,'Accords d’échange','Il convient de conclure des accords pour l’échange d’informations et de logiciels entre l’organisme et la partie externe.');
insert into measureDescriptiontext values (268,85,2,'Supports physiques en transit','Il convient de protéger les supports contenant des informations contre les accès non autorisés, le mauvais usage ou l’altération lors du transport hors des limites physiques de l’organisme.');
insert into measureDescriptiontext values (269,86,2,'Messagerie électronique','Il convient de protéger de manière adéquate les informations transitant par la messagerie électronique.');
insert into measureDescriptiontext values (270,87,2,'Systèmes d’information d’entreprise','Il convient d’élaborer et de mettre en oeuvre des politiques et procédures pour protéger l’information liée à l’interconnexion de systèmes d’informations d’entreprise.');
insert into measureDescriptiontext values (271,88,2,'Services de commerce électronique','');
insert into measureDescriptiontext values (272,89,2,'Commerce électronique','Il convient de protéger l’information transitant par le commerce électronique transmise sur les réseaux publics contre les activités frauduleuses, les litiges sur les contrats et la divulgation et la modification non autorisées.');
insert into measureDescriptiontext values (273,90,2,'Transactions en ligne','Il convient de protéger les informations transitant par les transactions en ligne pour empêcher la transmission incomplète, les erreurs d’acheminement, la modification non autorisée, la divulgation non autorisée, la duplication non autorisée du message ou la réémission.');
insert into measureDescriptiontext values (274,91,2,'Informations à disposition du public','Il convient de protéger l’intégrité des informations mises à disposition sur un système accessible au public pour empêcher toute modification non autorisée.');
insert into measureDescriptiontext values (275,92,2,'Surveillance','');
insert into measureDescriptiontext values (276,93,2,'Rapport d’audit','Il convient que les rapports d’audit, qui enregistrent les activités des utilisateurs, les exceptions et les événements liés à la sécurité soient produits et conservés pendant une période préalablement définie afin de faciliter les investigations ultérieures et la surveillance du contrôle d’accès.');
insert into measureDescriptiontext values (277,94,2,'Surveillance de l’exploitation du système','Il convient d’établir des procédures permettant de surveiller l’utilisation des moyens de traitement de l’information et de réexaminer périodiquement les résultats des activités de surveillance.');
insert into measureDescriptiontext values (278,95,2,'Protection des informations journalisées','Il convient de protéger les équipements de journalisation et les informations journalisées contre le sabotage et les accès non autorisés.');
insert into measureDescriptiontext values (279,96,2,'Journal administrateur et journal des opérations','Il convient de journaliser les activités de l’administrateur système et de l’opérateur système.');
insert into measureDescriptiontext values (280,97,2,'Rapports de défaut','Il convient de journaliser et d’analyser les éventuels défauts et de prendre les mesures appropriées.');
insert into measureDescriptiontext values (281,98,2,'Synchronisation des horloges','Il convient de synchroniser les horloges des différents systèmes de traitement de l’information d’un organisme ou d’un domaine de sécurité à l’aide d’une source de temps précise et préalablement définie.');
insert into measureDescriptiontext values (282,99,2,'Contrôle d’accès','');
insert into measureDescriptiontext values (283,100,2,'Exigences métier relatives au contrôle d’accès','');
insert into measureDescriptiontext values (284,101,2,'Politique de contrôle d’accès','Il convient d’établir, de documenter et de réexaminer une politique de contrôle d’accès sur la base des exigences d’exploitation et de sécurité.');
insert into measureDescriptiontext values (285,102,2,'Gestion de l’accès utilisateur','');
insert into measureDescriptiontext values (286,103,2,'Enregistrement des utilisateurs','Il convient de définir une procédure formelle d’enregistrement et de désinscription des utilisateurs destinée à accorder et à supprimer l’accès à tous les systèmes et services d’information.');
insert into measureDescriptiontext values (287,104,2,'Gestion des privilèges','Il convient de restreindre et de contrôler l’attribution et l’utilisation des privilèges.');
insert into measureDescriptiontext values (288,105,2,'Gestion du mot de passe utilisateur','Il convient que l’attribution de mots de passe soit réalisée dans le cadre d’un processus formel.');
insert into measureDescriptiontext values (289,106,2,'Réexamen des droits d’accès utilisateurs','Il convient que la direction revoie les droits d’accès utilisateurs à intervalles réguliers par le biais d’un processus formel.');
insert into measureDescriptiontext values (290,107,2,'Responsabilités utilisateurs','');
insert into measureDescriptiontext values (291,108,2,'Utilisation du mot de passe','Il convient de demander aux utilisateurs de respecter les bonnes pratiques de sécurité lors de la sélection et de l’utilisation de mots de passe.');
insert into measureDescriptiontext values (292,109,2,'Matériel utilisateur laissé sans surveillance','Il convient que les utilisateurs s’assurent que tout matériel laissé sans surveillance est doté d’un dispositif de protection approprié.');
insert into measureDescriptiontext values (293,110,2,'Politique du bureau propre et de l’écran vide','Il convient d’adopter une politique du bureau propre pour les documents papier et les supports de stockage amovibles, et une politique de l’écran vide pour les moyens de traitement de l’information.');
insert into measureDescriptiontext values (294,111,2,'Contrôle d’accès au réseau','');
insert into measureDescriptiontext values (295,112,2,'Politique relative à l’utilisation des services en réseau','Il convient que les utilisateurs aient uniquement accès aux services pour lesquels ils ont spécifiquement reçu une autorisation.');
insert into measureDescriptiontext values (296,113,2,'Authentification de l’utilisateur pour les connexions externes','Il convient d’utiliser des méthodes d’authentification appropriées pour contrôler l’accès d’utilisateurs distants.');
insert into measureDescriptiontext values (297,114,2,'Identification des matériels en réseau','Il convient de considérer l’identification automatique de matériels comme un moyen d’authentification des connexions à partir de lieux et matériels spécifiques.');
insert into measureDescriptiontext values (298,115,2,'Protection des ports de diagnostic et de configuration à distance','Il convient de contrôler l’accès physique et logique aux ports de diagnostic et de configuration à distance.');
insert into measureDescriptiontext values (299,116,2,'Cloisonnement des réseaux','Il convient que les groupes de services d’information, d’utilisateurs et de systèmes d’information soient séparés sur le réseau.');
insert into measureDescriptiontext values (300,117,2,'Mesure relative à la connexion réseau','Pour les réseaux partagés, en particulier les réseaux qui s’étendent au-delà des limites de l’organisme, il convient de restreindre la capacité de connexion réseau des utilisateurs, conformément à la politique de contrôle d’accès et les exigences relatives aux applications de gestion (voir 11.1).');
insert into measureDescriptiontext values (301,118,2,'Contrôle du routage réseau','Il convient de mettre en oeuvre des mesures du routage des réseaux afin d’éviter que les connexions réseau et les flux d’informations ne portent atteinte à la politique de contrôle d’accès des applications de gestion.');
insert into measureDescriptiontext values (302,119,2,'Contrôle d’accès au système d’exploitation','');
insert into measureDescriptiontext values (303,120,2,'Ouverture de sessions sécurisées','Il convient que l’accès aux systèmes d’exploitation soit soumis à une procédure sécurisée d’ouverture de session.');
insert into measureDescriptiontext values (304,121,2,'Identification et authentification de l’utilisateur','Il convient d’attribuer à chaque utilisateur un identifiant unique et exclusif et de choisir une technique d’authentification permettant de vérifier l’identité déclarée par l’utilisateur.');
insert into measureDescriptiontext values (305,122,2,'Système de gestion des mots de passe','Il convient que les systèmes qui gèrent les mots de passe soient interactifs et fournissent des mots de passe de qualité.');
insert into measureDescriptiontext values (306,123,2,'Emploi des utilitaires système','Il convient de limiter et de contrôler étroitement l’emploi des programmes utilitaires permettant de contourner les mesures d’un système ou d’une application.');
insert into measureDescriptiontext values (307,124,2,'Déconnexion automatique des sessions inactives','Il convient que les sessions inactives soient déconnectées après une période d’inactivité définie.');
insert into measureDescriptiontext values (308,125,2,'Limitation du temps de connexion','Il convient de restreindre les temps de connexion afin d’apporter un niveau de sécurité supplémentaire aux applications à haut risque.');
insert into measureDescriptiontext values (309,126,2,'Contrôle d’accès aux applications et à l’information','');
insert into measureDescriptiontext values (310,127,2,'Assistance technique avec accès restreint','Pour les utilisateurs et le personnel chargé de l’assistance technique, il convient de restreindre l’accès aux informations et aux fonctions applicatives conformément à la politique de contrôle d’accès.');
insert into measureDescriptiontext values (311,128,2,'Isolement des systèmes sensibles','Il convient que les systèmes sensibles disposent d’un environnement informatique dédié (isolé).');
insert into measureDescriptiontext values (312,129,2,'Informatique mobile et télétravail','');
insert into measureDescriptiontext values (313,130,2,'Informatique mobile et télécommunications','Il convient de mettre en place une procédure formelle et des mesures de sécurité appropriées pour assurer une protection contre le risque lié à l’utilisation d’appareils informatiques et de communication mobiles.');
insert into measureDescriptiontext values (314,131,2,'Télétravail','Il convient d’élaborer et de mettre en oeuvre une politique, des procédures et des programmes opérationnels spécifiques au télétravail.');
insert into measureDescriptiontext values (315,132,2,'Acquisition, développement et maintenance des systèmes d’information','');
insert into measureDescriptiontext values (316,133,2,'Exigences de sécurité applicables aux systèmes d’information','');
insert into measureDescriptiontext values (317,134,2,'Analyse et spécification des exigences de sécurité','Il convient que les exigences métier relatives aux nouveaux systèmes d’information ou que les améliorations apportées aux systèmes d’information existants spécifient les exigences de sécurité.');
insert into measureDescriptiontext values (318,135,2,'Bon fonctionnement des applications','');
insert into measureDescriptiontext values (319,136,2,'Validation des données d’entrée','Il convient de valider les données entrées dans les applications afin de vérifier si elles sont correctes et appropriées.');
insert into measureDescriptiontext values (320,137,2,'Mesure relative au traitement interne','Il convient d’inclure des mesures de validation dans les applications afin de détecter les éventuelles altérations de l’information dues à des erreurs de traitement ou des actes délibérés.');
insert into measureDescriptiontext values (321,138,2,'Intégrité des messages','Il convient d’identifier les exigences relatives à l’authentification et à la protection de l’intégrité des messages. Il convient également d’identifier et de mettre en oeuvre les mesures appropriées.');
insert into measureDescriptiontext values (322,139,2,'Validation des données de sortie','Il convient de valider les données de sortie d’une application pour vérifier que le traitement des informations stockées est correct et adapté aux circonstances.');
insert into measureDescriptiontext values (323,140,2,'Mesures cryptographiques','');
insert into measureDescriptiontext values (324,141,2,'Politique d’utilisation des mesures cryptographiques','Il convient d’élaborer et de mettre en oeuvre une politique d’utilisation des mesures cryptographiques en vue de protéger l’information.');
insert into measureDescriptiontext values (325,142,2,'Gestion des clés','Il convient qu’une procédure de gestion des clés vienne à l’appui de la politique de l’organisme en matière de chiffrement.');
insert into measureDescriptiontext values (326,143,2,'Sécurité des fichiers système','');
insert into measureDescriptiontext values (327,144,2,'Mesure relative aux logiciels en exploitation','Il convient de mettre des procédures en place pour contrôler l’installation du logiciel sur les systèmes en exploitation.');
insert into measureDescriptiontext values (328,145,2,'Protection des données système d’essai','Il convient que les données d’essai soient sélectionnées avec soin, protégées et contrôlées.');
insert into measureDescriptiontext values (329,146,2,'Contrôle d’accès au code source du programme','Il convient de restreindre l’accès au code source du programme.');
insert into measureDescriptiontext values (330,147,2,'Sécurité en matière de développement et d’assistance technique','');
insert into measureDescriptiontext values (331,148,2,'Procédures de contrôle des modifications','Il convient de contrôler la mise en oeuvre des modifications par le biais de procédures formelles.');
insert into measureDescriptiontext values (332,149,2,'Réexamen technique des applications après modification du système d’exploitation','Lorsque des modifications sont apportées aux systèmes d’exploitation, il convient de réexaminer et de soumettre à essai les applications critiques de gestion afin de vérifier l’absence de tout effet indésirable sur l’activité ou sur la sécurité.');
insert into measureDescriptiontext values (333,150,2,'Restrictions relatives à la modification des progiciels','Il convient de ne pas encourager la modification des progiciels et de se limiter aux changements nécessaires. Il convient également d’exercer un contrôle strict sur ces modifications.');
insert into measureDescriptiontext values (334,151,2,'Fuite d’informations','Il convient d’empêcher toute possibilité de fuite d’informations.');
insert into measureDescriptiontext values (335,152,2,'Externalisation du développement logiciel','Il convient que l’organisme encadre et contrôle le développement logiciel externalisé.');
insert into measureDescriptiontext values (336,153,2,'Gestion des vulnérabilités techniques','');
insert into measureDescriptiontext values (337,154,2,'Mesure relative aux vulnérabilités techniques','Il convient d’être informé en temps voulu de toute vulnérabilité technique des systèmes d’information en exploitation, d’évaluer l’exposition de l’organisme audites vulnérabilités et d’entreprendre les actions appropriées pour traiter le risque associé.');
insert into measureDescriptiontext values (338,155,2,'Gestion des incidents liés à la sécurité de l’information','');
insert into measureDescriptiontext values (339,156,2,'Signalement des événements et des failles liés à la sécurité de l’information','');
insert into measureDescriptiontext values (340,157,2,'Signalement des événements liés à la sécurité de l’information','Il convient de signaler, dans les meilleurs délais, les événements liés à la sécurité de l’information, par les voies hiérarchiques appropriées.');
insert into measureDescriptiontext values (341,158,2,'Signalement des failles de sécurité','Il convient de demander à tous les salariés, contractants et utilisateurs tiers des systèmes et services d’information de noter et de signaler toute faille de sécurité observée ou soupçonnée dans les systèmes ou services.');
insert into measureDescriptiontext values (342,159,2,'Gestion des améliorations et incidents liés à la sécurité de l’information','');
insert into measureDescriptiontext values (343,160,2,'Responsabilités et procédures','Il convient d’établir des responsabilités et des procédures permettant de garantir une réponse rapide, efficace et pertinente en cas d’incident lié à la sécurité de l’information.');
insert into measureDescriptiontext values (344,161,2,'Exploitation des incidents liés à la sécurité de l’information déjà survenus','Il convient de mettre en place des mécanismes permettant de quantifier et surveiller les différents types d’incidents liés à la sécurité de l’information ainsi que leur volume et les coûts associés.');
insert into measureDescriptiontext values (345,162,2,'Collecte de preuves','Lorsqu’une action en justice civile ou pénale est engagée contre une personne physique ou un organisme, à la suite d’un incident lié à la sécurité de l’information, il convient de recueillir, conserver et présenter les informations conformément aux dispositions légales relatives à la présentation de preuves régissant la ou les juridiction(s) compétente(s).');
insert into measureDescriptiontext values (346,163,2,'Gestion du plan de continuité de l’activité','');
insert into measureDescriptiontext values (347,164,2,'Aspects de la sécurité de l’information en matière de gestion de la continuité de l’activité','');
insert into measureDescriptiontext values (348,165,2,'Intégration de la sécurité de l’information dans le processus de gestion du plan de continuité de l’activité','Il convient d’élaborer et de gérer un processus de continuité de l’activité dans l’ensemble de l’organisme qui satisfait aux exigences en matière de sécurité de l’information requises pour la continuité de l’activité de l’organisme.');
insert into measureDescriptiontext values (349,166,2,'Continuité de l’activité et appréciation du risque','Il convient d’identifier les événements pouvant être à l’origine d’interruptions des processus métier tout comme la probabilité et l’impact de telles interruptions et leurs conséquences pour la sécurité de l’information.');
insert into measureDescriptiontext values (350,167,2,'Élaboration et mise en oeuvre des plans de continuité intégrant la sécurité de l’information','Il convient d’élaborer et de mettre en oeuvre des plans destinés à maintenir ou à restaurer l’exploitation et à assurer la disponibilité des informations au niveau et dans les délais requis suite à une interruption ou une panne affectant les processus métier cruciaux.');
insert into measureDescriptiontext values (351,168,2,'Cadre de la planification de la continuité de l’activité','Il convient gérer un cadre unique pour les plans de continuité de l’activité afin de garantir la cohérence de l’ensemble des plans, de satisfaire de manière constante aux exigences en matière de sécurité de l’information et d’identifier les priorités en matière de mise à l’essai et de maintenance.');
insert into measureDescriptiontext values (352,169,2,'Mise à l’essai, gestion et appréciation constante des plans de continuité de l’activité','Il convient de soumettre à essai et de mettre à jour régulièrement les plans de continuité de l’activité afin de s’assurer qu’ils sont actualisés et efficaces.');
insert into measureDescriptiontext values (353,170,2,'Conformité','');
insert into measureDescriptiontext values (354,171,2,'Conformité avec les exigences légales','');
insert into measureDescriptiontext values (355,172,2,'Identification de la législation en vigueur','Pour chaque système d’information et pour l’organisme, il convient de définir, documenter et mettre à jour explicitement toutes les exigences légales, réglementaires et contractuelles en vigueur, ainsi que la procédure utilisée par l’organisme pour satisfaire à ces exigences.');
insert into measureDescriptiontext values (356,173,2,'Droits de propriété intellectuelle','Il convient de mettre en oeuvre des procédures appropriées visant à garantir la conformité avec les exigences légales, réglementaires et contractuelles concernant l’utilisation du matériel pouvant être soumis à des droits de propriété intellectuelle et l’utilisation des logiciels propriétaires.');
insert into measureDescriptiontext values (357,174,2,'Protection des enregistrements de l’organisme','Il convient de protéger les enregistrements importants de la perte, destruction et falsification conformément aux exigences légales, réglementaires et aux exigences métier.');
insert into measureDescriptiontext values (358,175,2,'Protection des données et confidentialité des informations relatives à la vie privée','Il convient de garantir la protection et la confidentialité des données telles que l’exigent la législation ou les réglementations applicables, et les clauses contractuelles le cas échéant.');
insert into measureDescriptiontext values (359,176,2,'Mesure préventive à l’égard du mauvais usage des moyens de traitement de l’information','Il convient de dissuader les utilisateurs de toute utilisation de moyens de traitement de l’information à des fins illégales.');
insert into measureDescriptiontext values (360,177,2,'Réglementation relative aux mesures cryptographiques','Il convient de prendre des mesures cryptographiques conformément aux accords, lois et réglementations applicables.');
insert into measureDescriptiontext values (361,178,2,'Conformité avec les politiques et normes de sécurité et conformité technique','');
insert into measureDescriptiontext values (362,179,2,'Conformité avec les politiques et les normes de sécurité','Il convient que les responsables s’assurent de l’exécution correcte de l’ensemble des procédures de sécurité placées sous leur responsabilité en vue de garantir leur conformité avec les politiques et normes de sécurité.');
insert into measureDescriptiontext values (363,180,2,'Vérification de la conformité technique','Il convient de vérifier régulièrement la conformité des systèmes d’information avec les normes relatives à la mise en oeuvre de la sécurité.');
insert into measureDescriptiontext values (364,181,2,'Prises en compte de l’audit du système d’information','');
insert into measureDescriptiontext values (365,182,2,'Contrôles de l’audit du système d’information','Il convient que les exigences d’audit et les activités impliquant des contrôles des systèmes en exploitation soient planifiées de manière précise et qu’elles soient le résultat d’un accord afin de réduire le plus possible le risque de perturbations des processus métier.');
insert into measureDescriptiontext values (366,183,2,'Protection des outils d’audit du système d’information','Il convient de protéger l’accès aux outils d’audit du système d’information afin d’empêcher tous mauvais usage ou compromission éventuels.');
INSERT INTO `measureDescriptiontext` VALUES (367,184,1,'Risk Assessment and treatment','');
INSERT INTO `measureDescriptiontext` VALUES (368,185,1,'Assessing Security risks','');
INSERT INTO `measureDescriptiontext` VALUES (369,186,1,'Assessing Security risks','Risk assessments should identify, quantify, and prioritize risks against criteria for risk acceptance and objectives relevant to the organization. Risk assessments should also be performed periodically to address changes in the security requirements and in the risk situation, e.g. in the assets, threats, vulnerabilities, impacts, the risk evaluation, and when significant changes occur. These risk assessments should be undertaken in a methodical manner capable of producing comparable and reproducible results.');
INSERT INTO `measureDescriptiontext` VALUES (370,187,1,'Treating security risks','');
INSERT INTO `measureDescriptiontext` VALUES (371,188,1,'Treating security risks','For each of the risks identified following the risk assessment a risk treatment decision needs to be made. Possible options for risk treatment include:\na) applying appropriate controls to reduce the risks;\nb) knowingly and objectively accepting risks, providing they clearly satisfy the organization’s policy and criteria for risk acceptance; \nc) avoiding risks by not allowing actions that would cause the risks to occur; \nd) transferring the associated risks to other parties, e.g. insurers or suppliers. For those risks where the risk treatment decision has been to apply appropriate controls, these controls should be selected and implemented to meet the requirements identified by a risk assessment.');
INSERT INTO `measureDescriptiontext` VALUES (372,189,1,'Security Policy','');
INSERT INTO `measureDescriptiontext` VALUES (373,190,1,'Information security policy','');
INSERT INTO `measureDescriptiontext` VALUES (374,191,1,'Information security policy document','An information security policy document should be approved by management, and published and communicated to all employees and relevant external parties.\n\nImplementation guidance\nThe information security policy document should state management commitment and set out the organization’s approach to managing information security. The policy document should contain statements concerning:\na) a definition of information security, its overall objectives and scope and the importance of\nsecurity as an enabling mechanism for information sharing (see introduction);\nb) a statement of management intent, supporting the goals and principles of information security in line with the business strategy and objectives;\nc) a framework for setting control objectives and controls, including the structure of risk assessment and risk management;\nd) a brief explanation of the security policies, principles, standards, and compliance requirements of particular importance to the organization, including:\n1) compliance with legislative, regulatory, and contractual requirements;\n2) security education, training, and awareness requirements;\n3) business continuity management;\n4) consequences of information security policy violations;\ne) a definition of general and specific responsibilities for information security management, including reporting information security incidents;\nf) references to documentation which may support the policy, e.g. more detailed security policies and procedures for specific information systems or security rules users should comply with.\n\nThis information security policy should be communicated throughout the organization to users in a form that is relevant, accessible and understandable to the intended reader.');
INSERT INTO `measureDescriptiontext` VALUES (375,192,1,'Review of the information security policy','The information security policy should be reviewed at planned intervals or if significant changes occur to ensure its continuing suitability, adequacy, and effectiveness.\n\nImplementation guidance\nThe information security policy should have an owner who has approved management responsibility for the development, review, and evaluation of the security policy. The review should include assessing opportunities for improvement of the organization’s information security policy and approach to managing information security in response to changes to the organizational environment, business circumstances, legal conditions, or technical environment. The review of the information security policy should take account of the results of management reviews. There should be defined management review procedures, including a schedule or period of the review.\n\nThe input to the management review should include information on:\na) feedback from interested parties;\nb) results of independent reviews (see 6.1.8);\nc) status of preventive and corrective actions (see 6.1.8 and 15.2.1);\nd) results of previous management reviews;\ne) process performance and information security policy compliance;\nf) changes that could affect the organization’s approach to managing information security, including changes to the organizational environment, business circumstances, resource availability, contractual, regulatory, and legal conditions, or to the technical environment;\ng) trends related to threats and vulnerabilities;\nh) reported information security incidents (see 13.1);\ni) recommendations provided by relevant authorities (see 6.1.6). The output from the management review should include any decisions and actions related to:\na) improvement of the organization’s approach to managing information security and its processes;\nb) improvement of control objectives and controls;\nc) improvement in the allocation of resources and/or responsibilities. \nA record of the management review should be maintained. \nManagement approval for the revised policy should be obtained.');
INSERT INTO `measureDescriptiontext` VALUES (376,193,1,'Organization of information security','');
INSERT INTO `measureDescriptiontext` VALUES (377,194,1,'Internal Organization','');
INSERT INTO `measureDescriptiontext` VALUES (378,195,1,'Management commitment to information security','Management should actively support security within the organization through clear direction, demonstrated commitment, explicit assignment, and acknowledgment of information security responsibilities.\n\nImplementation guidance\nManagement should:\na) ensure that information security goals are identified, meet the organizational requirements, and are integrated in relevant processes;\nb) formulate, review, and approve information security policy;\nc) review the effectiveness of the implementation of the information security policy;\nd) provide clear direction and visible management support for security initiatives;\ne) provide the resources needed for information security;\nf) approve assignment of specific roles and responsibilities for information security across the organization;\ng) initiate plans and programs to maintain information security awareness;\nh) ensure that the implementation of information security controls is co-ordinated across the organization (see 6.1.2).\n\nManagement should identify the needs for internal or external specialist information security advice, and review and coordinate results of the advice throughout the organization. Depending on the size of the organization, such responsibilities could be handled by a dedicated management forum or by an existing management body, such as the board of directors.');
INSERT INTO `measureDescriptiontext` VALUES (379,196,1,'Information security coordination','Information security activities should be co-ordinated by representatives from different parts of the organization with relevant roles and job functions.\n\nImplementation guidance\nTypically, information security co-ordination should involve the co-operation and collaboration of managers, users, administrators, application designers, auditors and security personnel, and specialist skills in areas such as insurance, legal issues, human resources, IT or risk management.\nThis activity should:\na) ensure that security activities are executed in compliance with the information security policy;\nb) identify how to handle non-compliances;\nc) approve methodologies and processes for information security, e.g. risk assessment, information classification;\nd) identify significant threat changes and exposure of information and information processing facilities to threats;\ne) assess the adequacy and co-ordinate the implementation of information security controls;\nf) effectively promote information security education, training and awareness throughout the organization;\ng) evaluate information received from the monitoring and reviewing of information security incidents, and recommend appropriate actions in response to identified information security incidents.\n\nIf the organization does not use a separate cross-functional group, e.g. because such a group is not appropriate for the organization’s size, the actions described above should be undertaken by another suitable management body or individual manager.');
INSERT INTO `measureDescriptiontext` VALUES (380,197,1,'Allocation of information security responsibilities','All information security responsibilities should be clearly defined.\n\nImplementation guidance\nAllocation of information security responsibilities should be done in accordance with the information security policy (see clause 4). Responsibilities for the protection of individual assets and for carrying out specific security processes should be clearly identified. This responsibility should be supplemented, where necessary, with more detailed guidance for specific sites and information processing facilities. Local responsibilities for the protection of assets and for carrying out specific security processes, such as business continuity planning, should be clearly defined. Individuals with allocated security responsibilities may delegate security tasks to others. Nevertheless they remain responsible and should determine that any delegated tasks have been correctly performed. Areas for which individuals are responsible should be clearly stated; in particular the following should\ntake place: \na) the assets and security processes associated with each particular system should be identified and clearly defined;\nb) the entity responsible for each asset or security process should be assigned and the details of this responsibility should be documented (see also 7.1.2);\nc) authorization levels should be clearly defined and documented.');
INSERT INTO `measureDescriptiontext` VALUES (381,198,1,'Authorization process for information processing facilities','A management authorization process for new information processing facilities should be defined and implemented.\n\nImplementation guidance\nThe following guidelines should be considered for the authorization process:\na) new facilities should have appropriate user management authorization, authorizing their purpose and use. Authorization should also be obtained from the manager responsible for maintaining the local information system security environment to ensure that all relevant security policies and requirements are met;\nb) where necessary, hardware and software should be checked to ensure that they are compatible with other system components;\nc) the use of personal or privately owned information processing facilities, e.g. laptops, home-computers or hand-held devices, for processing business information, may introduce new vulnerabilities and necessary controls should be identified and implemented.');
INSERT INTO `measureDescriptiontext` VALUES (382,199,1,'Confidentiality agreements','Requirements for confidentiality or non-disclosure agreements reflecting the organization’s needs for the protection of information should be identified and regularly reviewed.\n\nImplementation guidance\nConfidentiality or non-disclosure agreements should address the requirement to protect confidential information using legally enforceable terms. To identify requirements for confidentiality or nondisclosure agreements, the following elements should be considered:\na) a definition of the information to be protected (e.g. confidential information);\nb) expected duration of an agreement, including cases where confidentiality might need to be maintained indefinitely;\nc) required actions when an agreement is terminated;\nd) responsibilities and actions of signatories to avoid unauthorized information disclosure (such as ‘need to know’);\ne) ownership of information, trade secrets and intellectual property, and how this relates to the protection of confidential information;\nf) the permitted use of confidential information, and rights of the signatory to use information;\ng) the right to audit and monitor activities that involve confidential information;\nh) process for notification and reporting of unauthorized disclosure or confidential information breaches;\ni) terms for information to be returned or destroyed at agreement cessation; and\nj) expected actions to be taken in case of a breach of this agreement.\nBased on an organization’s security requirements, other elements may be needed in a confidentiality or non-disclosure agreement.\nConfidentiality and non-disclosure agreements should comply with all applicable laws and regulations for the jurisdiction to which it applies (see also 15.1.1).\nRequirements for confidentiality and non-disclosure agreements should be reviewed periodically and when changes occur that influence these requirements.');
INSERT INTO `measureDescriptiontext` VALUES (383,200,1,'Contact with authorities','Appropriate contacts with relevant authorities should be maintained.\n\nImplementation guidance\nOrganizations should have procedures in place that specify when and by whom authorities (e.g. law enforcement, fire department, supervisory authorities) should be contacted, and how identified information security incidents should be reported in a timely manner if it is suspected that laws may have been broken. Organizations under attack from the Internet may need external third parties (e.g. an Internet service provider or telecommunications operator) to take action against the attack source.');
INSERT INTO `measureDescriptiontext` VALUES (384,201,1,'Contact with special interest groups','Appropriate contacts with special interest groups or other specialist security forums and professional associations should be maintained.\n\nImplementation guidance\nMembership in special interest groups or forums should be considered as a means to:\na) improve knowledge about best practices and staying up to date with relevant security information;\nb) ensure the understanding of the information security environment is current and complete; \nc) receive early warnings of alerts, advisories, and patches pertaining to attacks and vulnerabilities;\nd) gain access to specialist information security advice;\ne) share and exchange information about new technologies, products, threats, or vulnerabilities;\nf) provide suitable liaison points when dealing with information security incidents (see also 13.2.1).');
INSERT INTO `measureDescriptiontext` VALUES (385,202,1,'Independent review of information security','The organization’s approach to managing information security and its implementation (i.e. control objectives, controls, policies, processes, and procedures for information security) should be reviewed independently at planned intervals, or when significant changes to the security implementation occur.\n\nImplementation guidance\nThe independent review should be initiated by management. Such an independent review is necessary to ensure the continuing suitability, adequacy, and effectiveness of the organization''s approach to managing information security. The review should include assessing opportunities for improvement\nand the need for changes to the approach to security, including the policy and control objectives. Such a review should be carried out by individuals independent of the area under review, e.g. the internal audit function, an independent manager or a third party organization specializing in such\nreviews. Individuals carrying out these reviews should have the appropriate skills and experience. The results of the independent review should be recorded and reported to the management who initiated the review. These records should be maintained. If the independent review identifies that the organization’s approach and implementation to managing information security is inadequate or not compliant with the direction for information security stated in the information security policy document (see 5.1.1), management should consider corrective actions.');
INSERT INTO `measureDescriptiontext` VALUES (386,203,1,'External parties','');
INSERT INTO `measureDescriptiontext` VALUES (387,204,1,'Identification of risks related to external parties','The risks to the organization’s information and information processing facilities from business processes involving external parties should be identified and appropriate controls implemented before granting access.\n\nImplementation guidance\nWhere there is a need to allow an external party access to the information processing facilities or information of an organization, a risk assessment (see also Section 4) should be carried out to identify any requirements for specific controls. The identification of risks related to external party access\nshould take into account the following issues:\na) the information processing facilities an external party is required to access;\nb) the type of access the external party will have to the information and information processing facilities, e.g.:\n1) physical access, e.g. to offices, computer rooms, filing cabinets;\n2) logical access, e.g. to an organization’s databases, information systems;\n3) network connectivity between the organization’s and the external party’s network(s), e.g. permanent connection, remote access;\n4) whether the access is taking place on-site or off-site;\nc) the value and sensitivity of the information involved, and its criticality for business operations;\nd) the controls necessary to protect information that is not intended to be accessible by external parties;\ne) the external party personnel involved in handling the organization’s information;\nf) how the organization or personnel authorized to have access can be identified, the authorization verified, and how often this needs to be reconfirmed;\ng) the different means and controls employed by the external party when storing, processing, communicating, sharing and exchanging information;\nh) the impact of access not being available to the external party when required, and the external party entering or receiving inaccurate or misleading information;\ni) practices and procedures to deal with information security incidents and potential damages, and the terms and conditions for the continuation of external party access in the case of an information security incident;\nj) legal and regulatory requirements and other contractual obligations relevant to the external party that should be taken into account;\nk) how the interests of any other stakeholders may be affected by the arrangements.\n\nAccess by external parties to the organization’s information should not be provided until the appropriate controls have been implemented and, where feasible, a contract has been signed defining the terms and conditions for the connection or access and the working arrangement. Generally, all security requirements resulting from work with external parties or internal controls should be reflected by the agreement with the external party (see also 6.2.2 and 6.2.3). It should be ensured that the external party is aware of their obligations, and accepts the responsibilities and liabilities involved in accessing, processing, communicating, or managing the organization’s information and information processing facilities.');
INSERT INTO `measureDescriptiontext` VALUES (388,205,1,'Addressing security when dealing with customers','All identified security requirements should be addressed before giving customers access to the organization’s information or assets.\n\nImplementation guidance\nThe following terms should be considered to address security prior to giving customers access to any of the organization’s assets (depending on the type and extent of access given, not all of them might apply):\na) asset protection, including:\n1) procedures to protect the organization’s assets, including information and software, and management of known vulnerabilities;\n2) procedures to determine whether any compromise of the assets, e.g. loss or modification of data, has occurred;\n3) integrity;\n4) restrictions on copying and disclosing information;\nb) description of the product or service to be provided;\nc) the different reasons, requirements, and benefits for customer access;\nd) access control policy, covering:\n1) permitted access methods, and the control and use of unique identifiers such as user IDs and passwords;\n2) an authorization process for user access and privileges;\n3) a statement that all access that is not explicitly authorised is forbidden;\n4) a process for revoking access rights or interrupting the connection between systems;\ne) arrangements for reporting, notification, and investigation of information inaccuracies (e.g. of personal details), information security incidents and security breaches;\nf) a description of each service to be made available;\ng) the target level of service and unacceptable levels of service;\nh) the right to monitor, and revoke, any activity related to the organization’s assets;\ni) the respective liabilities of the organization and the customer;\nj) responsibilities with respect to legal matters and how it is ensured that the legal requirements are met, e.g. data protection legislation, especially taking into account different national legal systems if the agreement involves co-operation with customers in other countries (see also 15.1);\nk) intellectual property rights (IPRs) and copyright assignment (see 15.1.2) and protection of any collaborative work (see also 6.1.5).');
INSERT INTO `measureDescriptiontext` VALUES (389,206,1,'Addressing security in third party agreements','Agreements with third parties involving accessing, processing, communicating or managing the organization’s information or information processing facilities, or adding products or services to information processing facilities should cover all relevant security requirements.\n\nImplementation guidance\nThe agreement should ensure that there is no misunderstanding between the organization and the third party. Organizations should satisfy themselves as to the indemnity of the third party. The following terms should be considered for inclusion in the agreement in order to satisfy the\nidentified security requirements (see 6.2.1): \na) the information security policy;\nb) controls to ensure asset protection, including:\n1) procedures to protect organizational assets, including information, software and hardware;\n2) any required physical protection controls and mechanisms;\n3) controls to ensure protection against malicious software (see 10.4.1);\n4) procedures to determine whether any compromise of the assets, e.g. loss or modification of information, software and hardware, has occurred;\n5) controls to ensure the return or destruction of information and assets at the end of, or at an agreed point in time during, the agreement;\n6) confidentiality, integrity, availability, and any other relevant property (see 2.1.5) of the assets;\n7) restrictions on copying and disclosing information, and using confidentiality agreements (see 6.1.5);\nc) user and administrator training in methods, procedures, and security;\nd) ensuring user awareness for information security responsibilities and issues;\ne) provision for the transfer of personnel, where appropriate;\nf) responsibilities regarding hardware and software installation and maintenance;\ng) a clear reporting structure and agreed reporting formats;\nh) a clear and specified process of change management;\ni) access control policy, covering:\n1) the different reasons, requirements, and benefits that make the access by the third party necessary;\n2) permitted access methods, and the control and use of unique identifiers such as user IDs and passwords;\n3) an authorization process for user access and privileges;\n4) a requirement to maintain a list of individuals authorized to use the services being made available, and what their rights and privileges are with respect to such use;\n5) a statement that all access that is not explicitly authorised is forbidden;\n6) a process for revoking access rights or interrupting the connection between systems;\nj) arrangements for reporting, notification, and investigation of information security incidents and security breaches, as well as violations of the requirements stated in the agreement;\nk) a description of the product or service to be provided, and a description of the information to be made available along with its security classification (see 7.2.1);\nl) the target level of service and unacceptable levels of service;\nm) the definition of verifiable performance criteria, their monitoring and reporting; \nn) the right to monitor, and revoke, any activity related to the organization’s assets;\no) the right to audit responsibilities defined in the agreement, to have those audits carried out by a third party, and to enumerate the statutory rights of auditors;\np) the establishment of an escalation process for problem resolution;\nq) service continuity requirements, including measures for availability and reliability, in accordance with an organization’s business priorities;\nr) the respective liabilities of the parties to the agreement;\ns) responsibilities with respect to legal matters and how it is ensured that the legal requirements are met, e.g. data protection legislation, especially taking into account  different national legal systems if the agreement involves co-operation with organizations in other countries (see also 15.1);\nt) intellectual property rights (IPRs) and copyright assignment (see 15.1.2) and protection of any collaborative work (see also 6.1.5);\nu) involvement of the third party with subcontractors, and the security controls these subcontractors need to implement;\nv) conditions for renegotiation/termination of agreements:\n1) a contingency plan should be in place in case either party wishes to terminate the relation before the end of the agreements;\n2) renegotiation of agreements if the security requirements of the organization change;\n3) current documentation of asset lists, licences, agreements or rights relating to them.');
INSERT INTO `measureDescriptiontext` VALUES (390,207,1,'Asset management','');
INSERT INTO `measureDescriptiontext` VALUES (391,208,1,'Responsibility for assets','');
INSERT INTO `measureDescriptiontext` VALUES (392,209,1,'Inventory of assets','All assets should be clearly identified and an inventory of all important assets drawn up and maintained.\n\nImplementation guidance\nAn organization should identify all assets and document the importance of these assets. The asset inventory should include all information necessary in order to recover from a disaster, including type of asset, format, location, backup information, license information, and a business value. The inventory should not duplicate other inventories unnecessarily, but it should be ensured that the content is aligned.\nIn addition, ownership (see 7.1.2) and information classification (see 7.2) should be agreed and documented for each of the assets. Based on the importance of the asset, its business value and its security classification, levels of protection commensurate with the importance of the assets should be identified (more information on how to value assets to represent their importance can be found in ISO/IEC TR 13335-3).');
INSERT INTO `measureDescriptiontext` VALUES (393,210,1,'Ownership of assets','All information and assets associated with information processing facilities should be ‘owned’ 3) by a designated part of the organization.\n\nImplementation guidance\nThe asset owner should  be responsible for:\na) ensuring that information and assets associated with information processing facilities are appropriately classified;\nb) defining and periodically reviewing access restrictions and classifications, taking into account applicable access control policies.\n\nOwnership may be allocated to:\na) a business process;\nb) a defined set of activities;\nc) an application; or\nd) a defined set of data.');
INSERT INTO `measureDescriptiontext` VALUES (394,211,1,'Acceptable use of assets','Rules for the acceptable use of information and assets associated with information processing facilities should be identified, documented, and implemented.\n\nImplementation guidance\nAll employees, contractors and third party users should follow rules for the acceptable use of information and assets associated with information processing facilities, including:\na) rules for electronic mail and Internet usages (see 10.8);\nb) guidelines for the use of mobile devices, especially for the use outside the premises of the organization (see 11.7.1);\nSpecific rules or guidance should be provided by the relevant management. Employees, contractors and third party users using or having access to the organization’s assets should be aware of the limits existing for their use of organization’s information and assets associated with information processing facilities, and resources. They should be responsible for their use of any information processing resources, and of any such use carried out under their responsibility.');
INSERT INTO `measureDescriptiontext` VALUES (395,212,1,'Information Classification','');
INSERT INTO `measureDescriptiontext` VALUES (396,213,1,'Classification guidelines','Information should be classified in terms of its value, legal requirements, sensitivity and criticality to the organization.\n\nImplementation guidance\nClassifications and associated protective controls for information should take account of business needs for sharing or restricting information and the business impacts associated with such needs. Classification guidelines should include conventions for initial classification and reclassification over\ntime; in accordance with some predetermined access control policy (see 11.1.1). It should be the responsibility of the asset owner (see 7.1.2) to define the classification of an asset, periodically review it, and ensure it is kept up to date and at the appropriate level. The classification should take account of the aggregation effect mentioned in 10.7.2. Consideration should be given to the number of classification categories and the benefits to be gained from their use. Overly complex schemes may become cumbersome and uneconomic to use or prove impractical. Care should be taken in interpreting classification labels on documents from other organizations, which may have different definitions for the same or similarly named labels.');
INSERT INTO `measureDescriptiontext` VALUES (397,214,1,'Information labelling and handling','An appropriate set of procedures for information labeling and handling should be developed and implemented in accordance with the classification scheme adopted by the organization.\n\nImplementation guidance\nProcedures for information labeling need to cover information assets in physical and electronic formats.\nOutput from systems containing information that is classified as being sensitive or critical should carry an appropriate classification label (in the output). The labeling should reflect the classification according to the rules established in 7.2.1. Items for consideration include printed reports, screen displays, recorded media (e.g. tapes, disks, CDs), electronic messages, and file transfers. For each classification level, handling procedures including the secure processing, storage, transmission, declassification, and destruction should be defined. This should also include the procedures for chain of custody and logging of any security relevant event. Agreements with other organizations that include information sharing should include  procedures to identify the classification of that information and to interpret the classification labels from other organizations.');
INSERT INTO `measureDescriptiontext` VALUES (398,215,1,'Human resources security','');
INSERT INTO `measureDescriptiontext` VALUES (399,216,1,'Prior to employment','');
INSERT INTO `measureDescriptiontext` VALUES (400,217,1,'Roles and responsibilities','Security roles and responsibilities of employees, contractors and third party users should be defined and documented in accordance with the organization’s information security policy.\n\nImplementation guidance\nSecurity roles and responsibilities should include the requirement to:\na) implement and act in accordance with the organization’s information security policies (see 5.1);\nb) protect assets from unauthorized access, disclosure, modification, destruction or interference;\nc) execute particular security processes or activities;\nd) ensure responsibility is assigned to the individual for actions taken;\ne) report security events or potential events or other security risks to the organization.\n\nSecurity roles and responsibilities should be defined and clearly communicated to job candidates during the pre-employment process.');
INSERT INTO `measureDescriptiontext` VALUES (401,218,1,'Screening','Background verification checks on all candidates for employment, contractors, and third party users should be carried out in accordance with relevant laws, regulations and ethics, and proportional to the business requirements, the classification of the information to be accessed, and the perceived risks.\n\nImplementation guidance\nVerification checks should take into account all relevant privacy, protection of personal data and/or employment based legislation, and should, where permitted, include the following: \na) availability of satisfactory character references, e.g. one business and one personal;\nb) a check (for completeness and accuracy) of the applicant’s curriculum vitae;\nc) confirmation of claimed academic and professional qualifications;\nd) independent identity check (passport or similar document);\ne) more detailed checks, such as credit checks or checks of criminal records.\n\nWhere a job, either on initial appointment or on promotion, involves the person having access to information processing facilities, and in particular if these are handling sensitive information, e.g. financial information or highly confidential information, the organization should also consider further,\nmore detailed checks. Procedures should define criteria and limitations for verification checks, e.g. who is eligible to screen people, and how, when and why verification checks are carried out. A screening process should also be carried out for contractors, and third party users. Where contractors are provided through an agency the contract with the agency should clearly specify the agency’s responsibilities for screening and the notification procedures they need to follow if screening has not been completed or if the results give cause for doubt or concern. In the same way, the agreement with the third party (see also 6.2.3) should clearly specify all responsibilities and notification procedures for screening. Information on all candidates  being considered for positions within the organization should be collected and handled in accordance with any appropriate legislation existing in the relevant jurisdiction. Depending on applicable legislation, the candidates should be informed beforehand about the screening activities. ');
INSERT INTO `measureDescriptiontext` VALUES (402,219,1,'Terms and conditions of employment','As part of their contractual obligation, employees, contractors and third party users should agree and sign the terms and conditions of their employment contract, which should state their and the organization’s responsibilities for information security.\n\nImplementation guidance\nThe terms and conditions of employment should reflect the organization’s security policy in addition to clarifying and stating:\na) that all employees, contractors and third party users who are given access to sensitive information should sign a confidentiality or non-disclosure agreement prior to being given access to information processing facilities;\nb) the employee’s, contractor’s and any other user’s legal responsibilities and rights, e.g. regarding copyright laws or data protection legislation (see also 15.1.1 and 15.1.2);\nc) responsibilities for the classification of information and management of organizational assets associated with information systems and services handled by the employee, contractor or third party user (see also 7.2.1 and 10.7.3);\nd) responsibilities of the employee, contractor or third party user for the handling of information received from other companies or external parties;\ne) responsibilities of the organization for the handling of personal information, including personal information created as a result of, or in the course of, employment with the organization (see also 15.1.4);\nf) responsibilities that are extended outside the organization’s premises and outside normal working hours, e.g. in the case of home-working (see also 9.2.5 and 11.7.1);\ng) actions to be taken if the employee, contractor or third party user disregards the organization’s security requirements (see also 8.2.3).\n\nThe organization should ensure that employees, contractors and third party users agree to terms and conditions concerning information security appropriate to the nature and extent of access they will have to the organization’s assets associated with information systems and services. Where appropriate, responsibilities contained within the terms and conditions of employment should continue for a defined period after the end of the employment (see also 8.3).');
INSERT INTO `measureDescriptiontext` VALUES (403,220,1,'During employment','');
INSERT INTO `measureDescriptiontext` VALUES (404,221,1,'Management responsibilities','Management should require employees, contractors and third party users to apply security in accordance with established policies and procedures of the organization.\n\nImplementation guidance\nManagement responsibilities should include ensuring that employees, contractors and third party users:\na) are properly briefed on their information security roles and responsibilities prior to being granted access to sensitive information or information systems;\nb) are provided with guidelines to state security expectations of their role within the organization;\nc) are motivated to fulfil the security policies of the organization;\nd) achieve a level of awareness on security relevant to their roles and responsibilities within the organization (see also 8.2.2);\ne) conform to the terms and conditions of employment, which includes the organization’s information security policy and appropriate methods of working;\nf) continue to have the appropriate skills and qualifications.');
INSERT INTO `measureDescriptiontext` VALUES (405,222,1,'Information security awareness, education and training','All employees of the organization and, where relevant, contractors and third party users should receive appropriate awareness training and regular updates in organizational policies and procedures, as relevant for their job function.\n\nImplementation guidance\nAwareness training should commence with a formal induction process designed to introduce the organization’s security policies and expectations before access to information or services is granted. Ongoing training should include security requirements, legal responsibilities and business controls, as well as training in the correct use of information processing facilities e.g. log-on procedure, use of software packages and information on the disciplinary process (see 8.2.3).');
INSERT INTO `measureDescriptiontext` VALUES (406,223,1,'Disciplinary process','There should be a formal disciplinary process for employees who have committed a security breach.\n\nImplementation guidance\nThe disciplinary process should not be commenced without prior verification that a security breach has occurred (see also 13.2.3 for collection of evidence).\nThe formal disciplinary process should ensure correct and fair treatment for employees who are suspected of committing breaches of security. The formal disciplinary process should provide for a graduated response that takes into consideration factors such as the nature and gravity of the breach\nand its impact on business, whether or not this is a first or repeat offence, whether or not the violator was properly trained, relevant legislation, business contracts and other factors as required. In serious cases of misconduct the process should allow for instant removal of duties, access rights and privileges, and for immediate escorting out of the site, if necessary.');
INSERT INTO `measureDescriptiontext` VALUES (407,224,1,'Termination or change of employment','');
INSERT INTO `measureDescriptiontext` VALUES (408,225,1,'Termination responsibilities','Responsibilities for performing employment termination or change of employment should be clearly defined and assigned.\n\nImplementation guidance\nThe communication of termination responsibilities should include ongoing security requirements and legal responsibilities and, where appropriate, responsibilities contained within any confidentiality agreement (see 6.1.5), and the terms and conditions of employment (see 8.1.3) continuing for a defined period after the end of the employee’s, contractor’s or third party user’s employment. Responsibilities and duties still valid after termination of employment should be contained in employee’s, contractor’s or third party user’s contracts. Changes of responsibility or employment should be managed as the termination of the respective responsibility or employment, and the new responsibility or employment should be controlled as described in clause 8.1.');
INSERT INTO `measureDescriptiontext` VALUES (409,226,1,'Return of assets','All employees, contractors and third party users should return all of the organization’s assets in their possession upon termination of their employment, contract or agreement.\n\nImplementation guidance\nThe termination process should be formalized to include the return of all previously issued software, corporate documents, and equipment. Other organizational assets such as mobile computing devices, credit cards, access cards, software, manuals, and information stored on electronic media also need to be returned. In cases where an employee, contractor or third party user purchases the organization’s equipment or uses their own personal equipment, procedures should be followed to ensure that all relevant information is transferred to the organization and securely erased from the equipment (see also 10.7.1).\nIn cases where an employee, contractor or third party user has knowledge that is important to ongoing operations, that information should be documented and transferred to the organization.');
INSERT INTO `measureDescriptiontext` VALUES (410,227,1,'Removal of access rights','The access rights of all employees, contractors and third party users to information and information processing facilities should be removed upon termination of their employment, contract or agreement, or adjusted upon change.\n\nImplementation guidance\nUpon termination, the access rights of an individual to assets associated with information systems and services should be reconsidered. This will determine whether it is necessary to remove access rights. Changes of an employment should be reflected in removal of all access rights that were not approved for the new employment. The access rights that should be removed or adapted include physical and logical access, keys, identification cards, information processing facilities (see also 11.2.4), subscriptions, and removal from any documentation that identifies them as a current  member of the organization. If a departing employee, contractor or third party user has known passwords for accounts remaining active, these should be changed upon termination or change of employment, contract or agreement. Access rights for information assets and information processing facilities should be reduced or removed before the employment terminates or changes, depending on the evaluation of risk factors such as:\na) whether the termination or change is initiated by the employee, contractor or third party user, or by management and the reason of termination;\nb) the current responsibilities of the employee, contractor or any other user;\nc) the value of the assets currently accessible.');
INSERT INTO `measureDescriptiontext` VALUES (411,228,1,'Physical and environmental security','');
INSERT INTO `measureDescriptiontext` VALUES (412,229,1,'Secure areas','');
INSERT INTO `measureDescriptiontext` VALUES (413,230,1,'Physical security perimeter','Security perimeters (barriers such as walls, card controlled entry gates or manned reception desks) should be used to protect areas that contain information and information processing facilities.\n\nImplementation guidance\nThe following guidelines should be considered and implemented where appropriate for physical security perimeters:\na) security perimeters should be clearly defined, and the siting and strength of each of the perimeters should depend on the security requirements of the assets within the perimeter and the results of a risk assessment;\nb) perimeters of a building or site containing information processing facilities should be physically sound (i.e. there should be no gaps in the perimeter or areas where a break-in could easily occur); the external walls of the site should be of solid construction and all external doors should be suitably protected against unauthorized access with control mechanisms, e.g. bars, alarms, locks etc; doors and windows should be locked when unattended and external protection should be considered for windows, particularly at ground level;\nc) a manned reception area or other means to control physical access to the site or building should be in place; access to sites and buildings should be restricted to authorized personnel only;\nd) physical barriers should, where applicable, be built to prevent unauthorized physical access and environmental contamination;\ne) all fire doors on a security perimeter should be alarmed, monitored, and tested in conjunction with the walls to establish the required level of resistance in accordance to suitable regional, national, and international standards; they should operate in accordance with local fire code in a failsafe manner;\nf) suitable intruder detection systems should be installed to national, regional or international standards and regularly tested to cover all external doors and accessible windows; unoccupied areas should be alarmed at all times; cover should also be provided for other areas, e.g. computer room or communications rooms;\ng) information processing facilities managed by the organization should be physically separated from those managed by third parties.');
INSERT INTO `measureDescriptiontext` VALUES (414,231,1,'Physical entry controls','Secure areas should be protected by appropriate entry controls to ensure that only authorized personnel are allowed access.\n\nImplementation guidance\nThe following guidelines should be considered:\na) the date and time of entry and departure of visitors should be recorded, and all visitors should be supervised unless their access has been previously approved; they should only be granted access for specific, authorized purposes and should be issued with instructions on the security requirements of the area and on emergency procedures.\nb) access to areas where sensitive information is processed or stored should be controlled and restricted to authorized persons only; authentication controls, e.g. access control card plus PIN, should be used to authorize and validate all access; an audit trail of all access should be securely maintained;\nc) all employees, contractors and third party users and all visitors should be required to wear some form of visible identification and should immediately notify security personnel if they encounter unescorted visitors and anyone not wearing visible identification;\nd) third party support service personnel should be granted restricted access to secure areas or sensitive information processing facilities only when required; this access should be authorized and monitored;\ne) access rights to secure areas should be regularly reviewed and updated, and revoked when necessary (see 8.3.3).');
INSERT INTO `measureDescriptiontext` VALUES (415,232,1,'Securing offices, rooms and facilities','Physical security for offices, rooms, and facilities should be designed and applied.\n\nImplementation guidance\nThe following guidelines should be considered to secure offices, rooms, and facilities:\na) account should be taken of relevant health and safety regulations and standards;\nb) key facilities should be sited to avoid access by the public;\nc) where applicable, buildings should be unobtrusive and give minimum indication of their purpose, with no obvious signs, outside or inside the building identifying the presence of information processing activities;\nd) directories and internal telephone books identifying locations of sensitive information processing facilities should not be readily accessible by the public.');
INSERT INTO `measureDescriptiontext` VALUES (416,233,1,'Protecting against external and environmental threats','Physical protection against damage from fire, flood, earthquake, explosion, civil unrest, and other forms of natural or man-made disaster should be designed and applied.\n\nImplementation guidance\nConsideration should be given to any security threats presented by neighboring premises, e.g. a fire in a neighbouring building, water leaking from the roof or in floors below ground level or an explosion in the street.\nThe following guidelines should be considered to avoid damage from fire, flood, earthquake, explosion, civil unrest, and other forms of natural or man-made disaster:\na) hazardous or combustible materials should be stored at a safe distance from a secure area. Bulk supplies such as stationery should not be stored within a secure area;\nb) fallback equipment and back-up media should be sited at a safe distance to avoid damage from a disaster affecting the main site;\nc) appropriate fire fighting equipment should be provided and suitably placed.');
INSERT INTO `measureDescriptiontext` VALUES (417,234,1,'Working in secure areas','Physical protection and guidelines for working in secure areas should be designed and applied.\n\nImplementation guidance\nThe following guidelines should be considered:\na) personnel should only be aware of the existence of, or activities within, a secure area on a need to know basis;\nb) unsupervised working in secure areas should be avoided both for safety reasons and to prevent opportunities for malicious activities;\nc) vacant secure areas should be physically locked and periodically checked;\nd) photographic, video, audio or other recording equipment, such as cameras in mobile devices, should not be allowed, unless authorized;\nThe arrangements for working in secure areas include controls for the employees, contractors and third party users working in the secure area, as well as other third party activities taking place there.');
INSERT INTO `measureDescriptiontext` VALUES (418,235,1,'Public access, delivery and loading areas','Access points such as delivery and loading areas and other points where unauthorized persons may enter the premises should be controlled and, if possible, isolated from information processing facilities to avoid unauthorized access.\n\nImplementation guidance\nThe following guidelines should be considered:\na) access to a delivery and loading area from outside of the building should be restricted to identified and authorized personnel;\nb) the delivery and loading area should be designed so that supplies can be unloaded without delivery personnel gaining access to other parts of the building;\nc) the external doors of a delivery and loading area should be secured when the internal doors are opened;\nd) incoming material should be inspected for potential threats (see 9.2.1d)) before this material is moved from the delivery and loading area to the point of use;\ne) incoming material should be registered in accordance with asset management procedures (see also 7.1.1) on entry to the site;\nf) incoming and outgoing shipments should be physically segregated, where possible.');
INSERT INTO `measureDescriptiontext` VALUES (419,236,1,'Equipment security','');
INSERT INTO `measureDescriptiontext` VALUES (420,237,1,'Equipment siting and protection','Equipment should be sited or protected to reduce the risks from environmental threats and hazards, and opportunities for unauthorized access.\n\nImplementation guidance\nThe following guidelines should be considered to protect equipment:\na) equipment should be sited to minimize unnecessary access into work areas;\nb) information processing facilities handling sensitive data should be positioned and the viewing angle restricted to reduce the risk of information being viewed by unauthorized persons during their use, and storage facilities secured to avoid unauthorized access;\nc) items requiring special protection should be isolated to reduce the general level of protection required;\nd) controls should be adopted to minimize the risk of potential physical threats, e.g. theft, fire, explosives, smoke, water (or water supply failure), dust, vibration, chemical effects, electrical supply interference, communications interference, electromagnetic radiation, and vandalism;\ne) guidelines for eating, drinking, and smoking in proximity to information processing facilities should be established;\nf) environmental conditions, such as temperature and humidity, should be monitored for conditions, which could adversely affect the operation of information processing facilities;\ng) lightning protection should be applied to all buildings and lightning protection filters should be fitted to all incoming power and communications lines;\nh) the use of special protection methods, such as keyboard membranes, should be considered for equipment in industrial environments;\ni) equipment processing sensitive information should be protected to minimize the risk of information leakage due to emanation.');
INSERT INTO `measureDescriptiontext` VALUES (421,238,1,'Supporting utilities','Equipment should be protected from power failures and other disruptions caused by failures in supporting utilities.\n\nImplementation guidance\nAll supporting utilities, such as electricity, water supply, sewage, heating/ventilation, and air conditioning should be adequate for the systems they are supporting. Support utilities should be regularly inspected and as appropriate tested to ensure their proper functioning and to reduce any risk from their malfunction or failure. A suitable electrical supply should be provided that conforms to the equipment manufacturer’s specifications.\nAn uninterruptible power supply (UPS) to support orderly close down or continuous running is recommended for equipment supporting critical business operations. Power contingency plans should cover the action to be taken on failure of the UPS. A back-up generator should be considered if\nprocessing is required to continue in case of a prolonged power failure. An adequate supply of fuel should be available to ensure that the generator can perform for a prolonged period. UPS equipment and generators should be regularly checked to ensure it has adequate capacity and is tested in accordance with the manufacturer’s recommendations. In addition, consideration could be given to using multiple power sources or, if the site is large, a separate power substation. Emergency power off switches should be located near emergency exits in equipment rooms to facilitate rapid power down in case of an emergency. Emergency lighting should be provided in case of main power failure. The water supply should be stable and adequate to supply air conditioning, humidification equipment and fire suppression systems (where used). Malfunctions in the water supply system  any damage equipment or prevent fire suppression from acting effectively. An alarm system to detect malfunctions in the supporting utilities should be  evaluated and installed if required.\nTelecommunications equipment should be connected to the utility provider by at least two diverse routes to prevent failure in one connection path removing voice services. Voice services should be adequate to meet local legal requirements for emergency communications.');
INSERT INTO `measureDescriptiontext` VALUES (422,239,1,'Cabling security','Power and telecommunications cabling carrying data or supporting information services should be protected from interception or damage.\n\nImplementation guidance\nThe following guidelines for cabling security should be considered:\na) power and telecommunications lines into information processing facilities should be underground, where possible, or subject to adequate alternative protection;\nb) network cabling should be protected from unauthorized interception or damage, for example by using a conduit or by avoiding routes through public areas;\nc) power cables should be segregated from communications cables to prevent interference;\nd) clearly identifiable cable and equipment markings should be used to minimise handling errors, such as accidental patching of wrong network cables;\ne) a documented patch list should be used to reduce the possibility of errors;\nf) for sensitive or critical systems further controls to consider include:\n1) installation of armoured conduit and locked rooms or boxes at inspection and termination points;\n2) use of alternative routings and/or transmission media providing appropriate security;\n3) use of fibre optic cabling;\n4) use of electromagnetic shielding to protect the cables;\n5) initiation of technical sweeps and physical inspections for unauthorized devices being attached to the cables;\n6) controlled access to patch panels and cable rooms;');
INSERT INTO `measureDescriptiontext` VALUES (423,240,1,'Equipment maintenance','Equipment should be correctly maintained to ensure its continued availability and integrity.\n\nImplementation guidance\nThe following guidelines for equipment maintenance should be considered:\na) equipment should be maintained in accordance with the supplier’s recommended service intervals and specifications;\nb) only authorized maintenance personnel should carry out repairs and service equipment;\nc) records should be kept of all suspected or actual faults, and all preventive and corrective maintenance;\nd) appropriate controls should be implemented when equipment is scheduled for maintenance, taking into account whether this maintenance is performed by personnel on site or external to the organization; where necessary, sensitive information should be cleared from the equipment, or the maintenance personnel should be sufficiently cleared;\ne) all requirements imposed by insurance policies should be complied with.');
INSERT INTO `measureDescriptiontext` VALUES (424,241,1,'Security of equipment offpremises','Security should be applied to off-site equipment taking into account the different risks of working outside the organization’s premises.\n\nImplementation guidance\nRegardless of ownership, the use of any information processing equipment outside the organization’s premises should be authorized by management. The following guidelines should be considered for the protection of off-site equipment:\na) equipment and media taken off the premises should not be left unattended in public places; portable computers should be carried as hand luggage and disguised where possible when travelling;\nb) manufacturers’ instructions for protecting equipment should be observed at all times, e.g. protection against exposure to strong electromagnetic fields;\nc) home-working controls should be determined by a risk assessment and suitable controls applied as appropriate, e.g. lockable filing cabinets, clear desk policy, access controls for computers and secure communication with the office (see also ISO/IEC 18028 Network Security);\nd) adequate insurance cover should be in place to protect equipment off-site. Security risks, e.g. of damage, theft or eavesdropping, may vary considerably between locations and should be taken into account in determining the most appropriate controls.');
INSERT INTO `measureDescriptiontext` VALUES (425,242,1,'Secure disposal or re-use of equipment','All items of equipment containing storage media should be checked to ensure that any sensitive data and licensed software has been removed or securely overwritten prior to disposal.\n\nImplementation guidance\nDevices containing sensitive information should be physically destroyed or the information should be destroyed, deleted or overwritten using techniques to make the original information non-retrievable rather than using the standard delete or format function.');
INSERT INTO `measureDescriptiontext` VALUES (426,243,1,'Removal of property','Equipment, information or software should not be taken off-site without prior authorization.\n\nImplementation guidance\nThe following guidelines should be considered:\na) equipment, information or software should not be taken off-site without prior authorization;\nb) employees, contractors and third party users who have authority to permit off-site removal of assets should be clearly identified;\nc) time limits for equipment removal should be set and returns checked for compliance;\nd) where necessary and appropriate, equipment should be recorded as being removed off-site and recorded when returned.');
INSERT INTO `measureDescriptiontext` VALUES (427,244,1,'Communications and operations management','');
INSERT INTO `measureDescriptiontext` VALUES (428,245,1,'Operational procedures and responsibilities','');
INSERT INTO `measureDescriptiontext` VALUES (429,246,1,'Documented operating procedures','Operating procedures should be documented, maintained, and made available to all users who need them.\n\nImplementation guidance\nDocumented procedures should be prepared for system activities associated with information processing and communication facilities, such as computer start-up and close-down procedures, backup, equipment maintenance, media handling, computer room and mail handling management, and safety.\nThe operating procedures should specify the instructions for the detailed execution of each job including:\na) processing and handling of information;\nb) backup (see 10.5);\nc) scheduling requirements, including interdependencies with other systems, earliest job start and latest job completion times;\nd) instructions for handling errors or other exceptional conditions, which might arise during job execution, including restrictions on the use of system utilities (see 11.5.4);\ne) support contacts in the event of unexpected operational or technical difficulties; f) special output and media handling instructions, such as the use of special stationery or the management of confidential output including procedures for secure disposal of output from failed jobs (see 10.7.2 and 10.7.3);\ng) system restart and recovery procedures for use in the event of system failure;\nh) the management of audit-trail and system log information (see 10.10).\nOperating procedures, and the documented procedures for system activities, should be treated as formal documents and changes authorized by management. Where technically feasible, information systems should be managed consistently, using the same procedures, tools, and utilities.');
INSERT INTO `measureDescriptiontext` VALUES (430,247,1,'Change management','Changes to information processing facilities and systems should be controlled.\n\nImplementation guidance\nOperational systems and application software should be subject to strict change management control. In particular, the following items should be considered:\na) identification and recording of significant changes;\nb) planning and testing of changes;\nc) assessment of the potential impacts, including security impacts, of such changes;\nd) formal approval procedure for proposed changes;\ne) communication of change details to all relevant persons;\nf) fallback procedures, including procedures and responsibilities for aborting and recovering from unsuccessful changes and unforeseen events.\nFormal management responsibilities and procedures should be in place to ensure satisfactory control of all changes to equipment, software or procedures. When changes are made, an audit log containing all relevant information should be retained.');
INSERT INTO `measureDescriptiontext` VALUES (431,248,1,'Segregation of duties','Duties and areas of responsibility should be segregated to reduce opportunities for unauthorized or unintentional modification or misuse of the organization’s assets.\n\nImplementation guidance\nSegregation of duties is a method for reducing the risk of accidental or deliberate system misuse. Care should be taken that no single person can access, modify or use assets without authorization or detection. The initiation of an event should be separated from its authorization. The possibility of collusion should be considered in designing the controls. Small organizations may find segregation of duties difficult to achieve, but the principle should be applied as far as is possible and practicable. Whenever it is difficult to segregate, other controls such as monitoring of activities, audit trails and management supervision should be considered. It is important that security audit remains independent.');
INSERT INTO `measureDescriptiontext` VALUES (432,249,1,'Separation of development, test and operational facilities','Development, test and operational facilities should be separated to reduce the risks of unauthorised access or changes to the operational system.\n\nImplementation guidance\nThe level of separation between operational, test, and development environments that is necessary to prevent operational problems should be identified and appropriate controls implemented. The following items should be considered:\na) rules for the transfer of software from development to operational status should be defined and documented;\nb) development and operational software should run on different systems or computer processors and in different domains or directories;\nc) compilers, editors, and other development tools or system utilities should not be accessible from operational systems when not required;\nd) the test system environment should emulate the operational system environment as closely as possible;\ne) users should use different user profiles for operational and test systems, and menus should display appropriate identification messages to reduce the risk of error;\nf) sensitive data should not be copied into the test system environment (see 12.4.2).');
INSERT INTO `measureDescriptiontext` VALUES (433,250,1,'Third party service delivery management','');
INSERT INTO `measureDescriptiontext` VALUES (434,251,1,'Service delivery','It should be ensured that the security controls, service definitions and delivery levels included in the third party service delivery agreement are implemented, operated, and maintained by the third party.\n\nImplementation guidance\nService delivery by a third party should include the agreed security arrangements, service definitions, and aspects of service management. In case of outsourcing arrangements, the organization should plan the necessary transitions (of information, information processing facilities, and anything else that needs to be moved), and should ensure that security is maintained throughout the transition period. The organization should ensure that the third party maintains sufficient service capability together with workable plans designed to ensure that agreed service continuity levels are maintained  following major service failures or disaster (see 14.1).');
INSERT INTO `measureDescriptiontext` VALUES (435,252,1,'Monitoring and review of third party services','The services, reports and records provided by the third party should be regularly monitored and reviewed, and audits should be carried out regularly.\n\nImplementation guidance\nMonitoring and review of third party services should ensure that the information security terms and conditions of the agreements are being adhered to, and that information security incidents and problems are managed properly. This should involve a service management relationship and process between the organization and the third party to:\na) monitor service performance levels to check adherence to the agreements;\nb) review service reports produced by the third party and arrange regular progress meetings as required by the agreements;\nc) provide information about information security incidents and review of this information by the third party and the organization as required by the agreements and any supporting guidelines and procedures;\nd) review third party audit trails and records of security events, operational problems, failures, tracing of faults and disruptions related to the service delivered;\ne) resolve and manage any identified problems.\n\nThe responsibility for managing the relationship with a third party should be assigned to a designated individual or service management team. In addition, the organization should ensure that the third party assigns responsibilities for checking for compliance and enforcing the requirements of the\nagreements. Sufficient technical skills and resources should be made available to monitor that requirements of the agreement (see 6.2.3), in particular the information security requirements, are being met. Appropriate action should be taken when deficiencies in the service delivery are observed. The organization should maintain sufficient overall control and visibility into all security aspects for sensitive or critical information or information processing facilities accessed, processed or managed by a third party. The organization should ensure they retain visibility into security activities such as change management, identification of vulnerabilities, and information security incident reporting/response through a clearly defined reporting process, format and structure.');
INSERT INTO `measureDescriptiontext` VALUES (436,253,1,'Managing changes to third party services','Changes to the provision of services, including maintaining and improving existing information security policies, procedures and controls, should be managed, taking account of the criticality of business systems and processes involved and re-assessment of risks.\n\nImplementation guidance\nThe process of managing changes to a third party service needs to take account of:\na) changes made by the organization to implement:\n1) enhancements to the current services offered;\n2) development of any new applications and systems;\n3) modifications or updates of the organization’s policies and procedures;\n4) new controls to resolve information security incidents and to improve security;\nb) changes in third party services to implement:\n1) changes and enhancement to networks;\n2) use of new technologies;\n3) adoption of new products or newer versions/releases;\n4) new development tools and environments;\n5) changes to physical location of service facilities;\n6) change of vendors.');
INSERT INTO `measureDescriptiontext` VALUES (437,254,1,'System planning and acceptance','');
INSERT INTO `measureDescriptiontext` VALUES (438,255,1,'Capacity management','The use of resources should be monitored, tuned, and projections made of future capacity requirements to ensure the required system performance.\n\nImplementation guidance\nFor each new and ongoing activity, capacity requirements should be identified. System tuning and monitoring should be applied to ensure and, where necessary, improve the availability and efficiency of systems. Detective controls should be put in place to indicate problems in due time. Projections of future capacity requirements should take account of new business and system requirements and current and projected trends in the organization''s information processing capabilities. Particular attention needs to be paid to any resources with long procurement lead times or high costs; therefore managers should monitor the utilization of key system resources. They should identify trends in usage, particularly in relation to business  pplications or management information system tools. Managers should use this information to identify and avoid potential bottlenecks and dependence on key personnel that might present a threat to system security or services, and plan appropriate action.');
INSERT INTO `measureDescriptiontext` VALUES (439,256,1,'System acceptance','Acceptance criteria for new information systems, upgrades, and new versions should be established and suitable tests of the system(s) carried out during development and prior to acceptance.\n\nImplementation guidance\nManagers should ensure that the requirements and criteria for acceptance of new systems are clearly defined, agreed, documented, and tested. New information systems, upgrades, and new versions should only be migrated into production after obtaining formal acceptance. The following items should be considered prior to formal acceptance being provided:\na) performance and computer capacity requirements;\nb) error recovery and restart procedures, and contingency plans;\nc) preparation and testing of routine operating procedures to defined standards;\nd) agreed set of security controls in place;\ne) effective manual procedures;\nf) business continuity arrangements (see 14.1);\ng) evidence that installation of the new system will not adversely affect existing systems, particularly at peak processing times, such as month end;\nh) evidence that consideration has been given to the effect the new system has on the overall security of the organization;\ni) training in the operation or use of new systems;\nj) ease of use, as this affects user performance and avoids human error. \n\nFor major new developments, the operations function and users should be consulted at all stages in the development process to ensure the operational efficiency of the proposed system design. Appropriate tests should be carried out to confirm that all acceptance criteria have been fully satisfied.');
INSERT INTO `measureDescriptiontext` VALUES (440,257,1,'Protection against malicious and mobile code','');
INSERT INTO `measureDescriptiontext` VALUES (441,258,1,'Controls against malicious code','Detection, prevention, and recovery controls to protect against malicious code and appropriate user awareness procedures should be implemented.\n\nImplementation guidance\nProtection against malicious code should be based on malicious code detection and repair software, security awareness, and appropriate system access and change management controls. The following guidance should be considered:\na) establishing a formal policy prohibiting the use of unauthorized software (see 15.1.2);\nb) establishing a formal policy to protect against risks associated with obtaining files and software either from or via external networks, or on any other medium, indicating what protective measures should be taken;\nc) conducting regular reviews of the software and data content of systems supporting critical business processes; the presence of any unapproved files or unauthorized amendments should be formally investigated;\nd) installation and regular update of malicious code detection and repair software to scan computers and media as a precautionary control, or on a routine basis; the checks carried out should include:\n1) checking any files on electronic or optical media, and files received over networks, for malicious code before use;\n2) checking electronic mail attachments and downloads for malicious code before use; this check should be carried out at different places, e.g. at electronic mail servers, desk top computers and when entering the network of the organization;\n3) checking web pages for malicious code;\ne) defining management procedures and responsibilities to deal with malicious code protection on systems, training in their use, reporting and recovering from malicious code attacks (see 13.1 and 13.2);\nf) preparing appropriate business continuity plans for recovering from malicious code attacks, including all necessary data and software back-up and recovery arrangements (see clause 14);\ng) implementing procedures to regularly collect information, such as subscribing to mailing lists and/or checking web sites giving information about new malicious code;\nh) implementing procedures to verify information relating to malicious code, and ensure that warning bulletins are accurate and informative; managers should ensure that qualified sources, e.g. reputable journals, reliable Internet sites or suppliers producing software protecting against malicious code, are used to differentiate between hoaxes and real malicious code; all users should be made aware of the problem of hoaxes and what to do on receipt of them. ');
INSERT INTO `measureDescriptiontext` VALUES (442,259,1,'Controls against mobile code','Where the use of mobile code is authorized, the configuration should ensure that the authorized mobile code operates according to a clearly defined security policy, and unauthorized mobile code should be prevented from executing.\n\nImplementation guidance\nThe following actions should be considered to protect against mobile code performing unauthorized actions:\na) executing mobile code in a logically isolated environment;\nb) blocking any use of mobile code;\nc) blocking receipt of mobile code;\nd) activating technical measures as available on a specific system to ensure mobile code is managed;\ne) control the resources available to mobile code access;\nf) cryptographic controls to uniquely authenticate mobile code.');
INSERT INTO `measureDescriptiontext` VALUES (443,260,1,'Back-up','');
INSERT INTO `measureDescriptiontext` VALUES (444,261,1,'Information back-up','Back-up copies of information and software should be taken and tested regularly in accordance with the agreed backup policy.\n\nImplementation guidance\nAdequate back-up facilities should be provided to ensure that all essential information and software can be recovered following a disaster or media failure. The following items for information back up should be considered:\na) the necessary level of back-up information should be defined;\nb) accurate and complete records of the back-up copies and documented restoration procedures should be produced;\nc) the extent (e.g. full or differential backup) and frequency of backups should reflect the business requirements of the organization, the security requirements of the information involved, and the criticality of the information to the continued operation of the organization;\nd) the back-ups should be stored in a remote location, at a sufficient distance to escape any damage from a disaster at the main site;\ne) back-up information should be given an appropriate level of physical and environmental protection (see clause 9) consistent with the standards applied at the main site; the controls applied to media at the main site should be extended to cover the back-up site;\nf) back-up media should be regularly tested to ensure that they can be relied upon for emergency use when necessary;\ng) restoration procedures should be regularly checked and tested to ensure that they are effective and that they can be completed within the time allotted in the operational procedures for recovery;\nh) in situations where confidentiality is of importance, back-ups should be protected by means of encryption.\n\nBack-up arrangements for individual systems should be regularly tested to ensure that they meet the requirements of business continuity plans (see clause 14). For critical systems, the backup arrangements should cover all systems information, applications, and data necessary to recover the complete system in the event of a disaster.\nThe retention period for essential business information, and also any requirement for archive copies to be permanently retained should be determined (see 15.1.3).');
INSERT INTO `measureDescriptiontext` VALUES (445,262,1,'Network security management','');
INSERT INTO `measureDescriptiontext` VALUES (446,263,1,'Network controls','Networks should be adequately managed and controlled, in order to be protected from threats, and to maintain security for the systems and applications using the network, including information in transit.\n\nImplementation guidance\nNetwork managers should implement controls to ensure the security of information in networks, and the protection of connected services from unauthorized access. In particular, the following items should be considered:\na) operational responsibility for networks should be separated from computer operations where appropriate (see 10.1.3);\nb) responsibilities and procedures for the management of remote equipment, including equipment in user areas, should be established;\nc) special controls should be established to safeguard the confidentiality and integrity if data passing over public networks or over wireless networks, and to protect the connected systems and applications (see 11.4 and 12.3); special controls may also be required to maintain the availability of the network services and computers connected;\nd) appropriate logging and monitoring should be applied to enable recording of security relevant actions;\ne) management activities should be closely co-ordinated both to optimize the service to the organization and to ensure that controls are consistently applied across the information processing infrastructure.');
INSERT INTO `measureDescriptiontext` VALUES (447,264,1,'Security of network services','Security features, service levels, and management requirements of all network services should be identified and included in any network services agreement, whether these services are provided in-house or outsourced.\n\nImplementation guidance \nThe ability of the network service provider to manage agreed services in a secure way should be determined and regularly monitored, and the right to audit should be agreed.\nThe security arrangements necessary for particular services, such as security features, service levels, and management requirements, should be identified. The organization should ensure that network service providers implement these measures.');
INSERT INTO `measureDescriptiontext` VALUES (448,265,1,'Media handling','');
INSERT INTO `measureDescriptiontext` VALUES (449,266,1,'Management of removable media','There should be procedures in place for the management of removable media\n\nImplementation guidance\nThe following guidelines for the management of removable media should be considered:\na) if no longer required, the contents of any re-usable media that are to be removed from the organization should be made unrecoverable;\nb) where necessary and practical, authorization should be required for media removed from the organization and a record of such removals should be kept in order to maintain an audit trail;\nc) all media should be stored in a safe, secure environment, in accordance with manufacturers’ specifications;\nd) information stored on media that needs to be available longer than the media lifetime (in accordance with manufacturers’ specifications) should be also stored elsewhere to avoid information loss due to media deterioration;\ne) registration of removable media should be considered to limit the opportunity for data loss;\nf) removable media drives should only be enabled if there is a business reason for doing so.\n\nAll procedures and authorization levels should be clearly documented.');
INSERT INTO `measureDescriptiontext` VALUES (450,267,1,'Disposal of media','Media should be disposed of securely and safely when no longer required, using formal procedures.\n\nImplementation guidance\nFormal procedures for the secure disposal of media should minimize the risk of sensitive information leakage to unauthorised persons. The procedures for secure disposal of media containing sensitive information should be commensurate with the sensitivity of that information. The following items should be considered:\na) media containing sensitive information should be stored and disposed of securely and safely, e.g. by incineration or shredding, or erased of data for use by another application within the organization;\nb) procedures should be in place to identify the items that might require secure disposal;\nc) it may be easier to arrange for all media items to be collected and disposed of securely, rather than attempting to separate out the sensitive items;\nd) many organizations offer collection and disposal services for papers, equipment and media; care should be taken in selecting a suitable contractor with adequate controls and experience;\ne) disposal of sensitive items should be logged where possible in order to maintain an audit trail.\nWhen accumulating media for disposal, consideration should be given to the aggregation effect, which may cause a large quantity of non-sensitive information to become sensitive.');
INSERT INTO `measureDescriptiontext` VALUES (451,268,1,'Information handling procedures','Procedures for the handling and storage of information should be established to protect this information from unauthorized disclosure or misuse.\n\nImplementation guidance\nProcedures should be drawn up for handling, processing, storing, and communicating information consistent with its classification (see 7.2). The following items should be considered:\na) handling and labelling of all media to its indicated classification level;\nb) access restrictions to prevent access from unauthorized personnel;\nc) maintenance of a formal record of the authorized recipients of data;\nd) ensuring that input data is complete, that processing is properly completed and that output validation is applied;\ne) protection of spooled data awaiting output to a level consistent with its sensitivity;\nf) storage of media in accordance with manufacturers’ specifications;\ng) keeping the distribution of data to a minimum;\nh) clear marking of all copies of media for the attention of the authorized recipient;\ni) review of distribution lists and lists of authorized recipients at regular intervals.');
INSERT INTO `measureDescriptiontext` VALUES (452,269,1,'Security of system documentation','System documentation should be protected against unauthorized access.\n\nImplementation guidance\nTo secure system documentation, the following items should be considered:\na) system documentation should be stored securely;\nb) the access list for system documentation should be kept to a minimum and authorized by the application owner;\nc) system documentation held on a public network, or supplied via a public network, should be appropriately protected.');
INSERT INTO `measureDescriptiontext` VALUES (453,270,1,'Exchange of information','');
INSERT INTO `measureDescriptiontext` VALUES (454,271,1,'Information exchange policies and procedures','Formal exchange policies, procedures, and controls should be in place to protect the exchange of information through the use of all types of communication facilities.\n\nImplementation guidance\nThe procedures and controls to be followed when using electronic communication facilities for information exchange should consider the following items:\na) procedures designed to protect exchanged information from interception, copying, modification, mis-routing, and destruction;\nb) procedures for the detection of and protection against malicious code that may be transmitted through the use of electronic communications (see Clause 10.4.1);\nc) procedures for protecting communicated sensitive electronic information that is in the form of an attachment;\nd) policy or guidelines outlining acceptable use of electronic communication facilities (see 7.1.3);\ne) procedures for the use of wireless communications, taking into account the particular risks involved;\nf) employee, contractor and any other user’s responsibilities not to compromise the organization, e.g. through defamation, harassment, impersonation, forwarding of chain letters, unauthorized purchasing, etc.;\ng) use of cryptographic techniques e.g. to protect the confidentiality, integrity and authenticity of information (see Clause 12.3);\nh) retention and disposal guidelines for all business correspondence, including messages, in accordance with relevant national and local legislation and regulations;\ni) not leaving sensitive or critical information on printing facilities, e.g. copiers, printers, and facsimile machines, as these may be accessed by unauthorized personnel;\nj) controls and restrictions associated with the forwarding of communication facilities, e.g. automatic forwarding of electronic mail to external mail addresses;\nk) reminding personnel that they should take appropriate precautions, e.g. not to reveal sensitive information to avoid being overheard or intercepted when making a phone call by:\n1) people in their immediate vicinity particularly when using mobile phones;\n2) wiretapping, and other forms of eavesdropping through physical access to the phone handset or the phone line, or using scanning receivers;\n3) people at the recipient’s end;\nl) not leaving messages containing sensitive information on answering machines since these may be replayed by unauthorized persons, stored on communal systems or stored incorrectly as a result of misdialling;\nm) reminding personnel about the problems of using facsimile machines, namely:\n1) unauthorized access to built-in message stores to retrieve messages;\n2) deliberate or accidental programming of machines to send messages to specific numbers;\n3) sending documents and messages to the wrong number either by misdialling or using the wrong stored number;\nn) reminding personnel not to register demographic data, such as the e-mail address or other personal information, in any software to avoid collection for unauthorized use;\no) reminding personnel that modern facsimile machines and photocopiers have page caches and store pages in case of a paper or transmission fault, which will be printed once the fault is cleared. In addition, personnel should be reminded that they should not have confidential conversations in public places or open offices and meeting places with non-sound proofed-walls. Information exchange facilities should comply with any relevant legal requirements (see clause 15).');
INSERT INTO `measureDescriptiontext` VALUES (455,272,1,'Exchange agreements','Agreements should be established for the exchange of information and software between the organization and external parties.\n\nImplementation guidance\nExchange agreements should consider the following security conditions:\na) management responsibilities for controlling and notifying transmission, dispatch, and receipt;\nb) procedures for notifying sender of transmission, dispatch, and receipt;\nc) procedures to ensure traceability and non-repudiation;\nd) minimum technical standards for packaging and transmission;\ne) escrow agreements;\nf) courier identification standards;\ng) responsibilities and liabilities in the event of information security incidents, such as loss of data;\nh) use of an agreed labelling system for sensitive or critical information, ensuring that the meaning of the labels is immediately understood and that the information is appropriately protected;\ni) ownership and responsibilities for data protection, copyright, software license compliance and similar considerations (see 15.1.2 and 15.1.4);\nj) technical standards for recording and reading information and software;\nk) any special controls that may be required to protect sensitive items, such as cryptographic keys (see 12.3).\n\nPolicies, procedures, and standards should be established and maintained to protect information and physical media in transit (see also 10.8.3), and should be referenced in such exchange agreements. The security content of any agreement should reflect the sensitivity of the business information\ninvolved.');
INSERT INTO `measureDescriptiontext` VALUES (456,273,1,'Physical media in transit ','Media containing information should be protected against unauthorized access, misuse or corruption during transportation beyond an organization’s physical boundaries.\n\nImplementation guidance\nThe following guidelines should be considered to protect information media being transported between sites:\na) reliable transport or couriers should be used;\nb) a list of authorized couriers should be agreed with management;\nc) procedures to check the identification of couriers should be developed;\nd) packaging should be sufficient to protect the contents from any physical damage likely to arise during transit and in accordance with any manufacturers’ specifications (e.g. for software), for example protecting against any environmental factors that may reduce the media’s restoration effectiveness such as exposure to heat, moisture or electromagnetic fields;\ne) controls should be adopted, where necessary, to protect sensitive information from unauthorized disclosure or modification; examples include:\n1) use of locked containers;\n2) delivery by hand;\n3) tamper-evident packaging (which reveals any attempt to gain access);\n4) in exceptional cases, splitting of the consignment into more than one delivery and dispatch by different routes.');
INSERT INTO `measureDescriptiontext` VALUES (457,274,1,'Electronic messaging','Information involved in electronic messaging should be appropriately protected.\n\nImplementation guidance\nSecurity considerations for electronic messaging should include the following:\na) protecting messages from unauthorized access, modification or denial of service;\nb) ensuring correct addressing and transportation of the message;\nc) general reliability and availability of the service;\nd) legal considerations, for example requirements for electronic signatures;\ne) obtaining approval prior to using external public services such as instant messaging or file sharing;\nf) stronger levels of authentication controlling access from publicly accessible networks.');
INSERT INTO `measureDescriptiontext` VALUES (458,275,1,'Business information systems','Policies and procedures should be developed and implemented to protect information associated with the interconnection of business information systems.\n\nImplementation guidance\nConsideration given to the security and business implications of interconnecting such facilities should include:\na) known vulnerabilities in the administrative and accounting systems where information is shared between different parts of the organization;\nb) vulnerabilities of information in business communication systems, e.g. recording phone calls or conference calls, confidentiality of calls, storage of facsimiles, opening mail, distribution of mail;\nc) policy and appropriate controls to manage information sharing;\nd) excluding categories of sensitive business information and classified documents if the system does not provide an appropriate level of protection (see 7.2);\ne) restricting access to diary information relating to selected individuals, e.g. personnel working on sensitive projects;\nf) categories of personnel, contractors or business partners allowed to use the system and the locations from which it may be accessed (see 6.2 and 6.3);\ng) restricting selected facilities to specific categories of user;\nh) identifying the status of users, e.g. employees of the organization or contractors in directories for the benefit of other users;\ni) retention and back-up of information held on the system (see 10.5.1);\nj) fallback requirements and arrangements (see 14).');
INSERT INTO `measureDescriptiontext` VALUES (459,276,1,'Electronic commerce services','');
INSERT INTO `measureDescriptiontext` VALUES (460,277,1,'Electronic commerce','Information involved in electronic commerce passing over public networks should be protected from fraudulent activity, contract dispute, and unauthorized disclosure and modification.\n\nImplementation guidance\nSecurity considerations for electronic commerce should include the following:\na) the level of confidence each party requires in each others claimed identity, e.g. through authentication;\nb) authorization processes associated with who may set prices, issue or sign key trading documents;\nc) ensuring that trading partners are fully informed of their authorisations;\nd) determining and meeting requirements for confidentiality, integrity, proof of dispatch and receipt of key documents, and the non-repudiation of contracts, e.g. associated with tendering and contract processes;\ne) the level of trust required in the integrity of advertised price lists;\nf) the confidentiality of any sensitive data or information;\ng) the confidentiality and integrity of any order transactions, payment information, delivery address details, and confirmation of receipts;\nh) the degree of verification appropriate to check payment information supplied by a customer;\ni) selecting the most appropriate settlement form of payment to guard against fraud;\nj) the level of protection required to maintain the confidentiality and integrity of order information;\nk) avoidance of loss or duplication of transaction information;\nl) liability associated with any fraudulent transactions; m) insurance requirements.\n\nMany of the above considerations can be addressed by the application of cryptographic controls (see 12.3), taking into account compliance with legal requirements (see 15.1, especially 15.1.6 for cryptography legislation).\nElectronic commerce arrangements between trading partners should be supported by a documented agreement which commits both parties to the agreed terms of trading, including details of authorization (see b) above). Other agreements with information service and value added network providers may be necessary. Public trading systems should publicize their terms of business to customers.\nConsideration should be given to the resilience to attack of the host(s) used for electronic commerce, and the security implications of any network interconnection required for the implementation of electronic commerce services (see 11.4.6).');
INSERT INTO `measureDescriptiontext` VALUES (461,278,1,'On-line transactions','Information involved in on-line transactions should be protected to prevent incomplete transmission, mis-routing, unauthorized message alteration, unauthorized disclosure, unauthorized message duplication or replay.\n\nImplementation guidance\nSecurity considerations for on-line transactions should include the following:\na) the use of electronic signatures by each of the parties involved in the transaction;\nb) all aspects of the transaction, i.e. ensuring that:\n1) user credentials of all parties are valid and verified;\n2) the transaction remains confidential; and\n3) privacy associated with all parties involved is retained;\nc) communications path between all involved parties is encrypted;\nd) protocols used to communicate between all involved parties is secured;\ne) ensuring that the storage of the transaction details are located outside of any public accessible environment, e.g. on a storage platform existing on the organizational Intranet, and not retained and exposed on a storage medium directly accessible from the Internet;\nf) where a trusted authority is used (e.g. for the purposes of issuing and maintaining digital signatures and/or digital certificates) security is integrated and embedded throughout the entire end-to-end certificate/signature management process.');
INSERT INTO `measureDescriptiontext` VALUES (462,279,1,'Publicly available information','The integrity of information being made available on a publicly available system should be protected to prevent unauthorized modification.\n\nImplementation guidance\nSoftware, data, and other information requiring a high level of integrity, being made available on a publicly available system, should be protected by appropriate mechanisms, e.g. digital signatures (see 12.3). The publicly accessible system should be tested against weaknesses and failures prior to information being made available. There should be a formal approval process before information is made publicly available. In addition, all input provided from the outside to the system should be verified and approved. Electronic publishing systems, especially those that permit feedback and direct entering of information, should be carefully controlled so that:\na) information is obtained in compliance with any data protection legislation (see 15.1.4);\nb) information input to, and processed by, the publishing system will be processed completely and accurately in a timely manner;\nc) sensitive information will be protected during collection, processing, and storage;\nd) access to the publishing system does not allow unintended access to networks to which the system is connected.');
INSERT INTO `measureDescriptiontext` VALUES (463,280,1,'Monitoring','');
INSERT INTO `measureDescriptiontext` VALUES (464,281,1,'Audit logging','Audit logs recording user activities, exceptions, and information security events should be produced and kept for an agreed period to assist in future investigations and access control monitoring.\n\nImplementation guidance\nAudit logs should include, when relevant:\na) user IDs;\nb) dates, times, and details of key events, e.g. log-on and log-off;c) terminal identity or location if possible;\nd) records of successful and rejected system access attempts;\ne) records of successful and rejected data and other resource access attempts;\nf) changes to system configuration;\ng) use of privileges;\nh) use of system utilities and applications;\ni) files accessed and the kind of access;\nj) network addresses and protocols;\nk) alarms raised by the access control system;\nl) activation and de-activation of protection systems, such as anti-virus systems and intrusion detection systems.');
INSERT INTO `measureDescriptiontext` VALUES (465,282,1,'Monitoring system use','Procedures for monitoring use of information processing facilities should be established and the results of the monitoring activities reviewed regularly.\n\nImplementation guidance\nThe level of monitoring required for individual facilities should be determined by a risk assessment. An organisation should comply with all relevant legal requirements applicable to its monitoring activities. Areas that should be considered include:\na) authorized access, including detail such as:\n1) the user ID;\n2) the date and time of key events;\n3) the types of events;\n4) the files accessed;\n5) the program/utilities used;\nb) all privileged operations, such as:\n1) use of privileged accounts, e.g. supervisor, root, administrator;\n2) system start-up and stop;\n3) I/O device attachment/detachment;\nc) unauthorized access attempts, such as:\n1) failed or rejected user actions;\n2) failed or rejected actions involving data and other resources;\n3) access policy violations and notifications for network gateways and firewalls;\n4) alerts from proprietary intrusion detection systems;\nd) system alerts or failures such as:\n1) console alerts or messages;\n2) system log exceptions;\n3) network management alarms;\n4) alarms raised by the access control system;\ne) changes to, or attempts to change, system security settings and controls.\n\nHow often the results of monitoring activities are reviewed should depend on the risks involved. Risk factors that should be considered include the:\na) criticality of the application processes;\nb) value, sensitivity, and criticality of the information involved;\nc) past experience of system infiltration and misuse, and the frequency of vulnerabilities being exploited;\nd) extent of system interconnection (particularly public networks);\ne) logging facility being de-activated.');
INSERT INTO `measureDescriptiontext` VALUES (466,283,1,'Protection of log information','Logging facilities and log information should be protected against tampering and unauthorized access.\n\nImplementation guidance\nControls should aim to protect against unauthorized changes and operational problems with the logging facility including:\na) alterations to the message types that are recorded;\nb) log files being edited or deleted;\nc) storage capacity of the log file media being exceeded, resulting in either the failure to record events or over-writing of past recorded events.\n\nSome audit logs may be required to be archived as part of the record retention policy or because of requirements to collect and retain evidence (see also 13.2.3).');
INSERT INTO `measureDescriptiontext` VALUES (467,284,1,'Administrator and operator logs','System administrator and system operator activities should be logged.\n\nImplementation guidance\nLogs should include:\na) the time at which an event (success or failure) occurred;\nb) information about the event (e.g. files handled) or failure (e.g. error occurred and corrective action taken);\nc) which account and which administrator or operator was involved;\nd) which processes were involved.\n\nSystem administrator and operator logs should be reviewed on a regular basis.');
INSERT INTO `measureDescriptiontext` VALUES (468,285,1,'Fault logging','Faults should be logged, analyzed, and appropriate action taken.\n\nImplementation guidance\nFaults reported by users or by system programs related to problems with information processing or communications systems should be logged. There should be clear rules for handling reported faults including:\na) review of fault logs to ensure that faults have been satisfactorily resolved;\nb) review of corrective measures to ensure that controls have not been compromised, and that the action taken is fully authorized.\n\nIt should be ensured that error logging is enabled, if this system function is available.');
INSERT INTO `measureDescriptiontext` VALUES (469,286,1,'Clock synchronization','The clocks of all relevant information processing systems within an organization or security domain should be synchronized with an agreed accurate time source.\n\nImplementation guidance\nWhere a computer or communications device has the capability to operate a real-time clock, this clock should be set to an agreed standard, e.g. Coordinated Universal Time (UTC) or local standard time. As some clocks are known to drift with time, there should be a procedure that checks for and corrects any significant variation.\nThe correct interpretation of the date/time format is important to ensure that the timestamp reflects the real date/time. Local specifics (e.g. daylight savings) should be taken into account.');
INSERT INTO `measureDescriptiontext` VALUES (470,287,1,'Access control','');
INSERT INTO `measureDescriptiontext` VALUES (471,288,1,'Business requirement for access control','');
INSERT INTO `measureDescriptiontext` VALUES (472,289,1,'Access control policy','An access control policy should be established, documented, and reviewed based on business and security requirements for access.\n\nImplementation guidance\nAccess control rules and rights for each user or group of users should be clearly stated in an access control policy. Access controls are both logical and physical (see also section 9) and these should be considered together. Users and service providers should be given a clear statement of the business requirements to be met by access controls.\nThe policy should take account of the following:\na) security requirements of individual business applications;\nb) identification of all information related to the business applications and the risks the information is facing;\nc) policies for information dissemination and authorization, e.g. the need to know principle and security levels and classification of information (see 7.2);\nd) consistency between the access control and information classification policies of different systems and networks;\ne) relevant legislation and any contractual obligations regarding protection of access to data or services (see 15.1);\nf) standard user access profiles for common job roles in the organization;\ng) management of access rights in a distributed and networked environment which recognizes all types of connections available;\nh) segregation of access control roles, e.g. access request, access authorization, access administration;\ni) requirements for formal authorization of access requests (see 11.2.1);\nj) requirements for periodic review of access controls (see 11.2.4);\nk) removal of access rights (see 8.3.3).');
INSERT INTO `measureDescriptiontext` VALUES (473,290,1,'User access management','');
INSERT INTO `measureDescriptiontext` VALUES (474,291,1,'User registration','There should be a formal user registration and de-registration procedure in place for granting and revoking access to all information systems and services.\nImplementation guidance\nThe access control procedure for user registration and de-registration should include:\na) using unique user IDs to enable users to be linked to and held responsible for their actions; the use of group IDs should only be permitted where they are necessary for business or operational reasons, and should be approved and documented;\nb) checking that the user has authorization from the system owner for the use of the information system or service; separate approval for access rights from management may also be appropriate;\nc) checking that the level of access granted is appropriate to the business purpose (see 11.1) and is consistent with organizational security policy, e.g. it does not compromise segregation of duties (see 10.1.3);\nd) giving users a written statement of their access rights;\ne) requiring users to sign statements indicating that they understand the conditions of access;\nf) ensuring service providers do not provide access until authorization procedures have been completed;\ng) maintaining a formal record of all persons registered to use the service;\nh) immediately removing or blocking access rights of users who have changed roles or jobs or left the organization;\ni) periodically checking for, and removing or blocking, redundant user IDs and accounts (see 11.2.4);\nj) ensuring that redundant user IDs are not issued to other users.');
INSERT INTO `measureDescriptiontext` VALUES (475,292,1,'Privilege management','The allocation and use of privileges should be restricted and controlled.\n\nImplementation guidance\nMulti-user systems that require protection against unauthorized access should have the allocation of privileges controlled through a formal authorization process. The following steps should be considered:\na) the access privileges associated with each system product, e.g. operating system, database management system and each application, and the users to which they need to be allocated should be identified;\nb) privileges should be allocated to users on a need-to-use basis and on an event-by-event basis in line with the access control policy (11.1.1), i.e. the minimum requirement for their functional role only when needed;\nc) an authorization process and a record of all privileges allocated should be maintained. Privileges should not be granted until the authorization process is complete;\nd) the development and use of system routines should be promoted to avoid the need to grant privileges to users;\ne) the development and use of programs which avoid the need to run with privileges should be promoted;\nf) privileges should be assigned to a different user ID from those used for normal business use.');
INSERT INTO `measureDescriptiontext` VALUES (476,293,1,'User password management','The allocation of passwords should be controlled through a formal management process.\n\nImplementation guidance\nThe process should include the following requirements:\na) users should be required to sign a statement to keep personal passwords confidential and to keep group passwords solely within the members of the group; this signed statement could be included in the terms and conditions of employment (see 8.1.3);\nb) when users are required to maintain their own passwords they should be provided initially with a secure temporary password (see 11.3.1), which they are forced to change immediately;\nc) establish procedures to verify the identity of a user prior to providing a new, replacement or temporary password;\nd) temporary passwords should be given to users in a secure manner; the use of third parties or unprotected (clear text) electronic mail messages should be avoided;\ne) temporary passwords should be unique to an individual and should not be guessable;\nf) users should acknowledge receipt of passwords;\ng) passwords should never be stored on computer systems in an unprotected form;\nh) default vendor passwords should be altered following installation of systems or software.');
INSERT INTO `measureDescriptiontext` VALUES (477,294,1,'Review of user access rights','Management should review users'' access rights at regular intervals using a formal process.\n\nImplementation guidance\nThe review of access rights should consider the following guidelines:\na) users’ access rights should be reviewed at regular intervals, e.g. a period of 6 months, and after any changes, such as promotion, demotion, or termination of employment (see 11.2.1);\nb) user access rights should be reviewed and re-allocated when moving from one employment to another within the same organization;\nc) authorizations for special privileged access rights (see 11.2.2) should be reviewed at more frequent intervals, e.g. at a period of 3 months;\nd) privilege allocations should be checked at regular intervals to ensure that unauthorized privileges have not been obtained;\ne) changes to privileged accounts should be logged for periodic review.');
INSERT INTO `measureDescriptiontext` VALUES (478,295,1,'User responsibilities','');
INSERT INTO `measureDescriptiontext` VALUES (479,296,1,'Password use','Users should be required to follow good security practices in the selection and use of passwords.\n\nImplementation guidance\nAll users should be advised to:\na) keep passwords confidential;\nb) avoid keeping a record (e.g. paper, software file or hand-held device) of passwords, unless this can be stored securely and the method of storing has been approved;\nc) change passwords whenever there is any indication of possible system or password compromise;\nd) select quality passwords with sufficient minimum length which are:\n1) easy to remember;\n2) not based on anything somebody else could easily guess or obtain using person related information, e.g. names, telephone numbers, and dates of birth etc.;\n3) not vulnerable to dictionary attacks (i.e. do not consist of words included in dictionaries);\n4) free of consecutive identical, all-numeric or all-alphabetic characters;\ne) change passwords at regular intervals or based on the number of accesses (passwords for privileged accounts should be changed more frequently than normal passwords), and avoid re-using or cycling old passwords;\nf) change temporary passwords at the first log-on;\ng) not include passwords in any automated log-on process, e.g. stored in a macro or function key;\nh) not share individual user passwords;\ni) not use the same password for business and non-business purposes.\n\nIf users need to access multiple services, systems or platforms, and are required to maintain multiple separate passwords, they should be advised that they may use a single, quality password (see d) above) for all services where the user is assured that a reasonable level of protection has been established for the storage of the password within each service, system or platform.');
INSERT INTO `measureDescriptiontext` VALUES (480,297,1,'Unattended user equipment','Users should ensure that unattended equipment has appropriate protection.\n\nImplementation guidance\nAll users should be made aware of the security requirements and procedures for protecting unattended equipment, as well as their responsibilities for implementing such protection. Users should be advised to:\na) terminate active sessions when finished, unless they can be secured by an appropriate locking mechanism, e.g. a password protected screen saver;\nb) log-off mainframe computers, servers, and office PCs when the session is finished (i.e. not just switch off the PC screen or terminal);\nc) secure PCs or terminals from unauthorized use by a key lock or an equivalent control, e.g. password access, when not in use (see also 11.3.3).');
INSERT INTO `measureDescriptiontext` VALUES (481,298,1,'Clear desk and clear screen policy','A clear desk policy for papers and removable storage media and a clear screen policy for information processing facilities should be adopted.\n\nImplementation guidance\nThe clear desk and clear screen policy should take into account the information classifications (see 7.2), legal and contractual requirements (see 15.1), and the corresponding risks and cultural aspects of the organization. The following guidelines should be considered:\na) sensitive or critical business information, e.g. on paper or on electronic storage media, should be locked away (ideally in a safe or cabinet or other forms of security furniture) when not required, especially when the office is vacated;\nb) computers and terminals should be left logged off or protected with a screen and keyboard locking mechanism controlled by a password, token or similar user authentication mechanism when unattended and should be protected by key locks, passwords or other controls when not in use;\nc) incoming and outgoing mail points and unattended facsimile machines should be protected;\nd) unauthorised use of photocopiers and other reproduction technology (e.g., scanners, digital cameras) should be prevented;\ne) documents containing sensitive or classified information should be removed from printers immediately.');
INSERT INTO `measureDescriptiontext` VALUES (482,299,1,'Network access control','');
INSERT INTO `measureDescriptiontext` VALUES (483,300,1,'Policy on use of network services','Users should only be provided with access to the services that they have been specifically authorized to use.\n\nImplementation guidance\nA policy should be formulated concerning the use of networks and network services. This policy should cover:\na) the networks and network services which are allowed to be accessed;\nb) authorization procedures for determining who is allowed to access which networks and networked services;\nc) management controls and procedures to protect access to network connections and network services;\nd) the means used to access networks and network services (e.g. the conditions for allowing dial-up access to an Internet service provider or remote system). \n\nThe policy on the use of network services should be consistent with the business access control policy (see 11.1).');
INSERT INTO `measureDescriptiontext` VALUES (484,301,1,'User authentication for external connections','Appropriate authentication methods should be used to control access by remote users.\n\nImplementation guidance\nAuthentication of remote users can be achieved using, for example, a cryptographic based technique, hardware tokens, or a challenge/response protocol. Possible implementations of such techniques can be found in various virtual private network (VPN) solutions. Dedicated private lines can also be used to provide assurance of the source of connections. Dial-back procedures and controls, e.g. using dial-back modems, can provide protection against unauthorized and unwanted connections to an organization’s information processing facilities. This type of control authenticates users trying to establish a connection to an organization’s network from remote locations. When using this control, an organization should not use network services, which include call forwarding, or, if they do, they should disable the use of such features to avoid weaknesses associated with call forwarding. The call back process should ensure that an actual disconnection on the organization’s side occurs. Otherwise, the remote user could hold the line open pretending that the call back verification has occurred. Call back procedures and controls should be thoroughly tested for this possibility. Node authentication can serve as an alternative means of authenticating groups of remote users where they are connected to a secure, shared computer facility. Cryptographic techniques, e.g. based on machine certificates, can be used for node authentication. This is part of several VPN based solutions. Additional authentication controls should be implemented to control access to wireless networks. In particular, special care is needed in the selection of controls for wireless networks due to the greater opportunities for undetected interception and insertion of network traffic. ');
INSERT INTO `measureDescriptiontext` VALUES (485,302,1,'Equipment identification in networks','Automatic equipment identification should be considered as a means to authenticate connections from specific locations and equipment.\n\nImplementation guidance\nEquipment identification can be used if it is important that the communication can only be initiated from a specific location or equipment. An identifier in or attached to, the equipment can be used to indicate whether this equipment is permitted to connect to the network. These identifiers should clearly indicate to which network the equipment is permitted to connect, if more than one network exists and particularly if these networks are of differing sensitivity. It may be necessary to consider physical protection of the equipment to maintain the security of the equipment identifier.');
INSERT INTO `measureDescriptiontext` VALUES (486,303,1,'Remote diagnostic and configuration port protection','Physical and logical access to diagnostic and configuration ports should be controlled.\n\nImplementation guidance\nPotential controls for the access to diagnostic and configuration ports include the use of a key lock and supporting procedures to control physical access to the port. An example for such a supporting procedure is to ensure that diagnostic and configuration ports are only accessible by arrangement between the manager of the computer service and the hardware/software support personnel requiring access.\nPorts, services, and similar facilities installed on a computer or network facility, which are not specifically required for business functionality, should be disabled or removed.');
INSERT INTO `measureDescriptiontext` VALUES (487,304,1,'Segregation in networks','Groups of information services, users, and information systems should be segregated on networks.\n\nImplementation guidance\nOne method of controlling the security of large networks is to divide them into separate logical network domains, e.g. an organization’s internal network domains and external network domains, each protected by a defined security perimeter. A graduated set of controls can be applied in different logical network domains to further segregate the network security environments, e.g. publicly accessible systems, internal networks, and critical assets. The domains should be defined based on a risk assessment and the different security requirements within each of the domains.\nSuch a network perimeter can be implemented by installing a secure gateway between the two networks to be interconnected to control access and information flow between the two domains. This gateway should be configured to filter traffic between these domains (see 11.4.6 and 11.4.7) and to block unauthorized access in accordance with the organization’s access control policy (see 11.1). An example of this type of gateway is what is commonly referred to as a firewall. Another method of segregating separate logical domains is to restrict network access by using virtual private  networks for user groups within the organization.\nNetworks can also be segregated using the network device functionality, e.g. IP switching. Separate domains can then be implemented by controlling the network data flows using the routing/switching capabilities, such as access control lists. The criteria for segregation of networks into domains should be based on the access control policy and access requirements (see 10.1), and also take account of the relative cost and performance impact of incorporating suitable network routing or gateway technology (see 11.4.6 and 11.4.7). In addition, segregation of networks should be based on the value and classification of information stored or processed in the network, levels of trust, or lines of business, in order to reduce the total\nimpact of a service disruption. Consideration should be given to the segregation of wireless networks from internal and private networks. As the perimeters of wireless networks are not well defined, a risk assessment should be carried out in such cases to identify controls (e.g. strong authentication, cryptographic methods, and frequency selection) to maintain network segregation.');
INSERT INTO `measureDescriptiontext` VALUES (488,305,1,'Network connection control','For shared networks, especially those extending across the organization’s boundaries, the capability of users to connect to the network should be restricted, in line with the access control policy and requirements of the business applications (see 11.1).\n\nImplementation guidance\nThe network access rights of users should be maintained and updated as required by the access control policy (see 11.1.1).\nThe connection capability of users can be restricted through network gateways that filter traffic by means of pre-defined tables or rules. Examples of applications to which restrictions should be applied are:\na) messaging, e.g. electronic mail;\nb) file transfer;\nc) interactive access;\nd) application access.\n\nLinking network access rights to certain times of day or dates should be considered.');
INSERT INTO `measureDescriptiontext` VALUES (489,306,1,'Network routing control','Routing controls should be implemented for networks to ensure that computer connections and information flows do not breach the access control policy of the business applications.\n\nImplementation guidance\nRouting controls should be based on positive source and destination address checking mechanisms. Security gateways can be used to validate source and destination addresses at internal and external network control points if proxy and/or network address translation technologies are employed. Implementers should be aware of the strength and shortcomings of any mechanisms deployed. The requirements for network routing control should be based on the access control policy (see 11.1).');
INSERT INTO `measureDescriptiontext` VALUES (490,307,1,'Operating system access control','');
INSERT INTO `measureDescriptiontext` VALUES (491,308,1,'Secure log-on procedures','Access to operating systems should be controlled by a secure log-on procedure.\n\nImplementation guidance\nThe procedure for logging into an operating system should be designed to minimize the opportunity  for unauthorized access. The log-on procedure should therefore disclose the minimum of information about the system, in order to avoid providing an unauthorized user with any unnecessary assistance. A good log-on procedure should: a) not display system or application identifiers until the log-on process has been successfully completed;\nb) display a general notice warning that the computer should only be accessed by authorized users;\nc) not provide help messages during the log-on procedure that would aid an unauthorized user;\nd) validate the log-on information only on completion of all input data. If an error condition arises, the system should not indicate which part of the data is correct or incorrect; \ne) limit the number of unsuccessful log-on attempts allowed, e.g. to three attempts, and consider:\n1) recording unsuccessful and successful attempts;\n2) forcing a time delay before further log-on attempts are allowed or rejecting any further attempts without specific authorization;\n3) disconnecting data link connections;\n4) sending an alarm message to the system console if the maximum number of log-on attempts is reached;\n5) setting the number of password retries in conjunction with the minimum length of the password and the value of the system being protected;\nf) limit the maximum and minimum time allowed for the log-on procedure. If exceeded, the system should terminate the log-on;\ng) display the following information on completion of a successful log-on:\n1) date and time of the previous successful log-on;\n2) details of any unsuccessful log-on attempts since the last successful log-on;\nh) not display the password being entered or consider hiding the password characters by\nsymbols;\ni) not transmit passwords in clear text over a network.');
INSERT INTO `measureDescriptiontext` VALUES (492,309,1,'User identification and authentication','All users should have a unique identifier (user ID) for their personal use only, and a suitable authentication technique should be chosen to substantiate the claimed identity of a user.\n\nImplementation guidance\nThis control should be applied for all types of users (including technical support personnel, operators, network administrators, system programmers, and database administrators). User IDs should be used to trace activities to the responsible individual. Regular user activities should not be performed from privileged accounts. In exceptional circumstances, where there is a clear business benefit, the use of a shared user ID for a group of users or a specific job can be used. Approval by management should be documented for such cases. Additional controls may be required to maintain accountability. Generic IDs for use by an individual should only be allowed either where the functions accessible or actions carried out by the ID do not need to be traced (e.g. read only access), or where there are other controls in place (e.g. password for a generic ID only issued to one staff at a time and logging such instance).\nWhere strong authentication and identity verification is required, authentication methods alternative to passwords, such as cryptographic means, smart cards, tokens or biometric means, should be used.');
INSERT INTO `measureDescriptiontext` VALUES (493,310,1,'Password management system','Systems for managing passwords should be interactive and should ensure quality passwords.\n\nImplementation guidance\nA password management system should:\na) enforce the use of individual user IDs and passwords to maintain accountability;\nb) allow users to select and change their own passwords and include a confirmation procedure to allow for input errors;\nc) enforce a choice of quality passwords (see 11.3.1);\nd) enforce password changes (see 11.3.1);\ne) force users to change temporary passwords at the first log-on (see 11.2.3);\nf) maintain a record of previous user passwords and prevent re-use;\ng) not display passwords on the screen when being entered;\nh) store password files separately from application system data;\ni) store and transmit passwords in protected (e.g. encrypted or hashed) form.');
INSERT INTO `measureDescriptiontext` VALUES (494,311,1,'Use of system utilities','The use of utility programs that might be capable of overriding system and application controls should be restricted and tightly controlled.\n\nImplementation guidance\nThe following guidelines for the use of system utilities should be considered:\na) use of identification, authentication, and authorization procedures for system utilities;\nb) segregation of system utilities from applications software;\nc) limitation of the use of system utilities to the minimum practical number of trusted, authorized users (see also 11.2.2);\nd) authorization for ad hoc use of systems utilities;\ne) limitation of the availability of system utilities, e.g. for the duration of an authorized change;\nf) logging of all use of system utilities;\ng) defining and documenting of authorization levels for system utilities;\nh) removal or disabling of all unnecessary software based utilities and system software;\ni) not making system utilities available to users who have access to applications on systems where segregation of duties is required.');
INSERT INTO `measureDescriptiontext` VALUES (495,312,1,'Session time-out','Inactive sessions should shut down after a defined period of inactivity.\n\nImplementation guidance\nA time-out facility should clear the session screen and also, possibly later, close both application and network sessions after a defined period of inactivity. The time-out delay should reflect the security risks of the area, the classification of the information being handled and the applications being used, and the risks related to the users of the equipment. A limited form of time-out facility can be provided for some systems, which clears the screen and prevents unauthorized access but does not close down the application or network sessions. ');
INSERT INTO `measureDescriptiontext` VALUES (496,313,1,'Limitation of connection time','Restrictions on connection times should be used to provide additional security for high-risk applications.\n\nImplementation guidance\nConnection time controls should be considered for sensitive computer applications, especially from high risk locations, e.g. public or external areas that are outside the organization’s security management. Examples of such restrictions include:\na) using predetermined time slots, e.g. for batch file transmissions, or regular interactive sessions of short duration;\nb) restricting connection times to normal office hours if there is no requirement for overtime or extended-hours operation;\nc) considering re-authentication at timed intervals.');
INSERT INTO `measureDescriptiontext` VALUES (497,314,1,'Application and information access control','');
INSERT INTO `measureDescriptiontext` VALUES (498,315,1,'Information access restriction','Access to information and application system functions by users and support personnel should be restricted in accordance with the defined access control policy.\n\nImplementation guidance\nRestrictions to access should be based on individual business application requirements. The access control policy should also be consistent with the organizational access policy (see section 11.1). Applying the following guidelines should be considered in order to support access restriction requirements:\na) providing menus to control access to application system functions;\nb) controlling the access rights of users, e.g. read, write, delete, and execute;\nc) controlling access rights of other applications;\nd) ensuring that outputs from application systems handling sensitive information contain only the information relevant to the use of the output and are sent only to authorized terminals and locations; this should include periodic reviews of such outputs to ensure that redundant information is removed.');
INSERT INTO `measureDescriptiontext` VALUES (499,316,1,'Sensitive system isolation','Sensitive systems should have a dedicated (isolated) computing environment.\n\nImplementation guidance\nThe following points should be considered for sensitive system isolation:\na) the sensitivity of an application system should be explicitly identified and documented by the application owner (see 7.1.2);\nb) when a sensitive application is to run in a shared environment, the application systems with which it will share resources and the corresponding risks should be identified and accepted by the owner of the sensitive application.');
INSERT INTO `measureDescriptiontext` VALUES (500,317,1,'Mobile computing and teleworking','');
INSERT INTO `measureDescriptiontext` VALUES (501,318,1,'Mobile computing and communications','A formal policy should be in place, and appropriate security measures should be adopted to protect against the risks of using mobile computing and communication facilities.\n\nImplementation guidance\nWhen using mobile computing and communicating facilities, e.g. notebooks, palmtops, laptops, smart cards, and mobile phones, special care should be taken to ensure that business information is not compromised. The mobile computing policy should take into account the risks of working with mobile computing equipment in unprotected environments. The mobile computing policy should include the requirements for physical protection, access controls, cryptographic techniques, back-ups, and virus protection. This policy should also include rules and advice on connecting mobile facilities to networks and guidance on the use of these facilities in public places. Care should be taken when using mobile computing facilities in public places, meeting rooms and other unprotected areas outside of the organization’s premises. Protection should be in place to avoid the unauthorized access to or disclosure of the information stored and processed by these facilities, e.g. using cryptographic techniques (see 12.3).\nUsers of mobile computing facilities in public places should take care to avoid the risk of overlooking by unauthorized persons. Procedures against malicious software should be in place and be kept up to date (see 10.4).\nBack-ups of critical business information should be taken regularly. Equipment should be available to enable the quick and easy back-up of information. These back-ups should be given adequate protection against, e.g., theft or loss of information. Suitable protection should be given to the use of mobile facilities connected to networks. Remote access to business information across public network using mobile computing facilities should only take place after successful identification and authentication, and with suitable access control mechanisms in place (see 11.4).\nMobile computing facilities should also be physically protected against theft especially when left, for example, in cars and other forms of transport, hotel rooms, conference centres, and meeting places. A specific procedure taking into account legal, insurance and other security requirements of the organization should be established for cases of theft or loss of the mobile computing facilities. Equipment carrying important, sensitive, and/or critical business information should not be left unattended and, where possible, should be physically locked away, or special locks should be used to secure the equipment (see 9.2.5). Training should be arranged for personnel using mobile computing to raise their awareness on the additional risks resulting from this way of working and the controls that should be implemented.');
INSERT INTO `measureDescriptiontext` VALUES (502,319,1,'Teleworking','A policy, operational plans and procedures should be developed and implemented for teleworking activities.\n\nImplementation guidance\nOrganizations should only authorize teleworking activities if they are satisfied that appropriate security arrangements and controls are in place, and that these comply with the organization’s security policy. Suitable protection of the teleworking site should be in place against, e.g., the theft of equipment and information, the unauthorized disclosure of information, unauthorized remote access to the organization’s internal systems or misuse of facilities. Teleworking activities should both be authorized and controlled by management, and it should be ensured that suitable arrangements are in place for this way of working.\nThe following matters should be considered:\na) the existing physical security of the teleworking site, taking into account the physical security of the building and the local environment;\nb) the proposed physical teleworking environment;\nc) the communications security requirements, taking into account the need for remote access to the organization’s internal systems, the sensitivity of the information that will be accessed and pass over the communication link and the sensitivity of the internal system;\nd) the threat of unauthorized access to information or resources from other persons using the accommodation, e.g. family and friends;\ne) the use of home networks and requirements or restrictions on the configuration of wireless network services;\nf) policies and procedures to prevent disputes concerning rights to intellectual property developed on privately owned equipment;\ng) access to privately owned equipment (to check the security of the machine or during an investigation), which may be prevented by legislation;\nh) software licensing agreements that are such that organizations may become liable for licensing for client software on workstations owned privately by employees, contractors or third party users;\ni) anti-virus protection and firewall requirements.\n\nThe guidelines and arrangements to be considered should include:\na) the provision of suitable equipment and storage furniture for the teleworking activities, where the use of privately owned equipment that is not under the control of the organization is not allowed;\nb) a definition of the work permitted, the hours of work, the classification of information that may be held and the internal systems and services that the teleworker is authorized to access;\nc) the provision of suitable communication equipment, including methods for securing remote access;\nd) physical security;\ne) rules and guidance on family and visitor access to equipment and information;\nf) the provision of hardware and software support and maintenance;\ng) the provision of insurance;\nh) the procedures for back-up and business continuity;\ni) audit and security monitoring;\nj) revocation of authority and access rights, and the return of equipment when the teleworking activities are terminated.');
INSERT INTO `measureDescriptiontext` VALUES (503,320,1,'Information systems acquisition, development and maintenance','');
INSERT INTO `measureDescriptiontext` VALUES (504,321,1,'Security requirements of information systems','');
INSERT INTO `measureDescriptiontext` VALUES (505,322,1,'Security requirements analysis and specification','Statements of business requirements for new information systems, or enhancements to existing information systems should specify the requirements for security controls.\n\nImplementation guidance\nSpecifications for the requirements for controls should consider the automated controls to be incorporated in the information system, and the need for supporting manual controls. Similar considerations should be applied when evaluating software packages, developed or purchased, for business applications. Security requirements and controls should reflect the business value of the information assets involved (see also 7.2), and the potential business damage, which might result from a failure or absence of security.\nSystem requirements for information security and processes for implementing security should be integrated in the early stages of information system projects. Controls introduced at the design stage are significantly cheaper to implement and maintain than those included during or after implementation.\nIf products are purchased, a formal testing and acquisition process should be followed. Contracts with the supplier should address the identified security requirements. Where the security functionality in a proposed product does not satisfy the specified requirement then the risk introduced and associated controls should be reconsidered prior to purchasing the product. Where additional functionality is supplied and causes a security risk, this should be disabled or the proposed control structure should be reviewed to determine if advantage can be taken of the enhanced functionality available.');
INSERT INTO `measureDescriptiontext` VALUES (506,323,1,'Correct processing in applications','');
INSERT INTO `measureDescriptiontext` VALUES (507,324,1,'Input data validation','Data input to applications should be validated to ensure that this data is correct and appropriate.\n\nImplementation guidance\nChecks should be applied to the input of business transactions, standing data (e.g. names and addresses, credit limits, customer reference numbers), and parameter tables (e.g. sales prices, currency conversion rates, tax rates). The following guidelines should be considered:\na) dual input or other input checks, such as boundary checking or limiting fields to specific ranges of input data, to detect the following errors:\n1) out-of-range values;\n2) invalid characters in data fields;\n3) missing or incomplete data;\n4) exceeding upper and lower data volume limits;\n5) unauthorized or inconsistent control data;\nb) periodic review of the content of key fields or data files to confirm their validity and integrity;\nc) inspecting hard-copy input documents for any unauthorized changes (all changes to input documents should be authorized);\nd) procedures for responding to validation errors;\ne) procedures for testing the plausibility of the input data;\nf) defining the responsibilities of all personnel involved in the data input process;\ng) creating a log of the activities involved in the data input process (see 10.10.1).');
INSERT INTO `measureDescriptiontext` VALUES (508,325,1,'Control of internal processing','Validation checks should be incorporated into applications to detect any corruption of information through processing errors or deliberate acts.\n\nImplementation guidance\nThe design and implementation of applications should ensure that the risks of processing failures leading to a loss of integrity are minimized. Specific areas to consider include:\na) the use of add, modify, and delete functions to implement changes to data;\nb) the procedures to prevent programs running in the wrong order or running after failure of prior processing (see also 10.1.1);\nc) the use of appropriate programs to recover from failures to ensure the correct processing of data;\nd) protection against attacks using buffer overruns/overflows.\nAn appropriate checklist should be prepared, activities documented, and the results should be kept secure. Examples of checks that can be incorporated include the following:\na) session or batch controls, to reconcile data file balances after transaction updates;\nb) balancing controls, to check opening balances against previous closing balances, namely:\n1) run-to-run controls;\n2) file update totals;\n3) program-to-program controls;\nc) validation of system-generated input data (see 12.2.1);\nd) checks on the integrity, authenticity or any other security feature of data or software downloaded, or uploaded, between central and remote computers;\ne) hash totals of records and files;\nf) checks to ensure that application programs are run at the correct time;\ng) checks to ensure that programs are run in the correct order and terminate in case of a failure, and that further processing is halted until the problem is resolved;\nh) creating a log of the activities involved in the processing (see 10.10.1).');
INSERT INTO `measureDescriptiontext` VALUES (509,326,1,'Message integrity','Requirements for ensuring authenticity and protecting message integrity in applications should be identified, and appropriate controls identified and implemented.\n\nImplementation guidance\nAn assessment of security risks should be carried out to determine if message integrity is required and to identify the most appropriate method of implementation.');
INSERT INTO `measureDescriptiontext` VALUES (510,327,1,'Output data validation','Data output from an application should be validated to ensure that the processing of stored information is correct and appropriate to the circumstances.\n\nImplementation guidance\nOutput validation may include:\na) plausibility checks to test whether the output data is reasonable;\nb) reconciliation control counts to ensure processing of all data;\nc) providing sufficient information for a reader or subsequent processing system to\ndetermine the accuracy, completeness, precision, and classification of the information;\nd) procedures for responding to output validation tests;\ne) defining the responsibilities of all personnel involved in the data output process;\nf) creating a log of activities in the data output validation process.');
INSERT INTO `measureDescriptiontext` VALUES (511,328,1,'Cryptographic controls','');
INSERT INTO `measureDescriptiontext` VALUES (512,329,1,'Policy on the use of cryptographic controls','A policy on the use of cryptographic controls for protection of information should be developed and implemented.\n\nImplementation guidance\nWhen developing a cryptographic policy the following should be considered:\na) the management approach towards the use of cryptographic controls across the organization, including the general principles under which business information should be protected (see also 5.1.1);\nb) based on a risk assessment, the required level of protection should be identified taking into account the type, strength, and quality of the encryption algorithm required;\nc) the use of encryption for protection of sensitive information transported by mobile or removable media, devices or across communication lines;\nd) the approach to key management, including methods to deal with the protection of cryptographic keys and the recovery of encrypted information in the case of lost, compromised or damaged keys;\ne) roles and responsibilities, e.g. who is responsible for:\n1) the implementation of the policy;\n2) the key management, including key generation (see also 12.3.2);\nf) the standards to be adopted for the effective implementation throughout the organization (which solution is used for which business processes);\ng) the impact of using encrypted information on controls that rely upon content inspection (e.g. virus detection).\nWhen implementing the organization’s cryptographic policy, consideration should be given to the regulations and national restrictions that might apply to the use of cryptographic techniques in different parts of the world and to the issues of trans-border flow of encrypted information (see also\n15.1.6). Cryptographic controls can be used to achieve different security objectives, e.g.:\na) confidentiality: using encryption of information to protect sensitive or critical information, either stored or transmitted;\nb) integrity/authenticity: using digital signatures or message authentication codes to protect the authenticity and integrity of stored or transmitted sensitive or critical information;\nc) non-repudiation: using cryptographic techniques to obtain proof of the occurrence or nonoccurrence of an event or action.');
INSERT INTO `measureDescriptiontext` VALUES (513,330,1,'Key management','Key management should be in place to support the organization’s use of cryptographic techniques.\n\nImplementation guidance\nAll cryptographic keys should be protected against modification, loss, and destruction. In addition, secret and private keys need protection against unauthorized disclosure. Equipment used to generate, store and archive keys should be physically protected.\nA key management system should be based on an agreed set of standards, procedures, and secure methods for:\na) generating keys for different cryptographic systems and different applications;\nb) generating and obtaining public key certificates;\nc) distributing keys to intended users, including how keys should be activated when received;\nd) storing keys, including how authorized users obtain access to keys;\ne) changing or updating keys including rules on when keys should be changed and how this will be done;\nf) dealing with compromised keys;\ng) revoking keys including how keys should be withdrawn or deactivated, e.g. when keys have been compromised or when a user leaves an organization (in which case keys should also be archived);\nh) recovering keys that are lost or corrupted as part of business continuity management, e.g. for recovery of encrypted information;\ni) archiving keys, e.g. for information archived or backed up;\nj) destroying keys;\nk) logging and auditing of key management related activities.\nIn order to reduce the likelihood of compromise, activation, and deactivation dates for keys should be defined so that the keys can only be used for a limited period of time. This period of time should be dependent on the circumstances under which the cryptographic control is being used, and the\nperceived risk. In addition to securely managing secret and private keys, the authenticity of public keys should also be considered. This authentication process can be done using public key certificates which are normally issued by a certification authority, which should be a recognized organization with suitable controls and procedures in place to provide the required degree of trust. \nThe contents of service level agreements or contracts with external suppliers of cryptographic services, e.g. with a certification authority, should cover issues of liability, reliability of services and response times for the provision of services (see 6.2.3).');
INSERT INTO `measureDescriptiontext` VALUES (514,331,1,'Security of system files','');
INSERT INTO `measureDescriptiontext` VALUES (515,332,1,'Control of operational software','There should be procedures in place to control the installation of software on operational systems.\n\nImplementation guidance\nTo minimize the risk of corruption to operational systems, the following guidelines should be considered to control changes:\na) the updating of the operational software, applications, and program libraries should only be performed by trained administrators upon appropriate management authorization (see 12.4.3);\nb) operational systems should only hold approved executable code, and not development code or compilers;\nc) applications and operating system software should only be implemented after extensive and successful testing; the tests should include tests on usability, security, effects on other systems and user-friendliness, and should be carried out on separate systems (see also 10.1.4); it should be ensured that all corresponding program source libraries have been updated;\nd) a configuration control system should be used to keep control of all implemented software as well as the system documentation;\ne) a rollback strategy should be in place before changes are implemented;\nf) an audit log should be maintained of all updates to operational program libraries;\ng) previous versions of application software should be retained as a contingency measure;\nh) old versions of software should be archived, together with all required information and parameters, procedures, configuration details, and supporting software for as long as the data is retained in archive.\nVendor supplied software used in operational systems should be maintained at a level supported by the supplier. Over time, software vendors will cease to support older versions of software. The organization should consider the risks of relying on unsupported software.\nAny decision to upgrade to a new release should take into account the business requirements for the change, and the security of the release, i.e. the introduction of new security functionality or the number and severity of security problems affecting this version. Software patches should be applied\nwhen they can help to remove or reduce security weaknesses (see also 12.6.1). Physical or logical access should only be given to suppliers for support purposes when necessary, and with management approval. The supplier’s activities should be monitored. Computer software may rely on externally supplied software and modules, which should be monitored and controlled to avoid unauthorized changes, which could introduce security weaknesses.');
INSERT INTO `measureDescriptiontext` VALUES (516,333,1,'Protection of system test data','Test data should be selected carefully, and protected and controlled.\n\nImplementation guidance\nThe use of operational databases containing personal information or any other sensitive information for testing purposes should be avoided. If personal or otherwise sensitive information is used for testing purposes, all sensitive details and content should be removed or modified beyond recognition before use. The following guidelines should be applied to protect operational data, when used for testing purposes:\na) the access control procedures, which apply to operational application systems, should also apply to test application systems;\nb) there should be separate authorization each time operational information is copied to a test application system;\nc) operational information should be erased from a test application system immediately after the testing is complete;\nd) the copying and use of operational information should be logged to provide an audit trail.');
INSERT INTO `measureDescriptiontext` VALUES (517,334,1,'Access control to program source code','Access to program source code should be restricted.\n\nImplementation guidance\nAccess to program source code and associated items (such as designs, specifications, verification plans and validation plans) should be strictly controlled, in order to prevent the introduction of unauthorized functionality and to avoid unintentional changes. For program source code, this can be\nachieved by controlled central storage of such code, preferably in program source libraries. The following guidelines should then be considered (see also 11) to control access to such program source libraries in order to reduce the potential for corruption of computer programs:\na) where possible, program source libraries should not be held in operational systems;\nb) the program source code and the program source libraries should be managed according to established procedures;\nc) support personnel should not have unrestricted access to program source libraries;\nd) the updating of program source libraries and associated items, and the issuing of program sources to programmers should only be performed after appropriate authorization has been received;\ne) program listings should be held in a secure environment (see 10.7.4);\nf) an audit log should be maintained of all accesses to program source libraries;\ng) maintenance and copying of program source libraries should be subject to strict change control procedures (see 12.5.1).');
INSERT INTO `measureDescriptiontext` VALUES (518,335,1,'Security in development and support processes','');
INSERT INTO `measureDescriptiontext` VALUES (519,336,1,'Change control procedures','The implementation of changes should be controlled by the use of formal change control procedures.\n\nImplementation guidance\nFormal change control procedures should be documented and enforced in order to minimize the corruption of information systems. Introduction of new systems and major changes to existing systems should follow a formal process of documentation, specification, testing, quality control, and managed implementation. This process should include a risk assessment, analysis of the impacts of changes, and specification of security controls needed. This process should also ensure that existing security and control procedures are not compromised, that support programmers are given access only to those parts of the system necessary for their work, and that formal agreement and approval for any change is obtained. Wherever practicable, application and operational change control procedures should be integrated (see also 10.1.2). The change procedures should include:\na) maintaining a record of agreed authorization levels;\nb) ensuring changes are submitted by authorized users;\nc) reviewing controls and integrity procedures to ensure that they will not be compromised by the changes;\nd) identifying all software, information, database entities, and hardware that require amendment;\ne) obtaining formal approval for detailed proposals before work commences;\nf) ensuring authorized users accept changes prior to implementation;\ng) ensuring that the system documentation set is updated on the completion of each change and that old documentation is archived or disposed of;\nh) maintaining a version control for all software updates;\ni) maintaining an audit trail of all change requests;\nj) ensuring that operating documentation (see 10.1.1) and user procedures are changed as necessary to remain appropriate;\nk) ensuring that the implementation of changes takes place at the right time and does not disturb the business processes involved.');
INSERT INTO `measureDescriptiontext` VALUES (520,337,1,'Technical review of applications after operating system changes','When operating systems are changed, business critical applications should be reviewed and tested to ensure there is no adverse impact on organizational operations or security.\n\nImplementation guidance\nThis process should cover:\na) review of application control and integrity procedures to ensure that they have not been compromised by the operating system changes;\nb) ensuring that the annual support plan and budget will cover reviews and system testing resulting from operating system changes;\nc) ensuring that notification of operating system changes is provided in time to allow appropriate tests and reviews to take place before implementation; \nd) ensuring that appropriate changes are made to the business continuity plans (see clause 14).\nA specific group or individual should be given responsibility for monitoring vulnerabilities and vendors’ releases of patches and fixes (see 12.6).');
INSERT INTO `measureDescriptiontext` VALUES (521,338,1,'Restrictions on changes to software packages','Modifications to software packages should be discouraged, limited to necessary changes, and all changes should be strictly controlled.\n\nImplementation guidance\nAs far as possible, and practicable, vendor-supplied software packages should be used without modification. Where a software package needs to be modified the following points should be considered:\na) the risk of built-in controls and integrity processes being compromised;\nb) whether the consent of the vendor should be obtained;\nc) the possibility of obtaining the required changes from the vendor as standard program updates;\nd) the impact if the organization becomes responsible for the future maintenance of the software as a result of changes.\n\nIf changes are necessary the original software should be retained and the changes applied to a clearly identified copy. A software update management process should be implemented to ensure the most up-to-date approved patches and application updates are installed for all authorized software (see 12.6). All changes should be fully tested and documented, so that they can be reapplied if necessary to future software upgrades. If required, the modifications should be tested and validated by an independent evaluation body.');
INSERT INTO `measureDescriptiontext` VALUES (522,339,1,'Information leakage','Opportunities for information leakage should be prevented.\n\nImplementation guidance\nThe following should be considered to limit the risk of information leakage, e.g. through the use and exploitation of covert channels:\na) scanning of outbound media and communications for hidden information;\nb) masking and modulating system and communications behaviour to reduce the likelihood of a third party being able to deduce information from such behaviour;\nc) making use of systems and software that are considered to be of high integrity, e.g. using evaluated products (see ISO/IEC 15408);\nd) regular monitoring of personnel and system activities, where permitted under existing legislation or regulation;\ne) monitoring resource usage in computer systems.');
INSERT INTO `measureDescriptiontext` VALUES (523,340,1,'Outsourced software development','Outsourced software development should be supervised and monitored by the organization.\n\nImplementation guidance\nWhere software development is outsourced, the following points should be considered:\na) licensing arrangements, code ownership, and intellectual property rights (see 15.1.2);\nb) certification of the quality and accuracy of the work carried out;\nc) escrow arrangements in the event of failure of the third party;\nd) rights of access for audit of the quality and accuracy of work done;\ne) contractual requirements for quality and security functionality of code;\nf) testing before installation to detect malicious and Trojan code.');
INSERT INTO `measureDescriptiontext` VALUES (524,341,1,'Technical Vulnerability Management','');
INSERT INTO `measureDescriptiontext` VALUES (525,342,1,'Control of technical vulnerabilities','Timely information about technical vulnerabilities of information systems being used should be obtained, the organization''s exposure to such vulnerabilities evaluated, and appropriate measures taken to address the associated risk.\n\nImplementation guidance\nA current and complete inventory of assets (see 7.1) is a prerequisite for effective technical vulnerability management. Specific information needed to support technical vulnerability management includes the software vendor, version numbers, current state of deployment (e.g. what software is installed on what systems), and the person(s) within the organization responsible for the software.\nAppropriate, timely action should be taken in response to the identification of potential technical vulnerabilities. The following guidance should be followed to establish an effective management process for technical vulnerabilities:\na) the organization should define and establish the roles and responsibilities associated with technical vulnerability management, including vulnerability monitoring, vulnerability risk assessment, patching, asset tracking, and any coordination responsibilities required;\nb) information resources that will be used to identify relevant technical vulnerabilities and to maintain awareness about them should be identified for software and other technology (based on the asset inventory list, see 7.1.1); these information resources should be updated based on changes in the inventory, or when other new or useful resources are found;\nc) a timeline should be defined to react to notifications of potentially relevant technical vulnerabilities;\nd) once a potential technical vulnerability has been identified, the organization should identify the associated risks and the actions to be taken; such action could involve patching of vulnerable systems and/or applying other controls; \ne) depending on how urgently a technical vulnerability needs to be addressed, the action taken should be carried out according to the controls related to change management (see 12.5.1) or by following information security incident response procedures (see 13.2); \nf) if a patch is available, the risks associated with installing the patch should be assessed (the risks posed by the vulnerability should be compared with the risk of installing the patch); \ng) patches should be tested and evaluated before they are installed to ensure they are effective and do not result in side effects that cannot be tolerated; if no patch is available, other controls should be considered, such as \n1) turning off services or capabilities related to the vulnerability;\n2) adapting or adding access controls, e.g. firewalls, at network borders (see 11.4.5);\n3) increased monitoring to detect or prevent actual attacks;\n4) raising awareness of the vulnerability;\nh) an audit log should be kept for all procedures undertaken;\ni) the technical vulnerability management process should be regularly monitored and evaluated in order to ensure its effectiveness and efficiency;\nj) systems at high risk should be addressed first.');
INSERT INTO `measureDescriptiontext` VALUES (526,343,1,'Information security incident management','');
INSERT INTO `measureDescriptiontext` VALUES (527,344,1,'Reporting information security events and weaknesses','');
INSERT INTO `measureDescriptiontext` VALUES (528,345,1,'Reporting information security events','Information security events should be reported through appropriate management channels as quickly as possible.\n\nImplementation guidance\nA formal information security event reporting procedure should be established, together with an incident response and escalation procedure, setting out the action to be taken on receipt of a report of an information security event. A point of contact should be established for the reporting of information security events. It should be ensured that this point of contact is known throughout the organization, is always available and is able to provide adequate and timely response. All employees, contractors and third party users should be made aware of their responsibility to report any information security events as quickly as possible. They should also be aware of the procedure for reporting information security events and the point of contact. The reporting procedures should include:\na) suitable feedback processes to ensure that those reporting information security events are notified of results after the issue has been dealt with and closed;\nb) information security event reporting forms to support the reporting action, and to help the person reporting to remember all necessary actions in case of an information security event;\nc) the correct behaviour to be undertaken in case of an information security event, i.e.\n1) noting all important details (e.g. type of non-compliance or breach, occurring malfunction, messages on the screen, strange behaviour) immediately;\n2) not carrying out any own action, but immediately reporting to the point of contact;\nd) reference to an established formal disciplinary process for dealing with employees, contractors or third party users who commit security breaches.\nIn high-risk environments, a duress alarm4 may be provided whereby a person under duress can indicate such problems. The procedures for  responding to duress alarms should reflect the high risk situation such alarms are indicating.');
INSERT INTO `measureDescriptiontext` VALUES (529,346,1,'Reporting security weaknesses','All employees, contractors and third party users of information systems and services should be required to note and report any observed or suspected security weaknesses in systems or services.\n\nImplementation guidance\nAll employees, contractors and third party users should report these matters either to their management or directly to their service provider as quickly as possible in order to prevent information security incidents. The reporting mechanism should be as easy, accessible, and available as possible.\nThey should be informed that they should not, in any circumstances, attempt to prove a suspected weakness.');
INSERT INTO `measureDescriptiontext` VALUES (530,347,1,'Management of information security incidents and improvements','');
INSERT INTO `measureDescriptiontext` VALUES (531,348,1,'Responsibilities and procedures','Management responsibilities and procedures should be established to ensure a quick, effective, and orderly response to information security incidents.\n\nImplementation guidance\nIn addition to reporting of information security events and weaknesses (see also 13.1), the monitoring of systems, alerts, and vulnerabilities (10.10.2) should be used to detect information security incidents. The following guidelines for information security incident management procedures should be\nconsidered:\na) procedures should be established to handle different types of information security incident, including:\n1) information system failures and loss of service;\n2) malicious code (see 10.4.1);\n3) denial of service;\n4) errors resulting from incomplete or inaccurate business data;\n5) breaches of confidentiality and integrity;\n6) misuse of information systems;\nb) in addition to normal contingency plans (see 14.1.3), the procedures should also cover (see also 13.2.2):\n1) analysis and identification of the cause of the incident;\n2) containment;\n3) planning and implementation of corrective action to prevent recurrence, if necessary;\n4) communication with those affected by or involved with recovery from the incident;\n5) reporting the action to the appropriate authority;\nc) audit trails and similar evidence should be collected (see 13.2.3) and secured, as appropriate, for:\n1) internal problem analysis;\n2) use as forensic evidence in relation to a potential breach of contract or regulatory requirement or in the event of civil or criminal proceedings, e.g. under computer misuse or data protection legislation;\n3) negotiating for compensation from software and service suppliers;\nd) action to recover from security breaches and correct system failures should be carefully and formally controlled; the procedures should ensure that:\n1) only clearly identified and authorized personnel are allowed access to live systems and data (see also 6.2 for external access);\n2) all emergency actions taken are documented in detail;\n3) emergency action is reported to management and reviewed in an orderly manner;\n4) the integrity of business systems and controls is confirmed with minimal delay.\n\nThe objectives for information security incident management should be agreed with management, and it should be ensured that those responsible for information security incident management understand the organization’s priorities for handling information security incidents.');
INSERT INTO `measureDescriptiontext` VALUES (532,349,1,'Learning from information security incidents','There should be mechanisms in place to enable the types, volumes, and costs of information security incidents to be quantified and monitored.\n\nImplementation guidance\nThe information gained from the evaluation of information security incidents should be used to identify recurring or high impact incidents.');
INSERT INTO `measureDescriptiontext` VALUES (533,350,1,'Collection of evidence','Where a follow-up action against a person or organization after an information security incident involves legal action (either civil or criminal), evidence should be collected, retained, and presented to conform to the rules for evidence laid down in the relevant jurisdiction(s).\n\nImplementation guidance\nInternal procedures should be developed and followed when collecting and presenting evidence for the purposes of disciplinary action handled within an organization. In general, the rules for evidence cover:\na) admissibility of evidence: whether or not the evidence can be used in court;\nb) weight of evidence: the quality and completeness of the evidence.\n\nTo achieve admissibility of the evidence, the organization should ensure that their information systems comply with any published standard or code of practice for the production of admissible evidence. The weight of evidence provided should comply with any applicable requirements. To achieve weight of evidence, the quality and completeness of the controls used to correctly and consistently protect the evidence (i.e. process control evidence) throughout the period that the evidence to be recovered was stored and processed should be demonstrated by a strong evidence trail. In general, such a strong trail can be established under the following conditions:\na) for paper documents: the original is kept securely with a record of the individual who found the document, where the document was found, when the document was found and who witnessed the discovery; any investigation should ensure that originals are not tampered with;\nb) for information on computer media: mirror images or copies (depending on applicable requirements) of any removable media, information on hard disks or in memory should be taken to ensure availability; the log of all actions during the copying process should be kept and the process should be witnessed; the original media and the log (if this is not possible, at least one mirror image or copy) should be kept securely and untouched.\n\nAny forensics work should only be performed on copies of the evidential material. The integrity of all evidential material should be protected. Copying of evidential material should be supervised by trustworthy personnel and information on when and where the copying process was executed, who\nperformed the copying activities and which tools and programs have been utilized should be logged. ');
INSERT INTO `measureDescriptiontext` VALUES (534,351,1,'Business continuity management','');
INSERT INTO `measureDescriptiontext` VALUES (535,352,1,'Information security aspects of business continuity management','');
INSERT INTO `measureDescriptiontext` VALUES (536,353,1,'Including information security in the business continuity management process','A managed process should be developed and maintained for business continuity throughout the organization that addresses the information security requirements needed for the organization’s business continuity.\n\nImplementation guidance\nThe process should bring together the following key elements of business continuity management: \na) understanding the risks the organization is facing in terms of likelihood and impact in time, including an identification and prioritisation of critical business processes (see 14.1.2);\nb) identifying all the assets involved in critical business processes (see 7.1.1);\nc) understanding the impact which interruptions caused by information security incidents are likely to have on the business (it is important that solutions are found that will handle incidents causing smaller impact, as well as serious incidents that could threaten the viability of the organization), and establishing the business objectives of information processing facilities;\nd) considering the purchase of suitable insurance which may form part of the overall business continuity process, as well as being part of operational risk management;\ne) identifying and considering the implementation of additional preventive and mitigating controls;\nf) identifying sufficient financial, organizational, technical, and environmental resources to address the identified information security requirements;\ng) ensuring the safety of personnel and the protection of information processing facilities and organizational property;\nh) formulating and documenting business continuity plans addressing information security requirements in line with the agreed business continuity strategy (see 14.1.3);\ni) regular testing and updating of the plans and processes put in place (see 14.1.5);\nj) ensuring that the management of business continuity is incorporated in the organization’s processes and structure; responsibility for the business continuity management process should be assigned at an appropriate level within the organization (see 6.1.1).');
INSERT INTO `measureDescriptiontext` VALUES (537,354,1,'Business continuity and risk assessment','Events that can cause interruptions to business processes should be identified, along with the probability and impact of such interruptions and their consequences for information security.\n\nImplementation guidance\nInformation security aspects of business continuity should be based on identifying events (or sequence of events) that can cause interruptions to the organizations business processes, e.g. equipment failure, human errors, theft, fire, natural disasters and acts of terrorism. This should be followed by a risk assessment to determine the probability and impact of such interruptions, in terms of time, damage scale and recovery period.\nBusiness continuity risk assessments should be carried out with full involvement from owners of business resources and processes. This assessment should consider all business processes and should not be limited to the information processing facilities, but should include the results specific to information security. It is important to link the different risk aspects together, to obtain a complete picture of the business continuity requirements of the organization. The assessment should identify, quantify, and prioritise risks against criteria and objectives relevant to the organization, including critical resources, impacts of disruptions, allowable outage times, and recovery priorities. Depending on the results of the risk assessment, a business continuity strategy should be developed to determine the overall approach to business continuity. Once this strategy has been created, endorsement should be provided by management, and a plan created and endorsed to implement this strategy.');
INSERT INTO `measureDescriptiontext` VALUES (538,355,1,'Developing and implementing continuity plans including information security','Plans should be developed and implemented to maintain or restore operations and ensure availability of information at the required level and in the required time scales following interruption to, or failure of, critical business processes.\n\nImplementation guidance\nThe business continuity planning process should consider the following:\na) identification and agreement of all responsibilities and business continuity procedures; \nb) identification of the acceptable loss of information and services;\nc) implementation of the procedures to allow recovery and restoration of business operations and availability of information in required time-scales; particular attention needs to be given to the assessment of internal and external business dependencies and the contracts in place;\nd) operational procedures to follow pending completion of recovery and restoration;\ne) documentation of agreed procedures and processes;\nf) appropriate education of staff in the agreed procedures and processes, including crisis management;\ng) testing and updating of the plans.\n\nThe planning process should focus on the required business objectives, e.g. restoring of specific communication services to customers in an acceptable amount of time. The services and resources facilitating this should be identified, including staffing, non-information processing resources, as well as fallback arrangements for information processing facilities. Such fallback arrangements may include arrangements with third parties in the form of reciprocal agreements, or commercial subscription services.\nBusiness continuity plans should address organizational vulnerabilities and therefore may contain sensitive information that needs to be appropriately protected. Copies of business continuity plans should be stored in a remote location, at a sufficient distance to escape any damage from a disaster at the main site. Management should ensure copies of the business continuity plans are up-to-date and protected with the same level of security as applied at the main site. Other material necessary to execute the continuity plans should also be stored at the remote location.\nIf alternative temporary locations are used, the level of implemented security controls at these locations should be equivalent to the main site.');
INSERT INTO `measureDescriptiontext` VALUES (539,356,1,'Business continuity planning framework','A single framework of business continuity plans should be maintained to ensure all plans are consistent, to consistently address information security requirements, and to identify priorities for testing and maintenance.\n\nImplementation guidance\nEach business continuity plan should describe the approach for continuity, for example the approach to ensure information or information system availability and security. Each plan should also specify the escalation plan and the conditions for its activation, as well as the individuals responsible  for executing each component of the plan. When new requirements are identified, any existing emergency procedures, e.g. evacuation plans or fallback arrangements, should be amended as appropriate. Procedures should be included within the organization’s change management programme to ensure that business continuity matters are always addressed appropriately. Each plan should have a specific owner. Emergency procedures, manual fallback plans, and resumption plans should be within the responsibility of the owners of the appropriate business resources or processes involved. Fallback arrangements for alternative technical services, such as information processing and communications facilities, should usually be the responsibility of the service providers.\nA business continuity planning framework should address the identified information security requirements and consider the following:\na) the conditions for activating the plans which describe the process to be followed (e.g. how to assess the situation, who is to be involved) before each plan is activated;\nb) emergency procedures, which describe the actions to be taken following an incident, which jeopardizes business operations;\nc) fallback procedures which describe the actions to be taken to move essential business activities or support services to alternative temporary locations, and to bring business processes back into operation in the required time-scales;\nd) temporary operational procedures to follow pending completion of recovery and restoration;\ne) resumption procedures which describe the actions to be taken to return to normal business operations;\nf) a maintenance schedule which specifies how and when the plan will be tested, and the process for maintaining the plan;\ng) awareness, education, and training activities which are designed to create understanding of the business continuity processes and ensure that the processes continue to be effective;\nh) the responsibilities of the individuals, describing who is responsible for executing which component of the plan. Alternatives should be nominated as required;\ni) the critical assets and resources needed to be able to perform the emergency, fallback and\nresumption procedures.');
INSERT INTO `measureDescriptiontext` VALUES (540,357,1,'Testing, maintaining and reassessing business continuity plans','Business continuity plans should be tested and updated regularly to ensure that they are up to date and effective.\n\nImplementation guidance\nBusiness continuity plan tests should ensure that all members of the recovery team and other relevant staff are aware of the plans and their responsibility for business continuity and information security and know their role when a plan is invoked. The test schedule for business continuity plan(s) should indicate how and when each element of the plan should be tested. Each element of the plan(s) should be tested frequently. A variety of techniques should be used in order to provide assurance that the plan(s) will operate in real life. These should include:\na) table-top testing of various scenarios (discussing the business recovery arrangements using example interruptions);\nb) simulations (particularly for training people in their post-incident/crisis management roles);\nc) technical recovery testing (ensuring information systems can be restored effectively);\nd) testing recovery at an alternate site (running business processes in parallel with recovery operations away from the main site);\ne) tests of supplier facilities and services (ensuring externally provided services and products will meet the contracted commitment);\nf) complete rehearsals (testing that the organization, personnel, equipment, facilities, and processes can cope with interruptions).');
INSERT INTO `measureDescriptiontext` VALUES (541,358,1,'Compliance','');
INSERT INTO `measureDescriptiontext` VALUES (542,359,1,'Compliance with legal requirements','');
INSERT INTO `measureDescriptiontext` VALUES (543,360,1,'Identification of applicable legislation','All relevant statutory, regulatory and contractual requirements and the organization’s approach to meet these requirements should be explicitly defined, documented, and kept up to date for each information system and the organization.\n\nImplementation guidance\nThe specific controls and individual responsibilities to meet these requirements should be similarly\ndefined and documented.');
INSERT INTO `measureDescriptiontext` VALUES (544,361,1,'Intellectual property rights (IPR)','Appropriate procedures should be implemented to ensure compliance with legislative, regulatory, and contractual requirements on the use of material in respect of which there may be intellectual property rights and on the use of proprietary software products.\n\nImplementation guidance\nThe following guidelines should be considered to protect any material that may be considered intellectual property:\na) publishing an intellectual property rights compliance policy which defines the legal use of software and information products;\nb) acquiring software only through known and reputable sources, to ensure that copyright is not violated;\nc) maintaining awareness of policies to protect intellectual property rights, and giving notice of the intent to take disciplinary action against personnel breaching them;\nd) maintaining appropriate asset registers, and identifying all assets with requirements to protect intellectual property rights;\ne) maintaining proof and evidence of ownership of licenses, master disks, manuals, etc;\nf) implementing controls to ensure that any maximum number of users permitted is not exceeded;\ng) carrying out checks that only authorized software and licensed products are installed;\nh) providing a policy for maintaining appropriate licence conditions;\ni) providing a policy for disposing or transferring software to others;\nj) using appropriate audit tools;\nk) complying with terms and conditions for software and information obtained from public networks;\nl) not duplicating, converting to another format or extracting from commercial recordings (film, audio) other than permitted by copyright law;\nm) not copying in full or in part, books, articles, reports or other documents, other than permitted by copyright law.');
INSERT INTO `measureDescriptiontext` VALUES (545,362,1,'Protection of organizational records','Important records should be protected from loss, destruction and falsification, in accordance with statutory, regulatory, contractual, and business requirements.\n\nImplementation guidance\nRecords should be categorized into record types, e.g. accounting records, database records, transaction logs, audit logs, and operational procedures, each with details of retention periods and type of storage media, e.g. paper, microfiche, magnetic, optical. Any related cryptographic keying material and programs associated with encrypted archives or digital signatures (see 12.3), should also be stored to enable decryption of the records for the length of time the records are retained. Consideration should be given to the possibility of deterioration of media used for storage of records. Storage and handling procedures should be implemented in accordance with manufacturer’s\nrecommendations. For long term storage, the use of paper and microfiche should be considered. Where electronic storage media are chosen, procedures to ensure the ability to access data (both media and format readability) throughout the retention period should be included, to safeguard against loss due to future technology change. Data storage systems should be chosen such that required data can be retrieved in an acceptable\ntimeframe and format, depending on the requirements to be fulfilled. The system of storage and handling should ensure clear identification of records and of their retention period as defined by national or regional legislation or regulations, if applicable. This system should permit appropriate destruction of records after that period if they are not needed by the organization. To meet these record safeguarding objectives, the following steps should be taken within an organization:\na) guidelines should be issued on the retention, storage, handling, and disposal of records and information;\nb) a retention schedule should be drawn up identifying records and the period of time for which they should be retained;\nc) an inventory of sources of key information should be maintained;\nd) appropriate controls should be implemented to protect records and information from loss, destruction, and falsification.');
INSERT INTO `measureDescriptiontext` VALUES (546,363,1,'Data protection and privacy of personal information','Data protection and privacy should be ensured as required in relevant legislation, regulations, and, if applicable, contractual clauses.\n\nImplementation guidance\nAn organizational data protection and privacy policy should be developed and implemented. This policy should be communicated to all persons involved in the processing of personal information. Compliance with this policy and all relevant data protection legislation and regulations requires appropriate management structure and control. Often this is best achieved by the appointment of a person responsible, such as a data protection officer, who should provide guidance to managers, users, and service providers on their individual responsibilities and the specific procedures that should be followed. Responsibility for handling personal information and ensuring awareness of the data protection principles should be dealt with in accordance with relevant legislation and regulations. Appropriate technical and organizational measures to protect personal information should be implemented.');
INSERT INTO `measureDescriptiontext` VALUES (547,364,1,'Prevention of misuse of information processing facilities','Users should be deterred from using information processing facilities for unauthorized purposes.\n\nImplementation guidance\nManagement should approve the use of information processing facilities. Any use of these facilities for non-business purposes without management approval (see 6.1.4), or for any unauthorized purposes, should be regarded as  improper use of the facilities. If any unauthorized activity is identified by monitoring or other means, this activity should be brought to the attention of the individual manager concerned for consideration of appropriate disciplinary and/or legal action. Legal advice should be taken before implementing monitoring procedures. \nAll users should be aware of the precise scope of their permitted access and of the monitoring in place to detect  unauthorized use. This can be achieved by giving users written authorization, a copy of which should be signed by the user and securely retained by the organization. Employees of an organization, contractors, and third party users should be advised that no access will be permitted except that which is authorized. At log-on, a warning message should be presented to indicate that the information processing facility being entered is owned by the organization and that unauthorized access is not permitted. The user has to acknowledge and react appropriately to the message on the screen to continue with the log-on process (see 11.5.1).');
INSERT INTO `measureDescriptiontext` VALUES (548,365,1,'Regulation of cryptographic controls','Cryptographic controls should be used in compliance with all relevant agreements, laws, and regulations.\n\nImplementation guidance\nThe following items should be considered for compliance with the relevant agreements, laws, and regulations:\na) restrictions on import and/or export of computer hardware and software for performing cryptographic functions;\nb) restrictions on import and/or export of computer hardware and software which is designed to have cryptographic functions added to it;\nc) restrictions on the usage of encryption;\nd) mandatory or discretionary methods of access by the countries’ authorities to information encrypted by hardware or software to provide confidentiality of content.');
INSERT INTO `measureDescriptiontext` VALUES (549,366,1,'Compliance with security policies and standards, and technical compliance','');
INSERT INTO `measureDescriptiontext` VALUES (550,367,1,'Compliance with security policies and standards','Managers should ensure that all security procedures within their area of responsibility are carried out correctly to achieve compliance with security policies and standards.\n\nImplementation guidance\nManagers should regularly review the compliance of information processing within their area of responsibility with the appropriate security policies, standards, and any other security requirements.\nIf any non-compliance is found as a result of the review, managers should:\na) determine the causes of the non-compliance;\nb) evaluate the need for actions to ensure that non-compliance do not recur;\nc) determine and implement appropriate corrective action;\nd) review the corrective action taken.');
INSERT INTO `measureDescriptiontext` VALUES (551,368,1,'Technical compliance checking','Information systems should be regularly checked for compliance with security implementation standards.\n\nImplementation guidance\nTechnical compliance checking should be performed either manually (supported by appropriate software tools, if necessary) by an experienced system engineer, and/or with the assistance of automated tools, which generate a  technical report for subsequent interpretation by a technical specialist.\nIf penetration tests or vulnerability assessments are used, caution should be exercised as such activities could lead to a compromise of the security of the system. Such tests should be planned, documented and repeatable. Any technical compliance check should only be carried out by competent, authorized persons, or under the supervision of such persons.');
INSERT INTO `measureDescriptiontext` VALUES (552,369,1,'Information systems audit considerations','');
INSERT INTO `measureDescriptiontext` VALUES (553,370,1,'Information systems audit controls','Audit requirements and activities involving checks on operational systems should be carefully planned and agreed to minimize the risk of disruptions to business processes.\n\nImplementation guidance\nThe following guidelines should be observed:\na) audit requirements should be agreed with appropriate management;\nb) the scope of the checks should be agreed and controlled;\nc) the checks should be limited to read-only access to software and data;\nd) access other than read-only should only be allowed for isolated copies of system files,\nwhich should be erased when the audit is completed, or given appropriate protection if there is an obligation to keep such files under audit documentation requirements;\ne) resources for performing the checks should be explicitly identified and made available;\nf) requirements for special or additional processing should be identified and agreed;\ng) all access should be monitored and logged to produce a reference trail; the use of timestamped reference trails should be considered for critical data or systems;\nh) all procedures, requirements, and responsibilities should be documented;\ni) the person(s) carrying out the audit should be independent of the activities audited.');
INSERT INTO `measureDescriptiontext` VALUES (554,371,1,'Protection of information systems audit tools','Access to information systems audit tools should be protected to prevent any possible misuse or compromise.\n\nImplementation guidance\nInformation systems audit tools, e.g. software or data files, should be separated from development and operational systems and not held in tape libraries or user areas, unless given an appropriate level of additional protection.');
INSERT INTO `measureDescriptiontext` VALUES (555,184,2,'Appréciation et traitement du risque','');
INSERT INTO `measureDescriptiontext` VALUES (556,185,2,'Appréciation du risque lié à la sécurité','');
INSERT INTO `measureDescriptiontext` VALUES (557,186,2,'Appréciation du risque lié à la sécurité','Il convient de réaliser une analyse du risque de manière à identifier, quantifier et affecter des priorités aux risques en fonction des critères d’acceptation et des objectifs fixés par l’organisme. Il convient que les résultats de l’appréciation du risque permettent de définir de façon pertinente les actions à engager et les priorités en matière de management du risque lié à la sécurité de l’information, et facilitent le choix des mesures appropriées destinées à protéger l’information contre ce risque. Il peut être nécessaire de répéter le processus d’appréciation du risque et de sélection des mesures en vue de couvrir les différentes composantes de l’organisme ou des systèmes d’information.');
INSERT INTO `measureDescriptiontext` VALUES (558,187,2,'Traitement du risque lié à la sécurité','');
INSERT INTO `measureDescriptiontext` VALUES (559,188,2,'Traitement du risque lié à la sécurité','Pour chacun des risques identifiés à l’issue de la phase d’appréciation du risque, une décision relative au traitement du risque doit être prise. ,,,\n,,,,,,,,\nLes solutions envisageables sont les suivantes: ,,,,,,,,\na) application de mesures appropriées visant à réduire le risque,,,,,,,,\nb) acceptation des risques en connaissance de cause et avec objectivité, dans la mesure où ils sont acceptables au regard de la politique de l’organisme et des critères d’acceptation,,,,,,,\nc) annulation des risques en interdisant les actions susceptibles de les engendrer,,,,,,,,\nd) transfert des risques à des tiers, par exemple assureurs et fournisseurs. ,,,,,,,\n,,,,,,,,\nLorsque la décision du traitement du risque consiste à appliquer des mesures appropriées, il convient de sélectionner et de mettre en oeuvre ces mesures en fonction des exigences identifiées par une appréciation du risque.');
INSERT INTO `measureDescriptiontext` VALUES (560,189,2,'Politique de sécurité','');
INSERT INTO `measureDescriptiontext` VALUES (561,190,2,'Politique de sécurité de l’information','');
INSERT INTO `measureDescriptiontext` VALUES (562,191,2,'Document de politique de sécurité de l’information','Il convient qu’un document de politique de sécurité de l’information soit approuvé par la direction, puis publié et diffusé auprès de l’ensemble des salariés et des tiers concernés.');
INSERT INTO `measureDescriptiontext` VALUES (563,192,2,'Réexamen de la politique de sécurité de l’information','Pour garantir la pertinence, l’adéquation et l’efficacité de la politique de sécurité de l’information, il convient de réexaminer la politique à intervalles fixés préalablement ou en cas de changements majeurs.');
INSERT INTO `measureDescriptiontext` VALUES (564,193,2,'Organisation de la sécurité de l’information','');
INSERT INTO `measureDescriptiontext` VALUES (565,194,2,'Organisation interne','');
INSERT INTO `measureDescriptiontext` VALUES (566,195,2,'Engagement de la direction vis-à-vis de la sécurité de l’information','Il convient que la direction soutienne activement la politique de sécurité au sein de l’organisme au moyen de directives claires, d’un engagement franc, d’attribution de fonctions explicites et d’une reconnaissance des responsabilités liées à la sécurité de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (567,196,2,'Coordination de la sécurité de l’information','Il convient que les activités relatives à la sécurité de l’information soient coordonnées par des intervenants ayant des fonctions et des rôles appropriés représentatifs des différentes parties de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (568,197,2,'Attribution des responsabilités en matière de sécurité de l’information','Il convient de définir clairement toutes les responsabilités en matière de sécurité de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (569,198,2,'Système d’autorisation concernant les moyens de traitement de l’information','Il convient de définir et de mettre en oeuvre un système de gestion des autorisations pour chaque nouveau moyen de traitement de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (570,199,2,'Engagements de confidentialité','Il convient d’identifier et de réexaminer régulièrement les exigences en matière d’engagements de confidentialité ou de non-divulgation, conformément aux besoins de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (571,200,2,'Relations avec les autorités','Il convient de mettre en place des relations appropriées avec les autorités compétentes.');
INSERT INTO `measureDescriptiontext` VALUES (572,201,2,'Relations avec des groupes de spécialistes','Il convient d’entretenir des contacts appropriés avec des groupes de spécialistes, des forums spécialisés dans la sécurité et des associations professionnelles.');
INSERT INTO `measureDescriptiontext` VALUES (573,202,2,'Revue indépendante de la sécurité de l’information','Il convient de procéder à des revues régulières et indépendantes de l’approche retenue par l’organisme pour gérer et mettre en oeuvre sa sécurité (à savoir le suivi des objectifs de sécurité, les politiques, les procédures et les processus relatifs à la sécurité de l’information');
INSERT INTO `measureDescriptiontext` VALUES (574,203,2,'Tiers','');
INSERT INTO `measureDescriptiontext` VALUES (575,204,2,'Identification des risques provenant des tiers','Il convient d’identifier les risques pesant sur l’information et les moyens de traitement de l’organisme qui découlent d’activités impliquant des tiers, et de mettre en oeuvre des mesures appropriées avant d’accorder des accès.');
INSERT INTO `measureDescriptiontext` VALUES (576,205,2,'La sécurité et les clients','Tous les besoins de sécurité doivent être traités avant d’accorder aux clients l’accès à l’information ou aux actifs de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (577,206,2,'La sécurité dans les accords conclus avec des tiers','Il convient que les accords conclus avec des tiers qui portent sur l’accès, le traitement, la communication ou la gestion de l’information, ou des moyens de traitement de l’information de l’organisme, ou qui portent sur l’ajout de produits ou de services aux moyens de traitement de l’information, couvrent l’ensemble des exigences applicables en matière de sécurité.');
INSERT INTO `measureDescriptiontext` VALUES (578,207,2,'Gestion des actifs','');
INSERT INTO `measureDescriptiontext` VALUES (579,208,2,'Responsabilités relatives aux actifs','');
INSERT INTO `measureDescriptiontext` VALUES (580,209,2,'Inventaire des actifs','Il convient de clairement identifier tous les actifs, de réaliser et de gérer un inventaire de tous les actifs importants.');
INSERT INTO `measureDescriptiontext` VALUES (581,210,2,'Propriété des actifs','Il convient d’attribuer la propriété de chaque information et moyens de traitement de l’information à une partie définie de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (582,211,2,'Utilisation correcte des biens','Il convient d’identifier, de documenter et de mettre en oeuvre des règles permettant l’utilisation correcte de l’information et des biens associés aux moyens de traitement de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (583,212,2,'Classification des informations','');
INSERT INTO `measureDescriptiontext` VALUES (584,213,2,'Lignes directrices pour la classification','Il convient de classer les informations en termes de valeur, d’exigences légales, de sensibilité et de criticité.');
INSERT INTO `measureDescriptiontext` VALUES (585,214,2,'Marquage et manipulation de l’information','Il convient d’élaborer et de mettre en oeuvre un ensemble approprié de procédures pour le marquage et la manipulation de l’information, conformément au plan de classification adopté par l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (586,215,2,'Sécurité liée aux ressources humaines','');
INSERT INTO `measureDescriptiontext` VALUES (587,216,2,'Avant le recrutement','');
INSERT INTO `measureDescriptiontext` VALUES (588,217,2,'Rôles et responsabilités','Il convient de définir et de documenter les rôles et responsabilités en matière de sécurité des salariés, contractants et utilisateurs tiers, conformément à la politique de sécurité de l’information de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (589,218,2,'Sélection','Qu’il s’agisse de postulants, de contractants ou d’utilisateurs tiers, il convient que les vérifications des informations concernant tous les candidats soient réalisées conformément aux lois, aux règlements et à l’étique et qu’elles soient proportionnelles aux exigences métier, à la classification des informations accessibles et aux risques identifiés.');
INSERT INTO `measureDescriptiontext` VALUES (590,219,2,'Conditions d’embauche','Dans le cadre de leurs obligations contractuelles, il convient que les salariés, contractants et utilisateurs tiers se mettent d’accord sur les modalités du contrat d’embauche les liant et le signent. Il convient que ce contrat définisse les responsabilités de l’organisme et de l’autre partie quant à la sécurité de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (591,220,2,'Pendant la durée du contrat','');
INSERT INTO `measureDescriptiontext` VALUES (592,221,2,'Obligation d''appliquer les règles','Il convient que la direction demande aux salariés, contractants et utilisateurs tiers d’appliquer les règles de sécurité conformément aux politiques et procédures établies de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (593,222,2,'Sensibilisation, qualification et formations en matière de sécurité de l’information','Il convient que l’ensemble des salariés d’un organisme et, le cas échant, les contractants et utilisateurs tiers suivent une formation adaptée sur la sensibilisation et reçoivent régulièrement les mises à jour des politiques et procédures de l’organisme, pertinentes pour leurs fonctions.');
INSERT INTO `measureDescriptiontext` VALUES (594,223,2,'Processus disciplinaire','Il convient d’élaborer un processus disciplinaire formel pour les salariés ayant enfreint les règles de sécurité.');
INSERT INTO `measureDescriptiontext` VALUES (595,224,2,'Fin ou modification de contrat','');
INSERT INTO `measureDescriptiontext` VALUES (596,225,2,'Responsabilités en fin de contrat','Il convient que les responsabilités relatives aux fins ou aux modifications de contrats soient clairement définies et attribuées.');
INSERT INTO `measureDescriptiontext` VALUES (597,226,2,'Restitution des biens','Il convient que tous les salariés, contractants et utilisateurs tiers restituent la totalité des biens de l’organisme qu’ils ont en leur possession à la fin de leur période d’emploi, contrat ou accord.');
INSERT INTO `measureDescriptiontext` VALUES (598,227,2,'Retrait des droits d’accès','Il convient que les droits d’accès de l’ensemble des salariés, contractants et utilisateurs tiers à l’information et aux moyens de traitement de l’information soient supprimés à la fin de leur période d’emploi, ou modifiés en cas de modification du contrat ou de l’accord.');
INSERT INTO `measureDescriptiontext` VALUES (599,228,2,'Sécurité physique et environnementale','');
INSERT INTO `measureDescriptiontext` VALUES (600,229,2,'Zones sécurisées','');
INSERT INTO `measureDescriptiontext` VALUES (601,230,2,'Périmètre de sécurité physique','Il convient de protéger les zones contenant des informations et des moyens de traitement de l’information par des périmètres de sécurité (obstacles tels que des murs, des portes avec un contrôle d’accès par cartes, ou des bureaux de réception avec personnel d’accueil).');
INSERT INTO `measureDescriptiontext` VALUES (602,231,2,'Contrôles physiques des accès','Il convient de protéger les zones sécurisées par des contrôles à l’entrée adéquats pour s’assurer que seul le personnel habilité soit admis.');
INSERT INTO `measureDescriptiontext` VALUES (603,232,2,'Sécurisation des bureaux, des salles et des équipements','Il convient de concevoir et d’appliquer des mesures de sécurité physique pour les bureaux, les salles et les équipements.');
INSERT INTO `measureDescriptiontext` VALUES (604,233,2,'Protection contre les menaces extérieures et environnementales','Il convient de concevoir et d’appliquer des mesures de protection physique contre les dommages causés par les incendies, les inondations, les tremblements de terre, les explosions, les troubles civils et autres formes de catastrophes naturelles ou de sinistres provoqués par l’homme.');
INSERT INTO `measureDescriptiontext` VALUES (605,234,2,'Travail dans les zones sécurisées','Il convient de concevoir et d’appliquer des mesures de protection physique et des directives pour le travail en zone sécurisée.');
INSERT INTO `measureDescriptiontext` VALUES (606,235,2,'Zones d’accès public, de livraison et de chargement','Il convient de contrôler les points d’accès tels que les zones de livraison/chargement et les autres points par lesquels des personnes non habilitées peuvent pénétrer dans les locaux. Il convient également d’isoler les points d’accès, si possible, des moyens de traitement de l’information, de façon à éviter les accès non autorisés.');
INSERT INTO `measureDescriptiontext` VALUES (607,236,2,'Sécurité du matériel','');
INSERT INTO `measureDescriptiontext` VALUES (608,237,2,'Choix de l’emplacement et protection du matériel','Il convient de situer et de protéger le matériel de manière à réduire les risques de menaces et de dangers environnementaux et les possibilités d’accès non autorisé.');
INSERT INTO `measureDescriptiontext` VALUES (609,238,2,'Services généraux','Il convient de protéger le matériel des coupures de courant et autres perturbations dues à une défaillance des services généraux.');
INSERT INTO `measureDescriptiontext` VALUES (610,239,2,'Sécurité du câblage','Il convient de protéger les câbles électriques ou de télécommunications transportant des données contre toute interception ou dommage.');
INSERT INTO `measureDescriptiontext` VALUES (611,240,2,'Maintenance du matériel','Il convient d’entretenir le matériel correctement pour garantir sa disponibilité permanente et son intégrité.');
INSERT INTO `measureDescriptiontext` VALUES (612,241,2,'Sécurité du matériel hors des locaux','Il convient d’appliquer la sécurité au matériel utilisé hors des locaux de l’organisme en tenant compte des différents risques associés au travail hors site.');
INSERT INTO `measureDescriptiontext` VALUES (613,242,2,'Mise au rebut ou recyclage sécurisé(e) du matériel','Il convient de vérifier tout le matériel contenant des supports de stockage soient vérifiées pour s’assurer que toute donnée sensible a bien été supprimée et que tout logiciel sous licence a bien été désinstallé ou écrasé de façon sécurisée, avant sa mise au rebut.');
INSERT INTO `measureDescriptiontext` VALUES (614,243,2,'Sortie d’un bien','Il convient de ne pas sortir un matériel, des informations ou des logiciels des locaux de l’organisme sans autorisation préalable.');
INSERT INTO `measureDescriptiontext` VALUES (615,244,2,'Gestion de l’exploitation et des télécommunications','');
INSERT INTO `measureDescriptiontext` VALUES (616,245,2,'Procédures et responsabilités liées à l’exploitation','');
INSERT INTO `measureDescriptiontext` VALUES (617,246,2,'Procédures d’exploitation documentées','Il convient que les procédures d’exploitation soient documentées, tenues à jour et disponibles pour tous les utilisateurs concernés.');
INSERT INTO `measureDescriptiontext` VALUES (618,247,2,'Gestion des modifications','Il convient de contrôler les changements apportés aux systèmes et moyens de traitement de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (619,248,2,'Séparation des tâches','Il convient de séparer les tâches et les domaines de responsabilité pour réduire les occasions de modification ou de mauvais usage non autorisé(e) ou involontaire des biens de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (620,249,2,'Séparation des équipements de développement, de test et d’exploitation','Il convient de séparer les équipements de développement, de test et d’exploitation pour réduire les risques d’accès ou de changements non autorisés dans le système en exploitation.');
INSERT INTO `measureDescriptiontext` VALUES (621,250,2,'Gestion de la prestation de service par un tiers','');
INSERT INTO `measureDescriptiontext` VALUES (622,251,2,'Prestation de service','Il convient de s’assurer que les mesures de sécurité, les définitions du service et les niveaux de prestation prévus dans l’accord de prestation de service tiers sont mis en oeuvre, appliqués et tenus à jour par le tiers.');
INSERT INTO `measureDescriptiontext` VALUES (623,252,2,'Surveillance et réexamen des services tiers','Il convient que les services, rapports et enregistrements fournis par les tiers soient régulièrement contrôlés et réexaminés, et que des audits soient régulièrement réalisés.');
INSERT INTO `measureDescriptiontext` VALUES (624,253,2,'Gestion des modifications dans les services tiers','Il convient de gérer les changements effectués dans la prestation de service, comprenant le maintien et l’amélioration des politiques, procédures et mesures existant en matière de sécurité de l’information, en tenant compte de la criticité des systèmes et processus de gestion concernés et de la réévaluation du risque.');
INSERT INTO `measureDescriptiontext` VALUES (625,254,2,'Planification et acceptation du système','');
INSERT INTO `measureDescriptiontext` VALUES (626,255,2,'Dimensionnement','Il convient de surveiller et d’ajuster au plus près l’utilisation des ressources et il convient de faire des projections sur les dimensionnements futurs pour assurer les performances requises pour le système.');
INSERT INTO `measureDescriptiontext` VALUES (627,256,2,'Acceptation du système','Il convient de fixer les critères d’acceptation pour les nouveaux systèmes d’information, les nouvelles versions et les mises à niveau, et de réaliser les tests adaptés du (des) système(s) au moment du développement et préalablement à leur acceptation.');
INSERT INTO `measureDescriptiontext` VALUES (628,257,2,'Protection contre les codes malveillant et mobile','');
INSERT INTO `measureDescriptiontext` VALUES (629,258,2,'Mesures contre les codes malveillants','Il convient de mettre en oeuvre des mesures de détection, de prévention et de récupération pour se protéger des codes malveillants ainsi que des procédures appropriées de sensibilisation des utilisateurs.');
INSERT INTO `measureDescriptiontext` VALUES (630,259,2,'Mesures contre le code mobile','Lorsque l’utilisation de code mobile est autorisée, il convient que la configuration garantisse que le code mobile fonctionne selon une politique de sécurité clairement définie et il convient d’empêcher tout code mobile non autorisé de s’exécuter.');
INSERT INTO `measureDescriptiontext` VALUES (631,260,2,'Sauvegarde','');
INSERT INTO `measureDescriptiontext` VALUES (632,261,2,'Sauvegarde des informations','Il convient de réaliser des copies de sauvegarde des informations et logiciels et de les soumettre régulièrement à essai conformément à la politique de sauvegarde convenue.');
INSERT INTO `measureDescriptiontext` VALUES (633,262,2,'Gestion de la sécurité des réseaux','');
INSERT INTO `measureDescriptiontext` VALUES (634,263,2,'Mesures sur les réseaux','Il convient de gérer et de contrôler les réseaux de manière adéquate pour qu’ils soient protégés des menaces et de maintenir la sécurité des systèmes et des applications utilisant le réseau, notamment les informations en transit.');
INSERT INTO `measureDescriptiontext` VALUES (635,264,2,'Sécurité des services réseau','Pour tous les services réseau, il convient d’identifier les fonctions réseau, les niveaux de service et les exigences de gestion, et de les intégrer dans tout accord sur les services réseau, qu’ils soient fournis en interne ou en externe.');
INSERT INTO `measureDescriptiontext` VALUES (636,265,2,'Manipulation des supports','');
INSERT INTO `measureDescriptiontext` VALUES (637,266,2,'Gestion des supports amovibles','Il convient de mettre en place des procédures pour la gestion des supports amovibles.');
INSERT INTO `measureDescriptiontext` VALUES (638,267,2,'Mise au rebut des supports','Il convient de mettre au rebut de façon sûre les supports qui ne servent plus, en suivant des procédures formelles.');
INSERT INTO `measureDescriptiontext` VALUES (639,268,2,'Procédures de manipulation des informations','Il convient d’établir des procédures de manipulation et de stockage des informations pour protéger ces informations d’une divulgation non autorisée ou d’un mauvais usage.');
INSERT INTO `measureDescriptiontext` VALUES (640,269,2,'Sécurité de la documentation système','Il convient de protéger la documentation système contre les accès non autorisés.');
INSERT INTO `measureDescriptiontext` VALUES (641,270,2,'Échange des informations','');
INSERT INTO `measureDescriptiontext` VALUES (642,271,2,'Politiques et procédures d’échange des informations','Il convient de mettre en place des politiques, procédures et mesures d’échange formelles pour protéger les échanges d’informations transitant par tous types d’équipements de télécommunication.');
INSERT INTO `measureDescriptiontext` VALUES (643,272,2,'Accords d’échange','Il convient de conclure des accords pour l’échange d’informations et de logiciels entre l’organisme et la partie externe.');
INSERT INTO `measureDescriptiontext` VALUES (644,273,2,'Supports physiques en transit','Il convient de protéger les supports contenant des informations contre les accès non autorisés, le mauvais usage ou l’altération lors du transport hors des limites physiques de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (645,274,2,'Messagerie électronique','Il convient de protéger de manière adéquate les informations transitant par la messagerie électronique.');
INSERT INTO `measureDescriptiontext` VALUES (646,275,2,'Systèmes d’information d’entreprise','Il convient d’élaborer et de mettre en oeuvre des politiques et procédures pour protéger l’information liée à l’interconnexion de systèmes d’informations d’entreprise.');
INSERT INTO `measureDescriptiontext` VALUES (647,276,2,'Services de commerce électronique','');
INSERT INTO `measureDescriptiontext` VALUES (648,277,2,'Commerce électronique','Il convient de protéger l’information transitant par le commerce électronique transmise sur les réseaux publics contre les activités frauduleuses, les litiges sur les contrats et la divulgation et la modification non autorisées.');
INSERT INTO `measureDescriptiontext` VALUES (649,278,2,'Transactions en ligne','Il convient de protéger les informations transitant par les transactions en ligne pour empêcher la transmission incomplète, les erreurs d’acheminement, la modification non autorisée, la divulgation non autorisée, la duplication non autorisée du message ou la réémission.');
INSERT INTO `measureDescriptiontext` VALUES (650,279,2,'Informations à disposition du public','Il convient de protéger l’intégrité des informations mises à disposition sur un système accessible au public pour empêcher toute modification non autorisée.');
INSERT INTO `measureDescriptiontext` VALUES (651,280,2,'Surveillance','');
INSERT INTO `measureDescriptiontext` VALUES (652,281,2,'Rapport d’audit','Il convient que les rapports d’audit, qui enregistrent les activités des utilisateurs, les exceptions et les événements liés à la sécurité soient produits et conservés pendant une période préalablement définie afin de faciliter les investigations ultérieures et la surveillance du contrôle d’accès.');
INSERT INTO `measureDescriptiontext` VALUES (653,282,2,'Surveillance de l’exploitation du système','Il convient d’établir des procédures permettant de surveiller l’utilisation des moyens de traitement de l’information et de réexaminer périodiquement les résultats des activités de surveillance.');
INSERT INTO `measureDescriptiontext` VALUES (654,283,2,'Protection des informations journalisées','Il convient de protéger les équipements de journalisation et les informations journalisées contre le sabotage et les accès non autorisés.');
INSERT INTO `measureDescriptiontext` VALUES (655,284,2,'Journal administrateur et journal des opérations','Il convient de journaliser les activités de l’administrateur système et de l’opérateur système.');
INSERT INTO `measureDescriptiontext` VALUES (656,285,2,'Rapports de défaut','Il convient de journaliser et d’analyser les éventuels défauts et de prendre les mesures appropriées.');
INSERT INTO `measureDescriptiontext` VALUES (657,286,2,'Synchronisation des horloges','Il convient de synchroniser les horloges des différents systèmes de traitement de l’information d’un organisme ou d’un domaine de sécurité à l’aide d’une source de temps précise et préalablement définie.');
INSERT INTO `measureDescriptiontext` VALUES (658,287,2,'Contrôle d’accès','');
INSERT INTO `measureDescriptiontext` VALUES (659,288,2,'Exigences métier relatives au contrôle d’accès','');
INSERT INTO `measureDescriptiontext` VALUES (660,289,2,'Politique de contrôle d’accès','Il convient d’établir, de documenter et de réexaminer une politique de contrôle d’accès sur la base des exigences d’exploitation et de sécurité.');
INSERT INTO `measureDescriptiontext` VALUES (661,290,2,'Gestion de l’accès utilisateur','');
INSERT INTO `measureDescriptiontext` VALUES (662,291,2,'Enregistrement des utilisateurs','Il convient de définir une procédure formelle d’enregistrement et de désinscription des utilisateurs destinée à accorder et à supprimer l’accès à tous les systèmes et services d’information.');
INSERT INTO `measureDescriptiontext` VALUES (663,292,2,'Gestion des privilèges','Il convient de restreindre et de contrôler l’attribution et l’utilisation des privilèges.');
INSERT INTO `measureDescriptiontext` VALUES (664,293,2,'Gestion du mot de passe utilisateur','Il convient que l’attribution de mots de passe soit réalisée dans le cadre d’un processus formel.');
INSERT INTO `measureDescriptiontext` VALUES (665,294,2,'Réexamen des droits d’accès utilisateurs','Il convient que la direction revoie les droits d’accès utilisateurs à intervalles réguliers par le biais d’un processus formel.');
INSERT INTO `measureDescriptiontext` VALUES (666,295,2,'Responsabilités utilisateurs','');
INSERT INTO `measureDescriptiontext` VALUES (667,296,2,'Utilisation du mot de passe','Il convient de demander aux utilisateurs de respecter les bonnes pratiques de sécurité lors de la sélection et de l’utilisation de mots de passe.');
INSERT INTO `measureDescriptiontext` VALUES (668,297,2,'Matériel utilisateur laissé sans surveillance','Il convient que les utilisateurs s’assurent que tout matériel laissé sans surveillance est doté d’un dispositif de protection approprié.');
INSERT INTO `measureDescriptiontext` VALUES (669,298,2,'Politique du bureau propre et de l’écran vide','Il convient d’adopter une politique du bureau propre pour les documents papier et les supports de stockage amovibles, et une politique de l’écran vide pour les moyens de traitement de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (670,299,2,'Contrôle d’accès au réseau','');
INSERT INTO `measureDescriptiontext` VALUES (671,300,2,'Politique relative à l’utilisation des services en réseau','Il convient que les utilisateurs aient uniquement accès aux services pour lesquels ils ont spécifiquement reçu une autorisation.');
INSERT INTO `measureDescriptiontext` VALUES (672,301,2,'Authentification de l’utilisateur pour les connexions externes','Il convient d’utiliser des méthodes d’authentification appropriées pour contrôler l’accès d’utilisateurs distants.');
INSERT INTO `measureDescriptiontext` VALUES (673,302,2,'Identification des matériels en réseau','Il convient de considérer l’identification automatique de matériels comme un moyen d’authentification des connexions à partir de lieux et matériels spécifiques.');
INSERT INTO `measureDescriptiontext` VALUES (674,303,2,'Protection des ports de diagnostic et de configuration à distance','Il convient de contrôler l’accès physique et logique aux ports de diagnostic et de configuration à distance.');
INSERT INTO `measureDescriptiontext` VALUES (675,304,2,'Cloisonnement des réseaux','Il convient que les groupes de services d’information, d’utilisateurs et de systèmes d’information soient séparés sur le réseau.');
INSERT INTO `measureDescriptiontext` VALUES (676,305,2,'Mesure relative à la connexion réseau','Pour les réseaux partagés, en particulier les réseaux qui s’étendent au-delà des limites de l’organisme, il convient de restreindre la capacité de connexion réseau des utilisateurs, conformément à la politique de contrôle d’accès et les exigences relatives aux applications de gestion (voir 11.1).');
INSERT INTO `measureDescriptiontext` VALUES (677,306,2,'Contrôle du routage réseau','Il convient de mettre en oeuvre des mesures du routage des réseaux afin d’éviter que les connexions réseau et les flux d’informations ne portent atteinte à la politique de contrôle d’accès des applications de gestion.');
INSERT INTO `measureDescriptiontext` VALUES (678,307,2,'Contrôle d’accès au système d’exploitation','');
INSERT INTO `measureDescriptiontext` VALUES (679,308,2,'Ouverture de sessions sécurisées','Il convient que l’accès aux systèmes d’exploitation soit soumis à une procédure sécurisée d’ouverture de session.');
INSERT INTO `measureDescriptiontext` VALUES (680,309,2,'Identification et authentification de l’utilisateur','Il convient d’attribuer à chaque utilisateur un identifiant unique et exclusif et de choisir une technique d’authentification permettant de vérifier l’identité déclarée par l’utilisateur.');
INSERT INTO `measureDescriptiontext` VALUES (681,310,2,'Système de gestion des mots de passe','Il convient que les systèmes qui gèrent les mots de passe soient interactifs et fournissent des mots de passe de qualité.');
INSERT INTO `measureDescriptiontext` VALUES (682,311,2,'Emploi des utilitaires système','Il convient de limiter et de contrôler étroitement l’emploi des programmes utilitaires permettant de contourner les mesures d’un système ou d’une application.');
INSERT INTO `measureDescriptiontext` VALUES (683,312,2,'Déconnexion automatique des sessions inactives','Il convient que les sessions inactives soient déconnectées après une période d’inactivité définie.');
INSERT INTO `measureDescriptiontext` VALUES (684,313,2,'Limitation du temps de connexion','Il convient de restreindre les temps de connexion afin d’apporter un niveau de sécurité supplémentaire aux applications à haut risque.');
INSERT INTO `measureDescriptiontext` VALUES (685,314,2,'Contrôle d’accès aux applications et à l’information','');
INSERT INTO `measureDescriptiontext` VALUES (686,315,2,'Assistance technique avec accès restreint','Pour les utilisateurs et le personnel chargé de l’assistance technique, il convient de restreindre l’accès aux informations et aux fonctions applicatives conformément à la politique de contrôle d’accès.');
INSERT INTO `measureDescriptiontext` VALUES (687,316,2,'Isolement des systèmes sensibles','Il convient que les systèmes sensibles disposent d’un environnement informatique dédié (isolé).');
INSERT INTO `measureDescriptiontext` VALUES (688,317,2,'Informatique mobile et télétravail','');
INSERT INTO `measureDescriptiontext` VALUES (689,318,2,'Informatique mobile et télécommunications','Il convient de mettre en place une procédure formelle et des mesures de sécurité appropriées pour assurer une protection contre le risque lié à l’utilisation d’appareils informatiques et de communication mobiles.');
INSERT INTO `measureDescriptiontext` VALUES (690,319,2,'Télétravail','Il convient d’élaborer et de mettre en oeuvre une politique, des procédures et des programmes opérationnels spécifiques au télétravail.');
INSERT INTO `measureDescriptiontext` VALUES (691,320,2,'Acquisition, développement et maintenance des systèmes d’information','');
INSERT INTO `measureDescriptiontext` VALUES (692,321,2,'Exigences de sécurité applicables aux systèmes d’information','');
INSERT INTO `measureDescriptiontext` VALUES (693,322,2,'Analyse et spécification des exigences de sécurité','Il convient que les exigences métier relatives aux nouveaux systèmes d’information ou que les améliorations apportées aux systèmes d’information existants spécifient les exigences de sécurité.');
INSERT INTO `measureDescriptiontext` VALUES (694,323,2,'Bon fonctionnement des applications','');
INSERT INTO `measureDescriptiontext` VALUES (695,324,2,'Validation des données d’entrée','Il convient de valider les données entrées dans les applications afin de vérifier si elles sont correctes et appropriées.');
INSERT INTO `measureDescriptiontext` VALUES (696,325,2,'Mesure relative au traitement interne','Il convient d’inclure des mesures de validation dans les applications afin de détecter les éventuelles altérations de l’information dues à des erreurs de traitement ou des actes délibérés.');
INSERT INTO `measureDescriptiontext` VALUES (697,326,2,'Intégrité des messages','Il convient d’identifier les exigences relatives à l’authentification et à la protection de l’intégrité des messages. Il convient également d’identifier et de mettre en oeuvre les mesures appropriées.');
INSERT INTO `measureDescriptiontext` VALUES (698,327,2,'Validation des données de sortie','Il convient de valider les données de sortie d’une application pour vérifier que le traitement des informations stockées est correct et adapté aux circonstances.');
INSERT INTO `measureDescriptiontext` VALUES (699,328,2,'Mesures cryptographiques','');
INSERT INTO `measureDescriptiontext` VALUES (700,329,2,'Politique d’utilisation des mesures cryptographiques','Il convient d’élaborer et de mettre en oeuvre une politique d’utilisation des mesures cryptographiques en vue de protéger l’information.');
INSERT INTO `measureDescriptiontext` VALUES (701,330,2,'Gestion des clés','Il convient qu’une procédure de gestion des clés vienne à l’appui de la politique de l’organisme en matière de chiffrement.');
INSERT INTO `measureDescriptiontext` VALUES (702,331,2,'Sécurité des fichiers système','');
INSERT INTO `measureDescriptiontext` VALUES (703,332,2,'Mesure relative aux logiciels en exploitation','Il convient de mettre des procédures en place pour contrôler l’installation du logiciel sur les systèmes en exploitation.');
INSERT INTO `measureDescriptiontext` VALUES (704,333,2,'Protection des données système d’essai','Il convient que les données d’essai soient sélectionnées avec soin, protégées et contrôlées.');
INSERT INTO `measureDescriptiontext` VALUES (705,334,2,'Contrôle d’accès au code source du programme','Il convient de restreindre l’accès au code source du programme.');
INSERT INTO `measureDescriptiontext` VALUES (706,335,2,'Sécurité en matière de développement et d’assistance technique','');
INSERT INTO `measureDescriptiontext` VALUES (707,336,2,'Procédures de contrôle des modifications','Il convient de contrôler la mise en oeuvre des modifications par le biais de procédures formelles.');
INSERT INTO `measureDescriptiontext` VALUES (708,337,2,'Réexamen technique des applications après modification du système d’exploitation','Lorsque des modifications sont apportées aux systèmes d’exploitation, il convient de réexaminer et de soumettre à essai les applications critiques de gestion afin de vérifier l’absence de tout effet indésirable sur l’activité ou sur la sécurité.');
INSERT INTO `measureDescriptiontext` VALUES (709,338,2,'Restrictions relatives à la modification des progiciels','Il convient de ne pas encourager la modification des progiciels et de se limiter aux changements nécessaires. Il convient également d’exercer un contrôle strict sur ces modifications.');
INSERT INTO `measureDescriptiontext` VALUES (710,339,2,'Fuite d’informations','Il convient d’empêcher toute possibilité de fuite d’informations.');
INSERT INTO `measureDescriptiontext` VALUES (711,340,2,'Externalisation du développement logiciel','Il convient que l’organisme encadre et contrôle le développement logiciel externalisé.');
INSERT INTO `measureDescriptiontext` VALUES (712,341,2,'Gestion des vulnérabilités techniques','');
INSERT INTO `measureDescriptiontext` VALUES (713,342,2,'Mesure relative aux vulnérabilités techniques','Il convient d’être informé en temps voulu de toute vulnérabilité technique des systèmes d’information en exploitation, d’évaluer l’exposition de l’organisme audites vulnérabilités et d’entreprendre les actions appropriées pour traiter le risque associé.');
INSERT INTO `measureDescriptiontext` VALUES (714,343,2,'Gestion des incidents liés à la sécurité de l’information','');
INSERT INTO `measureDescriptiontext` VALUES (715,344,2,'Signalement des événements et des failles liés à la sécurité de l’information','');
INSERT INTO `measureDescriptiontext` VALUES (716,345,2,'Signalement des événements liés à la sécurité de l’information','Il convient de signaler, dans les meilleurs délais, les événements liés à la sécurité de l’information, par les voies hiérarchiques appropriées.');
INSERT INTO `measureDescriptiontext` VALUES (717,346,2,'Signalement des failles de sécurité','Il convient de demander à tous les salariés, contractants et utilisateurs tiers des systèmes et services d’information de noter et de signaler toute faille de sécurité observée ou soupçonnée dans les systèmes ou services.');
INSERT INTO `measureDescriptiontext` VALUES (718,347,2,'Gestion des améliorations et incidents liés à la sécurité de l’information','');
INSERT INTO `measureDescriptiontext` VALUES (719,348,2,'Responsabilités et procédures','Il convient d’établir des responsabilités et des procédures permettant de garantir une réponse rapide, efficace et pertinente en cas d’incident lié à la sécurité de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (720,349,2,'Exploitation des incidents liés à la sécurité de l’information déjà survenus','Il convient de mettre en place des mécanismes permettant de quantifier et surveiller les différents types d’incidents liés à la sécurité de l’information ainsi que leur volume et les coûts associés.');
INSERT INTO `measureDescriptiontext` VALUES (721,350,2,'Collecte de preuves','Lorsqu’une action en justice civile ou pénale est engagée contre une personne physique ou un organisme, à la suite d’un incident lié à la sécurité de l’information, il convient de recueillir, conserver et présenter les informations conformément aux dispositions légales relatives à la présentation de preuves régissant la ou les juridiction(s) compétente(s).');
INSERT INTO `measureDescriptiontext` VALUES (722,351,2,'Gestion du plan de continuité de l’activité','');
INSERT INTO `measureDescriptiontext` VALUES (723,352,2,'Aspects de la sécurité de l’information en matière de gestion de la continuité de l’activité','');
INSERT INTO `measureDescriptiontext` VALUES (724,353,2,'Intégration de la sécurité de l’information dans le processus de gestion du plan de continuité de l’activité','Il convient d’élaborer et de gérer un processus de continuité de l’activité dans l’ensemble de l’organisme qui satisfait aux exigences en matière de sécurité de l’information requises pour la continuité de l’activité de l’organisme.');
INSERT INTO `measureDescriptiontext` VALUES (725,354,2,'Continuité de l’activité et appréciation du risque','Il convient d’identifier les événements pouvant être à l’origine d’interruptions des processus métier tout comme la probabilité et l’impact de telles interruptions et leurs conséquences pour la sécurité de l’information.');
INSERT INTO `measureDescriptiontext` VALUES (726,355,2,'Élaboration et mise en oeuvre des plans de continuité intégrant la sécurité de l’information','Il convient d’élaborer et de mettre en oeuvre des plans destinés à maintenir ou à restaurer l’exploitation et à assurer la disponibilité des informations au niveau et dans les délais requis suite à une interruption ou une panne affectant les processus métier cruciaux.');
INSERT INTO `measureDescriptiontext` VALUES (727,356,2,'Cadre de la planification de la continuité de l’activité','Il convient gérer un cadre unique pour les plans de continuité de l’activité afin de garantir la cohérence de l’ensemble des plans, de satisfaire de manière constante aux exigences en matière de sécurité de l’information et d’identifier les priorités en matière de mise à l’essai et de maintenance.');
INSERT INTO `measureDescriptiontext` VALUES (728,357,2,'Mise à l’essai, gestion et appréciation constante des plans de continuité de l’activité','Il convient de soumettre à essai et de mettre à jour régulièrement les plans de continuité de l’activité afin de s’assurer qu’ils sont actualisés et efficaces.');
INSERT INTO `measureDescriptiontext` VALUES (729,358,2,'Conformité','');
INSERT INTO `measureDescriptiontext` VALUES (730,359,2,'Conformité avec les exigences légales','');
INSERT INTO `measureDescriptiontext` VALUES (731,360,2,'Identification de la législation en vigueur','Pour chaque système d’information et pour l’organisme, il convient de définir, documenter et mettre à jour explicitement toutes les exigences légales, réglementaires et contractuelles en vigueur, ainsi que la procédure utilisée par l’organisme pour satisfaire à ces exigences.');
INSERT INTO `measureDescriptiontext` VALUES (732,361,2,'Droits de propriété intellectuelle','Il convient de mettre en oeuvre des procédures appropriées visant à garantir la conformité avec les exigences légales, réglementaires et contractuelles concernant l’utilisation du matériel pouvant être soumis à des droits de propriété intellectuelle et l’utilisation des logiciels propriétaires.');
INSERT INTO `measureDescriptiontext` VALUES (733,362,2,'Protection des enregistrements de l’organisme','Il convient de protéger les enregistrements importants de la perte, destruction et falsification conformément aux exigences légales, réglementaires et aux exigences métier.');
INSERT INTO `measureDescriptiontext` VALUES (734,363,2,'Protection des données et confidentialité des informations relatives à la vie privée','Il convient de garantir la protection et la confidentialité des données telles que l’exigent la législation ou les réglementations applicables, et les clauses contractuelles le cas échéant.');
INSERT INTO `measureDescriptiontext` VALUES (735,364,2,'Mesure préventive à l’égard du mauvais usage des moyens de traitement de l’information','Il convient de dissuader les utilisateurs de toute utilisation de moyens de traitement de l’information à des fins illégales.');
INSERT INTO `measureDescriptiontext` VALUES (736,365,2,'Réglementation relative aux mesures cryptographiques','Il convient de prendre des mesures cryptographiques conformément aux accords, lois et réglementations applicables.');
INSERT INTO `measureDescriptiontext` VALUES (737,366,2,'Conformité avec les politiques et normes de sécurité et conformité technique','');
INSERT INTO `measureDescriptiontext` VALUES (738,367,2,'Conformité avec les politiques et les normes de sécurité','Il convient que les responsables s’assurent de l’exécution correcte de l’ensemble des procédures de sécurité placées sous leur responsabilité en vue de garantir leur conformité avec les politiques et normes de sécurité.');
INSERT INTO `measureDescriptiontext` VALUES (739,368,2,'Vérification de la conformité technique','Il convient de vérifier régulièrement la conformité des systèmes d’information avec les normes relatives à la mise en oeuvre de la sécurité.');
INSERT INTO `measureDescriptiontext` VALUES (740,369,2,'Prises en compte de l’audit du système d’information','');
INSERT INTO `measureDescriptiontext` VALUES (741,370,2,'Contrôles de l’audit du système d’information','Il convient que les exigences d’audit et les activités impliquant des contrôles des systèmes en exploitation soient planifiées de manière précise et qu’elles soient le résultat d’un accord afin de réduire le plus possible le risque de perturbations des processus métier.');
INSERT INTO `measureDescriptiontext` VALUES (742,371,2,'Protection des outils d’audit du système d’information','Il convient de protéger l’accès aux outils d’audit du système d’information afin d’empêcher tous mauvais usage ou compromission éventuels.');
INSERT INTO `measureDescriptiontext` VALUES (743,372,1,'Risk Assessment and treatment','');
INSERT INTO `measureDescriptiontext` VALUES (744,373,1,'Security Maturity Level 1 - Performed','');
INSERT INTO `measureDescriptiontext` VALUES (745,374,1,'Pol 1','Formal, up-to-date documented policies exist and are readily available to employees.');
INSERT INTO `measureDescriptiontext` VALUES (746,375,1,'Imp 1','Procedures are communicated to individuals who are required to follow them.');
INSERT INTO `measureDescriptiontext` VALUES (747,376,1,'Pro 1','Formal, up-to-date, documented procedures are provided to implement the security controls identified by the defined policies.');
INSERT INTO `measureDescriptiontext` VALUES (748,377,1,'Tes 1','Tests are routinely conducted to evaluate the adequacy and effectiveness of all implementations.');
INSERT INTO `measureDescriptiontext` VALUES (749,378,1,'Security Maturity Level 2 - Managed','');
INSERT INTO `measureDescriptiontext` VALUES (750,379,1,'Pol 2','Policies establish a continuing cycle of assessing risk and implementation and uses monitoring for program effectiveness.');
INSERT INTO `measureDescriptiontext` VALUES (751,380,1,'Imp 2','Information security procedures and controls are implemented in a consistent manner everywhere that the procedure applies and are reinforced through training.');
INSERT INTO `measureDescriptiontext` VALUES (752,381,1,'Pro 2','Procedures clarify where the procedure is to be performed, how the procedure is to be performed, when the procedure is to be performed, who is to perform the procedure, and on what the procedure is to be performed.');
INSERT INTO `measureDescriptiontext` VALUES (753,382,1,'Tes 2','Tests ensure that all policies, procedures, and controls are acting as intended and that they ensure the appropriate information security level.');
INSERT INTO `measureDescriptiontext` VALUES (754,383,1,'Security Maturity Level 3 - Established','');
INSERT INTO `measureDescriptiontext` VALUES (755,384,1,'Pol 3','Policies are written to cover all major facilities in scope.');
INSERT INTO `measureDescriptiontext` VALUES (756,385,1,'Imp 3','Ad hoc approaches that tend to be applied on an individual or case-by-case basis are discouraged.');
INSERT INTO `measureDescriptiontext` VALUES (757,386,1,'Pro 3','Procedures clearly define Information security responsibilities and expected behaviors for (1) asset owners and users, (2) information resources management and data processing personnel, (3) management, and (4) Information security administrators.');
INSERT INTO `measureDescriptiontext` VALUES (758,387,1,'Tes 3','Effective corrective actions are taken to address identified weaknesses, including those identified as a result of potential or actual information security incidents or through information security alerts issued by national Computer Security Incident Response Teams (CSIRT) or Computer Emergency Response Teams (CERT) like CIRCL, vendors, and other trusted sources.');
INSERT INTO `measureDescriptiontext` VALUES (759,388,1,'Security Maturity Level 4 - Predictable','');
INSERT INTO `measureDescriptiontext` VALUES (760,389,1,'Pol 4','Policies are approved by key affected parties.');
INSERT INTO `measureDescriptiontext` VALUES (761,390,1,'Pro 4','Procedures contain appropriate individuals to be contacted for further information, guidance, and compliance.');
INSERT INTO `measureDescriptiontext` VALUES (762,391,1,'Tes 4','Self-assessments, a type of test that can be performed by company staff, by contractors, or others engaged by company management, are routinely conducted to evaluate the adequacy and effectiveness of all implementations.');
INSERT INTO `measureDescriptiontext` VALUES (763,392,1,'Tes 5','Independent audits are an important check on company performance, but are not to be viewed as a substitute for evaluations initiated by company management.');
INSERT INTO `measureDescriptiontext` VALUES (764,393,1,'Tes 6','Information gleaned from records of potential and actual Information security incidents and from security alerts, such as those issued by software vendors are considered as test results. Such information can identify specific vulnerabilities and provide insights into the latest threats and resulting risk.');
INSERT INTO `measureDescriptiontext` VALUES (765,394,1,'Security Maturity Level 5 - Optimised','');
INSERT INTO `measureDescriptiontext` VALUES (766,395,1,'Int 1','Policies, procedures, implementations, and tests are continually reviewed and improvements are made.');
INSERT INTO `measureDescriptiontext` VALUES (767,396,1,'Pol 5','Policies delineate the information security management structure, clearly assign Information security responsibilities, and lay the foundation necessary to reliably measure progress and compliance.');
INSERT INTO `measureDescriptiontext` VALUES (768,397,1,'Pol 6','Policies identify specific penalties and disciplinary actions to be used if the policy is not followed.');
INSERT INTO `measureDescriptiontext` VALUES (769,398,1,'Pro 5','Procedures document the implementation of and the rigor in which the control is applied.');
INSERT INTO `measureDescriptiontext` VALUES (770,399,1,'Tes 7','Evaluation requirements, including requirements regarding the type and frequency of testing, are documented, approved, and effectively implemented.');
INSERT INTO `measureDescriptiontext` VALUES (771,400,1,'Tes 8','The frequency and rigor with which individual controls are tested depend on the risks that will be posed if the controls are not operating effectively.');
INSERT INTO `measureDescriptiontext` VALUES (772,401,1,'Security Policy','');
INSERT INTO `measureDescriptiontext` VALUES (773,402,1,'Security Maturity Level 1 - Performed','');
INSERT INTO `measureDescriptiontext` VALUES (774,403,1,'Pol 1','Formal, up-to-date documented policies exist and are readily available to employees.');
INSERT INTO `measureDescriptiontext` VALUES (775,404,1,'Imp 1','Procedures are communicated to individuals who are required to follow them.');
INSERT INTO `measureDescriptiontext` VALUES (776,405,1,'Pro 1','Formal, up-to-date, documented procedures are provided to implement the security controls identified by the defined policies.');
INSERT INTO `measureDescriptiontext` VALUES (777,406,1,'Tes 1','Tests are routinely conducted to evaluate the adequacy and effectiveness of all implementations.');
INSERT INTO `measureDescriptiontext` VALUES (778,407,1,'Security Maturity Level 2 - Managed','');
INSERT INTO `measureDescriptiontext` VALUES (779,408,1,'Pol 2','Policies establish a continuing cycle of assessing risk and implementation and uses monitoring for program effectiveness.');
INSERT INTO `measureDescriptiontext` VALUES (780,409,1,'Imp 2','Information security procedures and controls are implemented in a consistent manner everywhere that the procedure applies and are reinforced through training.');
INSERT INTO `measureDescriptiontext` VALUES (781,410,1,'Pro 2','Procedures clarify where the procedure is to be performed, how the procedure is to be performed, when the procedure is to be performed, who is to perform the procedure, and on what the procedure is to be performed.');
INSERT INTO `measureDescriptiontext` VALUES (782,411,1,'Tes 2','Tests ensure that all policies, procedures, and controls are acting as intended and that they ensure the appropriate information security level.');
INSERT INTO `measureDescriptiontext` VALUES (783,412,1,'Security Maturity Level 3 - Established','');
